/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By : 
 */

import { useState, useEffect } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

//import DatePicker  from 'react-datepicker';
//import "react-datepicker/dist/react-datepicker.css";
//import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'

const schema = yup.object({
      //dak: yup.object().required('Required'),

      bank: yup.object().required('Required'),

      bankAccountClearText: yup.string().required('Required'),

      //ifsc: yup.string().required('Required'),
});


const AdrReviewEdit = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();

	let history = useHistory();
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});
	const [key, setKey] = useState('page1');
	const [disable,setDisable]=useState(false);
	const [editable,setEditable]=useState(false);


	useEffect(() => {
		let isCancelled = false;
		console.log(id);
		if (id !== 'new') {
			async function fetchData() {
				let record = '';
				await axios.get('/adrReviews/' + id)
					.then((response) => {
						record = response.data;
						const fields = [
						'id', 'dak', 'demandDak', 'cdaoNo', 'checkDigit','advanceType', 'advanceAmount', 'penalInterest', 
						'employee', 'demandDate', 'demandOrigin', 'recordStatus', 'approved'

					];

					fields.forEach(field => setValue(field, record[field]));
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if(error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					});


				if (!isCancelled) {
					setEntity(record);
					setState(prev => ({ ...prev, state: record }));
				}
			}

			fetchData();
			return () => {
				isCancelled = true;
			};
		}else
			setEditable(true);

	}, [id, setValue]);


	const onSubmit = (data, event) => {
		event.preventDefault();
		console.log(data);
		if (data.id) {
			axios.put("/adrReviews/" + data.id, data)
				.then((response) => { 
					if(response.status==200)
						history.push("/adrReviews");
					else
						setServerErrors(response.data);
				})
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		} else {
			axios.post("/adrReviews", data)
				.then((response) => { 
					if(response.status==200)
						history.push("/adrReviews");
					else
						setServerErrors(response.data);
				})
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		}


	}

	const onError = (errors, e) => console.log(errors, e);

	//All foreign keys. 
	// XXXXXX Add search fields and adjust the values as necessary
	const parentData = {

		dak: {
			title: "Dak",
			url : "daks",
			searchList : ['dakidNo'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "dak",
		},

		employee: {
			title: "Employee",
			url : "employees",
			searchList : ['cdaoNo','officerName'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "employee",
		},

	}

	//Callback for child components (Foreign Keys)
	const callback = (childData) => {
		//console.log("Parent Callback");
		setEntity(prev => ({ ...prev, [childData.fk]: childData.entity }));
		setValue(childData.fk, childData.entity);
		//console.log(errors);
		clearErrors(childData.fk);
	};

	const errorCallback = (err) => {
		//console.log(err);
		setServerErrors(err);
	}

	const handleInputChange = (e) => {
		//console.log(e.target.value);
	};
	const handleButtonClick = (e) =>{
		history.push("/adrReviews");
	}

	return (
		<div className="max-w-xl mx-auto ">
			<div className="w-full w-3/4  mx-auto " >

				<form onSubmit={handleSubmit(onSubmit, onError)}>
					<h1 >{id === 'new' ? 'Add' : 'Edit'} Advance Review</h1>
					<div className="text-red-500">{serverErrors}</div>



							<div className="grid grid-cols-2 gap-0 ">


								<div >
									<LiveSearch name="employee" onChange={handleInputChange}
										parentData={parentData.employee} parentCallback={callback}
										fkEntity={entity.employee} errCallback={errorCallback} />
									<div className="text-red-500 ">{errors.employee?.message}</div>
								</div>

								 <div>
									<label>Advance Type</label>
									<input type="text"    name="advanceType" {...register("advanceType")}   readOnly
										/>
									<div className="text-red-500">{errors.advanceType?.message}</div>
								</div>
								<div>
									<label>Advance Amount</label>
									<input type="text"    name="advanceAmount" {...register("advanceAmount")}   readOnly
										/>
									<div className="text-red-500">{errors.advanceAmount?.message}</div>

								</div>
								<div>
									<label>Penal Interest</label>
									<input type="text"    name="penalInterest" {...register("penalInterest")}   readOnly
										/>
									<div className="text-red-500">{errors.penalInterest?.message}</div>

								</div>
								 <div>
									<label>Demand Date</label>
									<input type="text"    name="demandDate" {...register("demandDate")}   readOnly
										/>
									<div className="text-red-500">{errors.demandDate?.message}</div>

								</div>
								<div>
									<label>Demand Origin</label>
									<input type="text"    name="demandOrigin" {...register("demandOrigin")}   readOnly
										/>
									<div className="text-red-500">{errors.demandOrigin?.message}</div>

								</div>




								<div className="px-2">
						<button type="submit" >Save</button>   
					</div>

				<div className="px-2">
				<button type="button" onClick={handleButtonClick} >Cancel</button>
				</div>
					</div>

				</form>
			</div>
		</div>
	);
};
export default withRouter(AdrReviewEdit);
