import { useState, useEffect, useMemo, useCallback } from 'react';
import Table, { SelectColumnFilter } from '../utils/Table'  // 
import axios from "axios";
import { withRouter, Link } from "react-router-dom";
import { useForm } from "react-hook-form";
import debounce from 'lodash';


function GrievanceList() {

	const [data, setData] = useState([]);
	const [update, setUpdate] = useState(false);
	const [serverErrors, setServerErrors] = useState([]);
	const { register, handleSubmit, formState: { errors }, clearErrors } = useForm({});

	useEffect(() => {
		let ignore = false;
		async function fetchData() {
			await axios.get('/grievances')
				.then((response) => {
					if (!ignore) setData(response.data);
				})
				.catch((error) => {
					//console.log(error);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					if (error.response)
						setServerErrors(error.response.data.error);
					else
						setServerErrors(error.Error);
				})
		}
		fetchData();
		return () => { ignore = true; }

	}, [update]);


	async function remove(id) {
		await axios.delete(`/grievances/${id}`)
			.then(() => {
				//console.log(data);
				let updatedRecords = [...data].filter((i) => i.id !== id);
				console.log(updatedRecords);
				setData(updatedRecords);
				setUpdate(!update);
			})
			.catch((error) => {
				console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}


	const columns = useMemo(() => [
		{
			Header: 'Action',
			Cell: ({ row }) => (
				<div>
					<Link to={"/grievances/" + row.original.id}>
						<button className=" w-16 m-0 p-0 " > Edit </button>
					</Link>
					{' '}
					<button
						className="w-16 m-0 p-0 bg-red-500 hover:bg-red-700 "
						onClick={() => remove(row.original.id)}
					>	Delete 	</button>
				</div>
			)
		},
		{
			Header: "Grievance Source",
			accessor: 'grievanceSource',
		},

		{
			Header: "Month Ending",
			accessor: 'monthEnding',
		},

		{
			Header: "Dak",
			accessor: 'dak',
		},

		{
			Header: "Unit",
			accessor: 'unit',
		},

		{
			Header: "Section",
			accessor: 'section',
		},

		{
			Header: "Employee",
			accessor: 'employee',
		},

		{
			Header: "Cdao No",
			accessor: 'cdaoNo',
		},

		{
			Header: "Check Digit",
			accessor: 'checkDigit',
		},

		{
			Header: "Web Id",
			accessor: 'webId',
		},

		{
			Header: "Email Id",
			accessor: 'emailId',
		},

		{
			Header: "Grievance Date",
			accessor: 'grievanceDate',
		},

		{
			Header: "Subject",
			accessor: 'subject',
		},

		{
			Header: "Reference No",
			accessor: 'referenceNo',
		},

		{
			Header: "Reference Date",
			accessor: 'referenceDate',
		},

		{
			Header: "Grievance Brief",
			accessor: 'grievanceBrief',
		},

		{
			Header: "Disposal No",
			accessor: 'disposalNo',
		},

		{
			Header: "Disposal Date",
			accessor: 'disposalDate',
		},

		{
			Header: "Disposal Remarks",
			accessor: 'disposalRemarks',
		},

		{
			Header: "Auditor Remarks",
			accessor: 'auditorRemarks',
		},

		{
			Header: "Auditor Date",
			accessor: 'auditorDate',
		},

		{
			Header: "Aao Remarks",
			accessor: 'aaoRemarks',
		},

		{
			Header: "Aao Date",
			accessor: 'aaoDate',
		},

		{
			Header: "Ao Remarks",
			accessor: 'aoRemarks',
		},

		{
			Header: "Ao Date",
			accessor: 'aoDate',
		},

		{
			Header: "Record Status",
			accessor: 'recordStatus',
		},

		{
			Header: "Approved",
			accessor: 'approved',
		},


		/*
		{
			Header: "Login Name",
			accessor: 'loginName',
			Filter: SelectColumnFilter,  // new
			filter: 'includes',
		},
		*/
	], [])


	const onSubmit = (data, event) => {
		event.preventDefault();
		console.log(data);

			axios.post("/grievances/search?query="+data.search)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					if (error.response)
						setServerErrors(error.response.data);
					else
						setServerErrors(error);
				});
		//history.push("/grievances");
	}

	const onError = (errors, e) => console.log(errors, e);

	const handleInputChange = (e) => {
		console.log(e);
		//console.log(state);
		const search = e.target.value;

	};

  const [value, setValue] = useState("");

  const handleChange = (e) => {
    const { value } = e.target;
    setValue(value);
    handleSearch(value);
  };

  const handleSearch = useCallback(
    debounce((value) => {
      //axios.get(`search?query=${value}`);
    }, 500),
    []
  );

	return (
		<div className="min-h-screen bg-gray-100 text-gray-900">
			<main className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
				<div className="">
					<h1 className="text-xl font-semibold">Grievances</h1>
				</div>
				<form onSubmit={handleSubmit(onSubmit, onError)}>
				<div>
					<input type="text" value={value} name="search" {...register("search")} placeholder="Search" 
					onChange = {handleChange}
						className="form-control py-0 max-w-xs"
					/><span><button type="submit" className="max-w-xs" >Search</button></span>
				</div>
				</form>


				<div className="mt-6 max-h-1">
					<Table columns={columns} data={data} className="table-auto" />
				</div>
			</main>
		</div>
	);
}

export default withRouter(GrievanceList);
