 */

import { useState, useEffect } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

//import DatePicker  from 'react-datepicker';
//import "react-datepicker/dist/react-datepicker.css";
//import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'

const schema = yup.object({

     // nature: yup.string().required('Required'),
    //  amount: yup.number().integer().required('Required'),
     // debitCredit: yup.string().required('Required'),

    //  debitMonth: yup.string().required('Required'),
   //   debitYear: yup.string().required('Required'),



});


const FundAdjustmentEdit = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();

	let history = useHistory();
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});
	const [key, setKey] = useState('page1');
	const [mesg,setMesg]=useState('');
	const [disabled,setDisabled]=useState(false);


	useEffect(() => {
		let isCancelled = false;
		console.log(id);
		if (id !== 'new') {
			async function fetchData() {
				let record = '';
				await axios.get('/fundAdjustments/' + id)
					.then((response) => {
						record = response.data;
						const fields = [
						'id', 'employee', 'nature', 'amount', 'debitCredit', 'interestAmount', 'debitMonth', 'debitYear',
						 'monthEnding',  'cdaoNo', 'checkDigit', 'dak', 'auditorDate', 'aaoDate', 'aoDate', 
						 'approved', 'recordStatus', 'reason', 'remarks','sectionRemarks'				
					];
					fields.forEach(field => setValue(field, record[field]));
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if(error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					});


				if (!isCancelled) {
					setEntity(record);
					setState(prev => ({ ...prev, state: record }));
				}
			}

			fetchData();
			return () => {
				isCancelled = true;
			};
		}

	}, []);


	const onSubmit = (data, event) => {
		event.preventDefault();
		console.log(data);
		if(disabled)
			return;
		console.log("777777777:"+data);
		setDisabled(true);
		if (data.id) {
			axios.put("/fundAdjustments/" + data.id, data)
				.then((response) => { 
					setMesg(response.data);
					//history.push("/fundAdjustments");
					setDisabled(false);
				})
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		} else {
			axios.post("/fundAdjustments", data)
				.then((response) => { 
					setMesg(response.data);
					//history.push("/fundAdjustments");
					setDisabled(false);
				})
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		}
	}

	const onError = (errors, e) => console.log(errors, e);

	//All foreign keys. 
	// XXXXXX Add search fields and adjust the values as necessary
	const parentData = {

		employee: {
			title: "Employee",
			url : "employees",
			searchList : ['cdaoNo', 'officerName','checkDigit'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "employee",
		},

	}

	//Callback for child components (Foreign Keys)
