import { useState, useEffect, } from "react";
import { withRouter } from "react-router-dom";
//import styled from "styled-components";

import { useForm } from "react-hook-form";
import axios from "axios";
import * as yup from "yup";

/**
@author Raja Reddy
October 2021
*/


const schema = yup.object({
	fkDisplay: yup.string().required('Required'),
	//age: yup.number().positive().integer().required(),
}).required();

function LiveSearch({ parentData, parentCallback, fkEntity }) {
	console.log(parentData);
	//console.log(parentCallback);
	//console.log(fkEntity);

	//const { register, handleSubmit, reset, setValue, getValues, formState: { errors } } = useForm({});
	const { register, setValue, formState: { errors } } = useForm({});


	const initialState = {
		search: '',
		list: '',
		showList: false,
	}
	const [state, setState] = useState(initialState);
	/*
		function reducer(state, { name, value }) {
			return {
				...state,
				[name]: value
			}
		}
	*/

	//const [entity, setEntity] = useState(entity);
	//console.log(parentData);
	useEffect(() => {
		let isCancelled = false;
		async function fetchData() {
			const result = await axios(`/${parentData.url}`);;
			setState({ list: result.data });

			if (!isCancelled && fkEntity) {
				let row = '';

				for (let field of parentData.searchList) {
					row += fkEntity[field] + " : ";
				}

				row = row.substring(0, row.length -3);// Strip the last :
				//row += fkEntity.id;
				//console.log(row);
				setValue('fkDisplay', row);
				//setValue(prev =>({...prev, parentData:parentData}));
			}
		}

		fetchData();
		return () => {
			isCancelled = true;
		};

	}, [fkEntity, parentData.url, parentData.searchList, setValue]);

	const handleInputChange = (e) => {
		console.log(e);
		console.log(state);
		const search = e.target.value;
		setState(prevState => ({
			...prevState,
			search: search,
			showList: true,
		}));
	};

	const sendDataToParent = (foreignKey) => {
		parentCallback(foreignKey);
	};

	const setValueHandler = (item) => {
		let row = "";
		for (let field of parentData.searchList) {
			row += item[field] + " : ";
		}
		//Strip the last ' : '
		row = row.substring(0, row.length -3);
		//row += item.id;

		setState(prev => ({
			...prev,
			search: row,
			showList: false,
		}));

		let dataToParent = { props: { entity: item } };

		console.log("ValueHandler");
		console.log(dataToParent);
		sendDataToParent(dataToParent);
	};

	const dropDownRow = (item) => {
		let row = "";
		for (let field of parentData.searchList) {
			row += item[field] + " : ";
		}
		//console.log(row);
		//Strip the last ':'
		row = row.substring(0, row.length -3);
		return <div className="ml-4">{row}</div>;
	};


	const dropDownList = (item) => {
		/*
		let displayItem = "";
		for (let field of parentData.searchList) {
			displayItem += item[field];
		}
		*/
		return (
			<li key={item.id} onClick={() => setValueHandler(item)} 
			className="border rounded bg-green-500 hover:bg-green-700 text-white font-bold">
				{dropDownRow(item)}
			</li>
		);
	};

	const sorter = (a, b) => {
		//return a.name.toLowerCase().localeCompare(b.name.toLowerCase());
		let aRow = "";
		let bRow = "";
		for (let field of parentData.searchList) {
			aRow += a[field];
			bRow += b[field];
		}
		return aRow.toLowerCase().localeCompare(bRow.toLowerCase());
	};

	const multiFieldFilter = (item) => {
		let compositeField = "";
		for (let field of parentData.searchList) {
			compositeField += item[field];
		}
		return (
			compositeField
				.toLowerCase()
				.indexOf(state.search.toLowerCase()) >= 0
		);
	};

	const makeResult = (list) => {
		if (!list)
			return <li>No results!</li>;
		const newList = list.filter(multiFieldFilter);
		if (newList.length)
			return newList
				.slice(0, 10) //Get only 10 items
				.sort((a, b) => sorter(a, b)) //Sort alphabetically
				.map(dropDownList);
		return <li>No results!</li>;
	};

	const handleFocus = (event) => event.target.select();

	return (
		<div className=" pb-0">

			<label className="text-sm block font-bold  py-0 ">{parentData.title} : {fkEntity ? fkEntity[parentData.searchList[0]] : ''}</label>

			<input
				className=" form-control border rounded  text-gray-700  py-0 focus  focus:ring " 
				type="text"	name="fkDisplay" {...register("fkDisplay")}
				onChange={handleInputChange} onFocus={handleFocus}
				placeholder={`Search`}
			/> 

			{state.showList}
			{state.search !== "" && state.showList ? (
				<ul>{makeResult(state.list)}</ul>
			) : (
					state.showList && (
						<span>
							<br />{" "}
						</span>
					)
				)}
		</div>
	);

}

export default withRouter(LiveSearch);
