/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By :
 */

import { useState, useEffect, useMemo } from "react";
import {
  withRouter,
  useParams,
  useHistory,
  useLocation,
} from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from "axios";
import * as yup from "yup";

import { yupResolver } from "@hookform/resolvers/yup";
import LiveSearch from "../utils/LiveSearch";

//import DatePicker  from 'react-datepicker';
//import "react-datepicker/dist/react-datepicker.css";
//import addDays from 'date-fns/addDays'

import Tabs from "react-bootstrap/Tabs";
import Tab from "react-bootstrap/Tab";
import Table from "../utils/Table";

const schema = yup.object({});

const NdcEdit = () => {
  const {
    register,
    handleSubmit,
    setValue,
    formState: { errors },
    clearErrors,
  } = useForm({
    resolver: yupResolver(schema),
  });

  let { id } = useParams();

  let history = useHistory();
  const [serverErrors, setServerErrors] = useState([]);
  const [entity, setEntity] = useState({});
  const [state, setState] = useState({});
  const [key, setKey] = useState("page1");
  const [dakId, setDakId] = useState("");
  const [secName, setSecName] = useState("");
  const [drData, setDrData] = useState([]);
  const [cbillData, setCbillData] = useState([]);
  const [omroData, setOmroData] = useState([]);

  //pcdao 29092022 ndc calculation correction
  const [mesg, setMesg] = useState();
  const [disabled, setDisabled] = useState(false);

  useEffect(() => {
    let isCancelled = false;
    console.log(id);
    if (id !== "new") {
      async function fetchData() {
        let record = "";
        await axios
          .get("/ndcs/" + id)
          .then((response) => {
            record = response.data;
            setDakId(record["dak"].dakidNo);
            setSecName(record["section"].sectionName);
            const fields = [
              "id",
              "dak",
              "auditorDate",
              "aaoDate",
              "aoDate",
              "approved",
              "section",
              "employee",
              "cdaoNo",
              "recordStatus",
              "checkDigit",
              "amountOutstanding",
              "ndcDate",
              "letterNo",
              "me",
              "remarks",
              "reason",
            ];
            fields.forEach((field) => setValue(field, record[field]));

            //pcdao 29092022 ndc calculation correction
            if (record["amountOutstanding"] < 0) {
              setMesg(
                "Outstanding Amount Less than 0. Please settle cancelled DTS advances and then process NDC."
              );
              setDisabled(true);
            }
          })
          .catch((error) => {
            //console.log(error);
            //console.log(error.response.status);
            //console.log(error.response.headers);
            if (error.response) setServerErrors(error.response.data.error);
            else setServerErrors(error.Error);
          });

        if (!isCancelled) {
          setEntity(record);
          setState((prev) => ({ ...prev, state: record }));
        }
      }

      fetchData();
      return () => {
        isCancelled = true;
      };
    }
