import { useState, useEffect } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";


import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import addDays from 'date-fns/addDays'
import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'


import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';


const schema = yup.object({

	//commType: yup.object().required('Required'),
	commissionDate: yup.date().required('Required'),
	goiLetterNo: yup.string().required('Required'),
	goiLetterDate: yup.date().required('Required'),
 	totalOfficers: yup.string().required('Required'),




	//age: yup.number().positive().integer().required(),
}).required();


const CommissionControlEdit = () => {
	//const { register, handleSubmit, reset, setValue, getValues, formState: { errors }, clearErrors } = useForm({
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();
	//console.log(id);

	let history = useHistory();
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});

	const [goiLetterDate, setGoiLetterDate] = useState(new Date());
	const [commissionType,setCommissionType]=useState('');
	const [commSubType,setCommSubType]=useState([]);
	const [selectedSubType,setSelectedSubType]=useState('');
	const [commissionDate, setCommissionDate] = useState(new Date());


	const [key, setKey] = useState('Page1');

	useEffect(() => {
		let isCancelled = false;
		console.log(id);
		if (id !== 'new') {
			async function fetchData() {
				let record = '';
				await axios.get('/commissionControls/' + id)
					.then((response) => {
						record = response.data;
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if(error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					});

				const fields = ['id', 'dak', 'goiLetterNo', 'goiLetterDate', 'commissionType', 'corps', 'armyAgency',
					'totalOfficers', 'commissionDate', 'annualLeaveFrom', 'annualLeaveTo', 'antedatePay','antedatePromotion','outfit','fkAuditor','fkAao','fkAo',
					'auditorDate','aaoDate','aoDate','monthEnding','recordStatus','approved','remarks','reason','commType','commTypeCode','commTypeName'];
				fields.forEach(field => setValue(field, record[field]));
				if (!isCancelled) {
					setEntity(record);
					setState(prev => ({ ...prev, state: record }));
				}
			}

			fetchData();
			return () => {
				isCancelled = true;
			};
		}

	}, [id, setValue]);
useEffect(() => {
		let fetching = false;
		let unmounted = false;

		async function fetchSubType() {

			if (!fetching)
				//console.log(secId);
				await axios.get(`/commissionControls/subType/${commissionType}`)
					.then((response) => {
						console.log("response>>" + response.data);
						setCommSubType(response.data);

					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		fetchSubType();

		return () => { fetching = true; unmounted = true; }
