 */

import { useState, useEffect, useRef } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'


const schema = yup.object({

	dakType: yup.object().required('Required'),
	section: yup.object().required('Required'),
	referenceNo: yup.string().required('Required'),
	referenceDate: yup.string().required('Required'),


});



const DakEdit = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();
	//console.log(id);

	let history = useHistory();

	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});

	const [dakList,setDakList]=useState([]);

	const [loading, setLoading] = useState(true);


	const [key, setKey] = useState('Page1');




	useEffect(() => {
		let fetching = false;
		let unmounted = false;

		async function fetchData() {

			if (!fetching)
				//console.log(secId);
				await axios.get(`/daks/dakLists`)
					.then((response) => {
						console.log("response>>" + response.data);
						setDakList(response.data);

						if (!unmounted) {

							setLoading(false);
						}
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		fetchData();

		return () => { fetching = true; unmounted = true; }
		// eslint-disable-next-line react-hooks/exhaustive-deps
	},[]);



	const onSubmit = (data, event) => {
		event.preventDefault();
		console.log(data);
		console.log("data id--------------"+data.id);
		if (data.id) {
			axios.put("/daks/" + data.id, data)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					console.log("response--------"+error.response.status);
					if(error.response.status===200)
						history.push("/daks");
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		} else {
			axios.post("/daks", data)
				.then((response) => { 
					console.log("reponse status--------------"+response.status+"--"+response.statusText+"----"+"-h--"+response.headers+"--"+response.data);
					if(response.status===201){

						//history.push("/daks/view/"+response.data);
						//history.replace({pathname:'/daks/new/'+response.data});



					}
				})
				.catch((error) => {
					//console.log(error.response.data);
					console.log("response--------"+error.response.status);
					if(error.response.status!==201)
						history.push("/daks");
					//console.log(error.response.headers);
					if (error.response)
						setServerErrors(error.response.data);
					else
						setServerErrors(error);
				});
		}

		//history.push("/daks");
	}



	const onError = (errors, e) => console.log(errors, e);

	//All foreign keys. 
	// XXXXXX Add search fields and adjust the values as necessary
	const parentData = {


		unit: {
			title: "Unit",
			url: "units",
			searchList: ['unitCode', 'unitName'], //XXXXXXXXX Add search fields
			fkEntity: "unit",
			preload: false, //XXXXXX Set this to true for small tables like designations
		},
		section: {
			title: "Section",
			url: "sections",
			searchList: ['sectionName'], //XXXXXXXXX Add search fields
			fkEntity: "section",
			preload: false, //XXXXXX Set this to true for small tables like designations
		},
		employee: {
			title: "Employee",
			url: "employees",
			searchList: ['cdaoNo', 'officerName'], //XXXXXXXXX Add search fields
			fkEntity: "employee",
			preload: false, //XXXXXX Set this to true for small tables like designations
		},
		dakType: {
			title: "DakType",
			url: "dakTypes",
			searchList: ['description'], //XXXXXXXXX Add search fields
			fkEntity: "dakType",
			preload: false, //XXXXXX Set this to true for small tables like designations
		},
	}

	//Callback for child components (Foreign Keys)


	const errorCallback = (err) => {
		//console.log(err);
		setServerErrors(err);
	}

	const handleButtonClick = (e) =>{
		history.push("/daks");
	}

	const handleInputChange = (e) => {
		console.log(e.target.value);
	//	console.log("handle input change");


	};


	return (
		<div className="max-w-xl mx-auto ">
			<div className="w-full w-3/4  mx-auto " >

				<form onSubmit={handleSubmit(onSubmit, onError)}>
					<h1 >  Dak List</h1>
					<div className="text-red-500">{serverErrors}</div>


					  			 <div className="container">  


        <table className="table table-striped table-bordered">  
            <tr>  
                <th>Sl</th>  
                <th>DakId</th>  
                <th>Section</th>  
                 <th>Ref No</th>
                  <th>Ref Data</th>
            </tr>  

            {dakList.map((data, index) => (  
              <tr key={index}> 
              <td>{index+1}</td>  
                <td>{data.dakidNo}</td>  
                <td>{data.section.sectionName}</td>  
                <td>{data.referenceNo}</td>  
                 <td>{data.referenceDate}</td>  
              </tr>  
            ))}  

        </table>  

    </div>


				</form>
			</div>
		</div>
	);
};


export default withRouter(DakEdit);
