              {["all", "ltcEncash"].includes(selectedMenu) && (
                <LtcEncashmentDetails />
              )}
              {["all", "loan"].includes(selectedMenu) && (
                <LoanRecoveryDetails />
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default withRouter(Pro);
