/**
 Code generated using Python
 @author Raja Reddy
 Date : 13 Oct 2021

 */


import { useState, useEffect } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";


import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import addDays from 'date-fns/addDays'
import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'


import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';


const schema = yup.object({
	officerName: yup.string().required('Officer Name is required.'),
	icNo: yup.string().required('IC No is required'),
	rank: yup.object().required('Rank is required'),
	commissionType: yup.object().required('Commission Type is required')



	//age: yup.number().positive().integer().required(),
}).required();


const CommissionControlEdit = () => {
	//const { register, handleSubmit, reset, setValue, getValues, formState: { errors }, clearErrors } = useForm({
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();
	//console.log(id);

	let history = useHistory();
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});

	const [goiLetterDate, setGoiLetterDate] = useState(new Date());


	const [dateOfCommission, setDateOfCommission] = useState(new Date());


	const [key, setKey] = useState('Page1');

	useEffect(() => {
		let isCancelled = false;
		console.log(id);
		if (id !== 'new') {
			async function fetchData() {
				let record = '';
				await axios.get('/commissioncontrols/' + id)
					.then((response) => {
						record = response.data;
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if(error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					});

				const fields = ['id', 'dak', 'goiLetterNo', 'goiLetterDate', 'commissionType', 'corps', 'armyAgency',
					'totalOfficers', 'commissionDate', 'annualLeaveFrom', 'annualLeaveTo', 'antedatePay','antedatePromotion','outfit','fkAuditor','fkAao','fkAo',
					'auditorDate','aaoDate','aoDate','monthEnding','recordStatus','approved','remarks','reason'];
				fields.forEach(field => setValue(field, record[field]));
				if (!isCancelled) {
					setEntity(record);
					setState(prev => ({ ...prev, state: record }));
				}
			}

			fetchData();
			return () => {
				isCancelled = true;
			};
		}

	}, [id, ]);


	const onSubmit = (data, event) => {
		event.preventDefault();

		console.log(data);
		if (data.id) {

			axios
				.put("/commissions/" + data.id, data)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		} else {
			axios.post("/commissions", data)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		}


	}

	const onError = (errors, e) => console.log(errors, e);

	//All foreign keys
	const parentData = {
		rank: {
			title: "Rank",
			url: "ranks",
			searchList: ["rankName"],
			fkEntity: "rank",
			preload: false,
		},

		commissionType: {
			title: "CommissionType",
			url: "commissionTypes",
			searchList: ["typeName"],
			fkEntity: "commissionType",
			preload: false,
		},
		corps: {
			title: "Corps",
			url: "corpss",
			searchList: ["unitCode","unitName"],
			fkEntity: "corps",
			preload: false,
		}

	}


	//Callback for child components (Foreign Keys)
	const callback = (childData) => {
		//console.log("Parent Callback");
		setEntity(prev => ({ ...prev, [childData.fk]: childData.entity }));
		setValue(childData.fk, childData.entity);
		console.log(errors);
		clearErrors(childData.fk);
	};

	const errorCallback = (err) => {
		//console.log(err);
		setServerErrors(err);
	}

	//console.log(entity);

	const handleInputChange = (e) => {
		console.log(e.target.value);
		console.log(entity.gender);
		//console.log(gender);
		setValue('entity.gender', e.target.value);
	};

	return (
		<div className="max-w-xl mx-auto ">
			<div className="w-full w-3/4  mx-auto " >

				<form onSubmit={handleSubmit(onSubmit, onError)}>
					<h1 >{id === 'new' ? 'Add' : 'Edit'} IRLA </h1>
					<div className="text-red-500">{serverErrors}</div>
					<Tabs
						id="CommissionEdit"
						activeKey={key}
						onSelect={(k) => setKey(k)}
						className="mb-3">
						<Tab eventKey="page1" title="Commission Details" className="h-120">

					<div className="grid grid-cols-2 gap-0">

						<div >
							<label >IC No</label>
							<input type="text" name="icNo" {...register("icNo")}
								className="form-control py-0"
							/>
							<div className="text-red-500">{errors.icNo?.message}</div>
						</div>

						<div className=" ">
							<label >Check Digit</label>
							<input type="text" name="checkDigit" {...register("checkDigit")}
								className="form-control py-0 "
							/>
							<div className="text-red-500">{errors.checkDigit?.message}</div>
						</div>

						<div >
							<label >Name of Officer</label>
							<input type="text" name="officerName" {...register("officerName")}
								className="form-control py-0 "
							/>
							<div className="text-red-500">{errors.officerName?.message}</div>
						</div>

						<div >
							<LiveSearch name="commissionType" onChange={handleInputChange}
								parentData={parentData.commissionType} parentCallback={callback} 
								fkEntity={entity.commissionType} errCallback = {errorCallback}/>
							<div className="text-red-500">{errors.commissionType?.message}</div>
						</div>

						<div >
							<LiveSearch name="rank" onChange={handleInputChange}
								parentData={parentData.rank} parentCallback={callback} 
								fkEntity={entity.rank} errCallback={errorCallback} />
							<div className="text-red-500 ">{errors.rank?.message}</div>
						</div>

						<div >
							<LiveSearch name="corps" onChange={handleInputChange}
								parentData={parentData.corps} parentCallback={callback} 
								fkEntity={entity.corps} errCallback={errorCallback} />
							<div className="text-red-500 ">{errors.corps?.message}</div>
						</div>


						<div className="">
							<label >Gender</label>
							<input type="radio" value="F" name="gender" {...register("gender")}  
								onChange={handleInputChange}
							/> Female <span>&nbsp; &nbsp;</span>

							<input type="radio" value="M" name="gender" {...register("gender")}
								onChange={handleInputChange}
							/> Male <span>&nbsp; &nbsp;</span>
						</div>

						<div>
									<label>Date of Birth</label>
									<input type="text" name="dateOfBirth" {...register("dateOfBirth")}
								className="form-control py-0 "/>
							<div className="text-red-500">{errors.dateOfBirth?.message}</div>
								</div>

						<div>
							<label >GoI Details</label>
							<input type="text" name="goiDetail" {...register("goiDetails")}
								className="form-control py-0 "/>
							<div className="text-red-500">{errors.goiDetails?.message}</div>
						</div>
						<div>
									<label>GoI Letter Date</label>
									<input type="date" name="goiLetterDetail" {...register("goiLetterDate")}
								className="form-control py-0 "/>
							<div className="text-red-500">{errors.goiLetterDate?.message}</div>
								</div>

								<div>
									<label>Date of Reporting</label>
									<input type="date" name="dateOfReporting" {...register("dateOfReporting")}
								className="form-control py-0 "/>
							<div className="text-red-500">{errors.dateOfReporting?.message}</div>
								</div>

								<div>
									<label>Date of Commission</label>
									<input type="date" name="dateOfCommission" {...register("dateOfCommission")}
								className="form-control py-0 "/>
							<div className="text-red-500">{errors.dateOfCommission?.message}</div>
								</div>

								</div>
								<div className="px-4">
						<button type="submit" name="goi" onClick={()=>(buttonstate.button=1)} >Save1</button>
					</div>
						</Tab>
						<Tab eventKey="page2" title="Form A" className="h-120">
							<div className="grid grid-cols-2 gap-0">
						<div>
							<label >Ifsc</label>
							<input type="text" name="ifsc" {...register("ifsc")}
								className="form-control py-0 "
							/>
							<div className="text-red-500">{errors.ifsc?.message}</div>
						</div>

						<div >
							<label >Bank Account</label>
							<input type="text" name="bankAccountNumber" {...register("bankAccountNumber")}
								className="form-control py-0 "
							/>
							<div className="text-red-500">{errors.bankAccountNumber?.message}</div>
						</div>

						<div >
							<label >Pay Advance</label>
							<input type="text" name="payAdvance" {...register("payAdvance")}
								className="form-control py-0 "
							/>
							<div className="text-red-500">{errors.payAdvance?.message}</div>
						</div>

						<div >
							<label >Dsop Amount</label>
							<input type="text" name="dsopAmount" {...register("dsopAmount")}
								className="form-control py-0 "
							/>
							<div className="text-red-500">{errors.dsopAmount?.message}</div>
						</div>

						<div>
									<label>Dsop Date</label>
									<DatePicker type="text" name="dsopDate" {...register("dsopDate")}
										selected={dsopDate} onChange={(date) => setDsopDate(date)}
										dateFormat="dd/MM/yyyy"
										className=" -ml-2 pl-2 pr-2" />
								</div>
						 <div >
							<label >PAN</label>
							<input type="text" name="pan" {...register("pan")}
								className="form-control py-0 "
							/>
							<div className="text-red-500">{errors.pan?.message}</div>
						</div>
						<div >
							<label >UID</label>
							<input type="text" name="uid" {...register("uid")}
								className="form-control py-0 "
							/>
							<div className="text-red-500">{errors.uid?.message}</div>
						</div>
						<div >
							<label >Home Town</label>
							<input type="text" name="hometown" {...register("hometown")}
								className="form-control py-0 "
							/>
							<div className="text-red-500">{errors.hometown?.message}</div>
						</div>
						<div >
							<label >NoK Name</label>
							<input type="text" name="nokName" {...register("nokName")}
								className="form-control py-0 "
							/>
							<div className="text-red-500">{errors.nokName?.message}</div>
						</div>
						<div >
							<label >NoK Relation</label>
							<input type="text" name="nokRelation" {...register("nokRelation")}
								className="form-control py-0 "
							/>
							<div className="text-red-500">{errors.nokRelation?.message}</div>
						</div>

						<div >
							<label >Mobile</label>
							<input type="text" name="mobile" {...register("mobile")}
								className="form-control py-0 "
							/>
							<div className="text-red-500">{errors.mobile?.message}</div>
						</div>

						<div className="col-span-2 ...">
									<label>Remarks</label>
									<textarea type="text" name="remarks" {...register("remarks")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.remarks?.message}</div>
								</div>
					</div>
					<div className="px-4">
						<button type="submit" name="forma" onClick={()=>(buttonstate.button=2)} >Save2</button>
					</div>
					</Tab>
					</Tabs>


				</form>
			</div>
		</div>
	);
};

export default withRouter(CommissionControlEdit);
