								<option value="select">--Select--</option>
								<option key="1" value="occ">Occupation</option>
								<option key="2" value="vac">Vacation</option>
								<option key="3" value="rev">Revision</option>


							</select>
						</div>

						<div>
							<button type="submit" onClick={handleSubmit} className="w-16 m-0 p-0">Search</button>

						</div>

						<div>
						{usrLevel < 30 &&

							<Link to={"/iorTrans/new"}>
								<button className=" w-40 ml-8 p-0 h-6 -mt-2" > Add Iors </button>
							</Link>

						}
						</div>
						<div>


							<Link to={"/iorTrans/approvedList/iors"}>
								<button className=" w-40 ml-8 p-0 h-6 -mt-2" > Approved Iors </button>
							</Link>


						</div>
						<div>


							<Link to={"/iorTrans/rejectedList/iors"}>
								<button className=" w-40 ml-8 p-0 h-6 -mt-2" > Invalid/Rejected Iors </button>
							</Link>


						</div>


						<div>

							{(usrLevel<30) &&
						  <Link to={"/occupationVacations"}>
								<button className=" w-40 ml-8 p-0 h-6 -mt-2" > Stop Ior </button>
							</Link>
							}
						</div>

						<div>

							{(usrLevel===30) &&
						<Link to={"/iorTrans/stopIor/iors"}>
								<button className=" w-40 ml-8 p-0 h-6 -mt-2" > Stop Ior </button>
							</Link>
							}
						</div>





						<div>
							{(usrLevel<30) &&
						<button type="button" onClick={submitValidate} disabled={disabled} className=" w-40 ml-8 p-0 h-6 -mt-2">
					{disabled && (
					<span className="spinner-grow spinner-grow-sm"></span>)

									} Validate Iors</button>
							}
						</div>

						<div>

						<button type="button" onClick={refresh}  className="w-20 m-0 p-0 bg-red-500 hover:bg-red-700 ">
				 						Refresh</button>

						</div>

			{/*			<div>
							{(usrLevel>30 && approved===true) &&
						<button type="button" onClick={updateRentMaster} disabled={disabled} className=" w-40 ml-8 p-0 h-6 -mt-2">
					Update Rent Master</button>
							}
						</div>

						<div>
							{(usrLevel>30 && approved===true && updateRent===true) &&
						<button type="button" onClick={updateOccVacMaster} disabled={disabled} className=" w-46 ml-8 p-0 h-6 -mt-2">
					 Update Occ Vac Master</button>
							}
						</div>
				*/}
					</div>



					<div className="-mt-2 max-h-1 py-0 ml-0">

						<TablePageAdj columns={columns} data={data} newpage={page} parentCallback={handleP}
							parentCallbackPageSize={handlePageSize} className="table-auto" />
					<div>
							{(usrLevel < 30  && (optionType==='occ' || optionType==='rev')) &&
								<button type="button" onClick={submitBulk} disabled={disabled} className="w-36 m-2 p-0">
									{disabled && (
										<span className="spinner-grow spinner-grow-sm"></span>
									)

									} Process File</button>
							}
							{((usrLevel === 30) && (optionType==='occ' || optionType==='rev')) &&
								<button type="button" onClick={submitBulk} disabled={disabled} className="w-36 m-2 p-0">
									{disabled && (
										<span className="spinner-grow spinner-grow-sm"></span>
									)

									} Submit File</button>
							}
							{((usrLevel > 30) && (optionType==='occ' || optionType==='rev')) &&
								<button type="button" onClick={approveBulk} className="w-36 m-2 p-0"> Approve File</button>
							}

						</div>
					</div>


					</div>

			</main>
		</div>
		</div>
	);
}

export default withRouter(IorTransList);
