/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By : 
 */

import { useState, useEffect, useRef } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'


const schema = yup.object({

	dakType: yup.object().required('Required'),
	section: yup.object().required('Required'),
	//aoRemarks: yup.string().required('Required'),
	//aoDate: yup.string().required('Required'),

});



const DakEdit = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();
	//console.log(id);

	let history = useHistory();

	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});
	const [dakMsg,setDakMsg]=useState("");
	const [secId,setSecId]=useState();
	const [dakTypeData, setDakTypeData] = useState([]);
	const [dakTypeItems, setDakTypeItems] = useState([]);
	const [dakTypeItem, setDakTypeItem] = useState();
	const [loading, setLoading] = useState(true);


	const [key, setKey] = useState('Page1');



	useEffect(() => {
		let isCancelled = false;
		console.log(id);
		if (id !== 'new') {
			async function fetchData() {
				let record = '';
				await axios.get('/daks/' + id)
					.then((response) => {
						record = response.data;
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					});

				const fields = [
					'id', 'dakidNo', 'dakYear', 'dakType', 'unit', 'section', 'employee', 'cdaoNo',
					'checkDigit', 'amount', 'referenceNo', 'referenceDate', 'subject',
					'billNo', 'billDate', 'disposalDate', 'disposalDate', 'disposalSummary', 'taskUsr',
					'reason', 'aaoDate', 'auditorDate', 'aoDate', 'recordStatus', 'approved'
				];
				fields.forEach(field => setValue(field, record[field]));
				if (!isCancelled) {
					setEntity(record);
					setState(prev => ({ ...prev, state: record }));
				}
			}

			fetchData();
			return () => {
				isCancelled = true;
			};
		}

	}, [id, setValue]);




	useEffect(() => {
		let fetching = false;
		let unmounted = false;
		console.log("daktype");
		async function fetchDakTypeData() {
			console.log(secId);
			if (!fetching && secId > 0)
				//console.log(secId);
				await axios.get(`/sections/${secId}/dakTypes`)
					.then((response) => {
						console.log("response>>" + response.data);
						setDakTypeData(response.data);
						if (!unmounted) {
							setDakTypeItems(
								response.data.map(({ id, description }) => ({ id: id, label: description, value: id }))
							);
							setLoading(false);
						}
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		fetchDakTypeData();

		return () => { fetching = true; unmounted = true; }
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [secId]);



	const onSubmit = (data, event) => {
		event.preventDefault();
		console.log(data);
		console.log("data id--------------"+data.id);
		if (data.id) {
			axios.put("/daks/" + data.id, data)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					console.log("response--------"+error.response.status);
