/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By : 
 */

import { useState, useEffect, useMemo } from 'react';
import Table, { SelectColumnFilter } from '../utils/Table'  // 
import axios from "axios";
import { withRouter, Link } from "react-router-dom";

function IafCda13List() {

	const [data, setData] = useState([]);
	const [update, setUpdate] = useState(false);
	const [serverErrors, setServerErrors] = useState([]);
	const [search, setSearch] = useState("");
	const [inputText, setInputText] = useState('');

	useEffect(() => {
		let fetching = false;
		async function fetchData() {
			if(!fetching)
			await axios.get('/iafCda13s?search='+search)
				.then((response) => {
					setData(response.data);
				})
				.catch((error) => {
					//console.log(error);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					if (error.response)
						setServerErrors(error.response.data.error);
					else
						setServerErrors(error.Error);
				})
		}
		fetchData();
		return () => { fetching = true; }

	}, [update, search]);


async function approve(id) {
		await axios.put(`/iafCda13s/approve/${id}`)
			.then(() => {
				console.log(data);
				let updatedRecords = [...data].filter((i) => i.id !== id);
				console.log(updatedRecords);
				setData(updatedRecords);
				setUpdate(!update);
			})
			.catch((error) => {
				console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}
	async function remove(id) {
		await axios.delete(`/iafCda13s/${id}`)
			.then(() => {
				//console.log(data);
				let updatedRecords = [...data].filter((i) => i.id !== id);
				console.log(updatedRecords);
				setData(updatedRecords);
				setUpdate(!update);
			})
			.catch((error) => {
				console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}


	const columns = useMemo(() => [
		{
			Header: 'Action',
			Cell: ({ row }) => (

				<div>
				{(row.original.action==='Editable')  &&
					<Link to={"/iafCda13s/" + row.original.id}>
						<button className=" w-16 m-0 p-0 " > Edit </button>
					</Link>
					} 
					 {' '}
					 {(row.original.action==='Submit')  &&
					<Link to={"/iafCda13s/" + row.original.id}>
						<button className=" w-16 m-0 p-0 " > Submit </button>
					</Link>
					}
					{' '}
					 {(row.original.action==='Approve')  &&
					<Link to={"/iafCda13s/" + row.original.id}>
						<button className=" w-16 m-0 p-0 " > Approve </button>
					</Link>
					}
				</div>

			)
		},
		{
			Header: "Dak",
			accessor: 'dak.dakidNo',// Change this
		},


		{
			Header: "Section",
			accessor: 'section.sectionName',// Change this
		},


		{
			Header: "Employee",
			accessor: 'employee.id',// Change this
			Cell: ({ row }) => (
				<div>
				<div>
				{row.original.employee!==null &&
				<label>{row.original.employee.rank.rankName} {' '} {row.original.employee.officerName}</label>
				}
				 </div>
				 <div>
				  {row.original.employee!==null &&
				<label> {row.original.employee.cdaoNo}{row.original.employee.checkDigit}</label>
				}
				 </div>

				</div>

				)
		},


		{
			Header: "Amount",
			accessor: 'amount',
		},





		{
			Header: "Cda13 No",
			accessor: 'cda13No',
		},
		{
			Header: "Cda13 Date",
			accessor: 'cda13Date',
		},

		 {
			Header: "Approved",
			accessor: 'approved',
		},
		{
			Header: "Record Status",
			accessor: 'recordStatus',
		},

		{
			Header: "Reason",
			accessor: 'reason',
		},

		{
			Header: "Remarks",
			accessor: 'remarks',
		},




		/*
		{
			Header: "Login Name",
			accessor: 'loginName',
			Filter: SelectColumnFilter,  // new
			filter: 'includes',
		},
		*/
	], [data])

	const handleSubmit = (event) => {
		event.preventDefault();
		console.log(inputText);
		setSearch(inputText);
	}

	const handleKeyPress = (event) => {
		// look for the `Enter` keyCode
		if (event.keyCode === 13 || event.which === 13) {
			handleSubmit(event)
		}
	}

	return (
		<div className="min-h-screen bg-gray-100 text-gray-900">
			<main className="max-w-8xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
				<div className="mt-2 ml-4">
					<h1 className="text-xl font-semibold">Iaf Cda13s</h1>
					<div className="flexContainer">
						<input type="text" name="search" 
						onChange={e => setInputText(e.target.value)}
							onKeyPress={handleKeyPress}
							className="pl-2 -ml-2 inputField flex-initial" />
						<button type="submit" onClick={handleSubmit} className="w-16 m-0 p-0">Search</button>
						<div>
							<Link to={"/iafCda13s/new"}>
								<button className=" w-32 ml-8 p-0 h-6 -mt-2" > Add Iaf Cda13 </button>
							</Link>
						</div>
					</div>					
				</div>
				<div className="-mt-2 max-h-1 py-0">
					<Table columns={columns} data={data} className="table-auto" />
				</div>
			</main>
		</div>
	);
}

export default withRouter(IafCda13List);
