                <div className="text-red-500">
                  {errors.vendor?.vendorName?.message}
                </div>
              </div>
            </div>
          )}

          <div className="px-4">
            {editable && <button type="submit">Submit</button>}

            {disable && <button type="submit">Disable</button>}
          </div>
        </form>
      </div>
    </div>
  );
};

export default withRouter(BankAccountEdit);
