/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By : 
 */

import { useState, useEffect, useMemo } from 'react';
import Table, { SelectColumnFilter } from '../utils/Table'  // 
import axios from "axios";
import { withRouter, Link } from "react-router-dom";

function Do2List() {

	const [data, setData] = useState([]);
	const [update, setUpdate] = useState(false);
	const [serverErrors, setServerErrors] = useState([]);
	const [search, setSearch] = useState("");
	const [inputText, setInputText] = useState('');

	useEffect(() => {
		let fetching = false;
		async function fetchData() {
			if(!fetching)
			await axios.get('/do2s?search='+search)
				.then((response) => {
					setData(response.data);
				})
				.catch((error) => {
					//console.log(error);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					if (error.response)
						setServerErrors(error.response.data.error);
					else
						setServerErrors(error.Error);
				})
		}
		fetchData();
		return () => { fetching = true; }

	}, [update, search]);


	async function remove(id) {
		await axios.delete(`/do2s/${id}`)
			.then(() => {
				//console.log(data);
				let updatedRecords = [...data].filter((i) => i.id !== id);
				console.log(updatedRecords);
				setData(updatedRecords);
				setUpdate(!update);
			})
			.catch((error) => {
				console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}


	const columns = useMemo(() => [
		{
			Header: 'Action',
			Cell: ({ row }) => (
				<div>
					<Link to={"/do2s/" + row.original.id}>
						<button className=" w-16 m-0 p-0 " > Edit </button>
					</Link>
					{' '}
					<button
						className="w-16 m-0 p-0 bg-red-500 hover:bg-red-700 "
						onClick={() => remove(row.original.id)}
					>	Delete 	</button>
				</div>
			)
		},
		{
			Header: "Dak",
			accessor: 'dak.dakidNo', 
		},
		{
			Header: "Employee",
			accessor: 'employee.officerName', 
		},
		{
			Header: "CdaoNo",
			accessor: 'employee.cdaoNo', 
		},
		{
			Header: "Unit",
			accessor: 'unit.unitCode', 
		},
		{
			Header: "Occurrence Code",
			accessor: 'occurrenceCode',
		},

		 {
			Header: "From Date",
			accessor: 'fromDate',
		},

		{
			Header: "To Date",
			accessor: 'toDate',
		},

		{
			Header: "Data1",
			accessor: 'data1',
		},

		{
			Header: "Data2",
			accessor: 'data2',
		},

		{
			Header: "Data3",
			accessor: 'data3',
		},

		{
			Header: "Data4",
			accessor: 'data4',
		},



		{
			Header: "Batch No",
			accessor: 'batchNo',
		},

		{
			Header: "Do2 Date",
			accessor: 'do2Date',
		},

		{
			Header: "Do2 Group",
			accessor: 'do2Group',
		},

		{
			Header: "Do2 Type",
			accessor: 'do2Type',
		},

		{
			Header: "Do2 No",
			accessor: 'do2No',
		},

		{
			Header: "Do2 Item No",
			accessor: 'do2ItemNo',
		},


		{
			Header: "Audit Cage",
			accessor: 'auditCage',
		},

		{
			Header: "Allowance Cage",
			accessor: 'allowanceCage',
		},

		{
			Header: "Transcription Type",
			accessor: 'transcriptionType',
		},



		{
			Header: "Input Month Ending",
			accessor: 'inputMonthEnding',
		},
		{
			Header: "Accepted Month Ending",
			accessor: 'acceptedMonthEnding',
		},


		{
			Header: "Review Month Ending",
			accessor: 'reviewMonthEnding',
		},	




		{
			Header: "Auditor Date",
			accessor: 'auditorDate',
		},

		{
			Header: "Aao Date",
			accessor: 'aaoDate',
		},

		{
			Header: "Ao Date",
			accessor: 'aoDate',
		},
		{
			Header: "Record Status",
			accessor: 'recordStatus',
		},

		{
			Header: "Approved",
			accessor: 'approved',
		},

		{
			Header: "Rejection Type",
			accessor: 'rejectionType',
		},

		{
			Header: "Attachment",
			accessor: 'attachment',
		},


		{
			Header: "Reason",
			accessor: 'reason',
		},




		/*
		{
			Header: "Login Name",
			accessor: 'loginName',
			Filter: SelectColumnFilter,  // new
			filter: 'includes',
		},
		*/
	], [data])

	const handleSubmit = (event) => {
		event.preventDefault();
		console.log(inputText);
		setSearch(inputText);
	}

	const handleKeyPress = (event) => {
		// look for the `Enter` keyCode
		if (event.keyCode === 13 || event.which === 13) {
			handleSubmit(event)
		}
	}

	return (
		<div className="min-h-screen bg-gray-100 text-gray-900">
			<main className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
				<div className="mt-2 ml-4">
					<h1 className="text-xl font-semibold">Do2s</h1>
					<div className="flexContainer">
						<input type="text" name="search" 
						onChange={e => setInputText(e.target.value)}
							onKeyPress={handleKeyPress}
							className="pl-2 -ml-2 inputField flex-initial" />
						<button type="submit" onClick={handleSubmit} className="w-16 m-0 p-0">Search</button>
						<div>
							<Link to={"/do2s/new"}>
								<button className=" w-32 ml-8 p-0 h-6 -mt-2" > Add Do2 </button>
							</Link>
						</div>
					</div>					
				</div>
				<div className="-mt-2 max-h-1 py-0">
					<Table columns={columns} data={data} className="table-auto" />
				</div>
			</main>
		</div>
	);
}

export default withRouter(Do2List);
