/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By : 
 */

import { useState, useEffect, useMemo } from "react";
import Table3, { SelectColumnFilter } from '../utils/Table3'  // 
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'
import { Link } from "react-router-dom";


const TBillInfoEdit = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		//resolver: yupResolver(schema)
	});

	let { id } = useParams();
	console.log(id);

	let history = useHistory();
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});
	const [key, setKey] = useState('page1');
	const [action, setAction] = useState('');
	const [tBillData, setTBillData] = useState([]);
	const [tBillFamData, setTBillFamData] = useState([]);
	const [tBillDisData, setTBillDisData] = useState([]);
	const [tBillPmData, setTBillPmData] = useState([]);
	const [tBillCsData, setTBillCsData] = useState([]);
	const [rejectionList, setRejectionList] = useState([]);
	const [dakId,setDakId]=useState('');
	const [dakType,setDakType]=useState('');
	const [billType,setBillType]=useState('');
	const [rank,setRank]=useState('');
	const [sectionName,setSectionName]=useState('');



	useEffect(() => {
		let isCancelled = false;
		let fetching = false;
		let unmounted = false;

		async function fetchTBillData() {
			let record = '';
			console.log("########id is---:" + id);
			await axios.get(`/cbillTadaLtcs/${id}/tbillInfo`)
				.then((response) => {
					console.log("&&&&&&&&:" + response.data['status']);
					record = response.data['cbill'];
					setAction(response.data['status']);
					setTBillData(response.data['cbill']);
					if (response.data['familyDetailList'] !== null)
						setTBillFamData(response.data['familyDetailList']);
					if (response.data['disallowanceList'] !== null)
						setTBillDisData(response.data['disallowanceList']);
					if (response.data['pmList'] !== null)
						setTBillPmData(response.data['pmList']);
					if (response.data['csList'] !== null)
						setTBillCsData(response.data['csList']);
					if (response.data['rejectionList'] !== null)
						setRejectionList(response.data['rejectionList']);
				})
				.catch((error) => {
					console.log(error);
					//	console.log(error.response.status);
					//	console.log(error.response.headers);
					if (error.response)
						setServerErrors(error.response.data.error);
					else
						setServerErrors(error.Error);
				})

			setDakId(record.dak.dakidNo);
			setBillType(record.billType.description);
			setDakType(record.dak.dakType.description);
			const fields = [
				'id', 'dak', 'billType', 'section', 'employee', 'cdaNo', 'checkDigit',
				'claimType', 'claimDate', 'monthEnding', 'journeyStationFrom', 'journeyStationTo',
				'modeOfJourney', 'blockYear', 'amountClaimed', 'amountPassed', 'advanceAmount',
				'amountDisallowed', 'penalInterest', 'adjustmentAmount', 'finalAdjustment',
				'approved', 'recordStatus', 'reason', 'unit', 'foreignTravel', 'recoveries',
				'periodFrom', 'periodTo', 'noOfDays', 'typeOfLtc', 'verifiedAuditChecks', 'codeHd',
				'provisionalPayment', 'classOfTravel', 'journeyStartDate', 'journeyEndDate',
				'journeyDa', 'paymentMode', 'cdr', 'paymentRecordType', 'foreignTadaAmount',
				'mroAmount', 'mroDakId', 'movementOrderNo', 'movementOrderDate', 'otherAmount',
				'bankAccount', 'htBlockYear', 'cvInLieuOfLtc', 'noOfMembers', 'empCategory',
				'roundTripFarePerPerson', 'totalFare', 'leaveEncashAmount', 'totalValue', 'auditorDate',
				'aaoDate', 'aoDate', 'dvNo', 'calendarYear', 'dvMonth', 'dpSheetNo', 'dpSheetDate',
				'cmpBatch', 'cmpFileNo', 'cmpDate', 'fdList', 'dList', 'oList', 'noOfDays', 'foodCharges', 'lodgingCharges',
				'distance', 'ctg', 'baggageQuantity', 'baggageAmount', 'conveyanceAmount', 'conveyanceType', 'taRule', 'advId',
				'transId', 'taskNo', 'rejectionDetailList', 'rma'
			];
			fields.forEach(field => setValue(field, record[field]));
			if (!isCancelled) {
				setEntity(record);

				setState(prev => ({ ...prev, state: record }));
			}
		}
		fetchTBillData();

		return () => { fetching = true; unmounted = true; }
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [id]);


	async function submitCbill(id) {
		await axios.put(`/cbillTadaLtcs/${id}/submitCbill`)
			.then((response) => {
				//console.log(data);
				//let updatedRecords = [...data].filter((i) => i.id !== id);
				//	console.log(updatedRecords);
				//setData(updatedRecords);
				//setUpdate(!update);
				history.push({ pathname: '/cbillTadaLtcs', state: response.data });
				//history.push("/cbillTadaLtcs");
			})
			.catch((error) => {
				console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}
	async function approve(id) {
		await axios.put(`/cbillTadaLtcs/${id}/approveCbill`)
			.then((response) => {
				//console.log(data);
				//let updatedRecords = [...data].filter((i) => i.id !== id);
				//console.log(updatedRecords);
				//setData(updatedRecords);
				//setUpdate(!update);
				history.push({ pathname: '/cbillTadaLtcs', state: response.data });
			})
			.catch((error) => {
				console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}



	const onError = (errors, e) => {
		console.log(errors, e);
	}

	const returnToList = () => {
		history.push("/cbillTadaLtcs");
	}



	const ShowCbillInfo = () => {

		const columns = useMemo(() => [


			{
				Header: "Dak-Id",
				accessor: 'dakidNo',
			},
			{
				Header: "Claim Type",
				accessor: 'typeOfClaim',
			},
			{
				Header: "CDAO No",
				accessor: 'cdaoNo',
			},

			{
				Header: "Check Digit",
				accessor: 'checkDigit',
			},
			{
				Header: "Name",
				accessor: 'empName',
			},
			{
				Header: "Rank",
				accessor: 'rankName',
			},
			{
				Header: "Code Head",
				accessor: 'codeHead',
			},

			{
				Header: "Amount Claimed",
				accessor: 'amountClaimed',
			},
			{
				Header: "Amount Passed",
				accessor: 'amountPassed',
			},
			{
				Header: "Amount Disallowed",
				accessor: 'amountDisallowed',
			},
			{
				Header: "Advance Amount",
				accessor: 'advanceAmount',
			},
			{
				Header: "Month",
				accessor: 'demandMonth',
			},
			{
				Header: "Movement Order No",
				accessor: 'movementOrderNo',
			},
			{
				Header: "Movement Order Date",
				accessor: 'movementOrderDate',
			},
			{
				Header: "Journey Station From",
				accessor: 'journeyStationFrom',
			},
			{
				Header: "Journey Station To",
				accessor: 'journeyStationTo',
			},
			{
				Header: "Submission Date-Auditor",
				accessor: 'auditorDate',
			},

			{
				Header: "Remarks",
				accessor: 'remarks',
			},
		], [tBillData])

		return (
			<div className="h-48 bg-green-100 text-gray-900">

				<main className="max-w-5xl mx-auto px-2 sm:px-6 lg:px-8 pt-4">

					<div className=" ">
					</div>
					<div className="-mt-2 max-h-0 py-0 ml-0">

						<Table3 columns={columns} data={tBillData} className="table-auto" />
					</div>
				</main>

			</div>
		);
	}

	const ShowRejectionInfo = () => {

		const columns = useMemo(() => [

			{
				Header: "Caption",
				accessor: 'caption',
			},
			{
				Header: "Remarks",
				accessor: 'remarks',// Change this
				Cell: ({ row }) => (
					<div>{row.original.caption === 'Other' &&
						<input type="text" value={rejectionList[row.index]['otherRejection']} />
					}
						{row.original.caption !== 'Other' &&
							<label>{row.original.remarks} </label>
						}

					</div>
				)
			},


		], [rejectionList])

		return (
			<div className="h-48 bg-green-100 text-gray-900">

				<main className="max-w-5xl mx-auto px-2 sm:px-6 lg:px-8 pt-4">

					<div className=" ">
					</div>
					<div className="-mt-2 max-h-0 py-0 ml-0">

						<Table3 columns={columns} data={rejectionList} className="table-auto" />
					</div>
				</main>

			</div>
		);
	}

	const ShowFamilyInfo = () => {
		const columns = useMemo(() => [

			{
				Header: "CDAO No",
				accessor: 'cdaoNo',
			},

			{
				Header: "Name Of Family Member",
				accessor: 'nameOfFamilyMember',
			},

			{
				Header: "Date of Birth",
				accessor: 'dob',
			},

			{
				Header: "Relation",
				accessor: 'relation',
			},


		], [tBillFamData])

		return (
			<div className="h-48 bg-green-100 text-gray-900">

				<main className="max-w-5xl mx-auto px-2 sm:px-6 lg:px-8 pt-4">

					<div className=" ">
					</div>
					<div className="-mt-2 max-h-0 py-0 ml-0">

						<Table3 columns={columns} data={tBillFamData} className="table-auto" />
					</div>
				</main>

			</div>
		);
	}

	const ShowDisallowanceInfo = () => {
		const columns = useMemo(() => [

			{
				Header: "Dak-Id",
				accessor: 'dakidNo',
			},

			{
				Header: "Disallowance Name",
				accessor: 'disallowanceName',
			},

			{
				Header: "Amount Claimed",
				accessor: 'itemAmountClaimed',
			},

			{
				Header: "Amount Admitted",
				accessor: 'amountAdmitted',
			},


		], [tBillDisData])

		return (
			<div className="h-48 bg-green-100 text-gray-900">

				<main className="max-w-5xl mx-auto px-2 sm:px-6 lg:px-8 pt-4">

					<div className=" ">
					</div>
					<div className="-mt-2 max-h-0 py-0 ml-0">

						<Table3 columns={columns} data={tBillDisData} className="table-auto" />
					</div>
				</main>

			</div>
		);
	}

	const ShowPmInfo = () => {
		const columns = useMemo(() => [

			{
				Header: "Dak-Id",
				accessor: 'dakidNo',
			},

			{
				Header: "Receipt Side",

				columns: [
					{
						Header: "Code Head",
						accessor: 'receiptCodeHead'
					},
					{
						Header: "Plus Receipt",
						accessor: 'receiptPlusAmount'
					},
					{
						Header: "Minus Receipt",
						accessor: 'receiptMinusAmount'
					}

				]

			},

			{
				Header: "Charges Side",

				columns: [
					{
						Header: "Code Head",
						accessor: 'chargeCodeHead'
					},
					{
						Header: "Plus Charge",
						accessor: 'chargePlusAmount'
					},
					{
						Header: "Minus Charge",
						accessor: 'chargeMinusAmount'
					}

				]

			},
			{
				Header: "Record Status",
				accessor: 'recordStatus',
			},


		], [tBillPmData])

		return (
			<div className="h-64 bg-green-100 text-gray-900">

				<main className="max-w-5xl mx-auto px-2 sm:px-6 lg:px-8 pt-4">

					<div className=" ">
					</div>
					<div className="-mt-2 max-h-0 py-0 ml-0">

						<Table3 columns={columns} data={tBillPmData} className="table-auto" />
					</div>
				</main>

			</div>
		);
	}

	const ShowCsInfo = () => {
		const columns = useMemo(() => [

			{
				Header: "Dak-Id",
				accessor: 'dakidNo',
			},

			{
				Header: "Bank Name",
				accessor: 'bankDetails',
			},

			{
				Header: "IFSC",
				accessor: 'ifscCode',
			},

			{
				Header: "Bank Account Number",
				accessor: 'bankAccountNo',
			},

			{
				Header: "Amount",
				accessor: 'amount',
			},

			{
				Header: "Cheque Slip Date",
				accessor: 'chequeSlipDate',
			},

			{
				Header: "Record Status",
				accessor: 'recordStatus',
			},


		], [tBillCsData])

		return (
			<div className="h-48 bg-green-100 text-gray-900">

				<main className="max-w-5xl mx-auto px-2 sm:px-6 lg:px-8 pt-4">

					<div className=" ">
					</div>
					<div className="-mt-2 max-h-0 py-0 ml-0">

						<Table3 columns={columns} data={tBillCsData} className="table-auto" />
					</div>
				</main>

			</div>
		);
	}

	return (
		<div className="h-126 bg-green-100 text-gray-900">

			<main className="max-w-5xl mx-auto px-2 sm:px-6 lg:px-8 pt-4">

				<div className="grid grid-cols-2 gap-0">
				<div>
						<label>Dak Id</label> {dakId}
						 						</div>
					<div>
						<label>Cdao No</label>
						<input type="text" name="cdaoNo" {...register("cdaoNo")} readOnly
							className="form-control py-0"/>
						</div>
						<div>
						<label>Amount Claimed</label>
						<input type="text" name="amountClaimed" {...register("amountClaimed")} readOnly
							className="form-control py-0"/>
						</div>

					</div>



					<div>
						<h3 className="p-2  text-xl font-bold">Bill Details</h3>
						<ShowCbillInfo />
					</div>

					<div>
						<h3 className="p-2  text-xl font-bold">Rejection Details</h3>
						<ShowRejectionInfo />
					</div>

					<div>
						<h3 className="p-2  text-xl font-bold">Family Details</h3>
						<ShowFamilyInfo />
					</div>

					<div>
						<h3 className="p-2  text-xl font-bold">Disallowance Details</h3>
						<ShowDisallowanceInfo />
					</div>

					<div>
						<h3 className="p-2  text-xl font-bold">PM Details</h3>
						<ShowPmInfo />
					</div>

					<div>
						<h3 className="p-2  text-xl font-bold">Cheque Slip Details</h3>
						<ShowCsInfo />
					</div>
					<div>
						<Link to={"/cbillTadaLtcs"}>
							<button className=" w-16 m-0 p-0 " > Close </button>
						</Link>


					</div>

					<div>
						{' '}
						{(action === 'Submission by AAO.') &&
							<button
								className="w-16 m-0 p-0 bg-red-500 hover:bg-red-700 "
								onClick={() => submitCbill(id)}
							>	Submit 	</button>
						}
						{' '}
						{(action === 'Approval by AO.') &&
							<button
								className="w-16 m-0 p-0 bg-red-500 hover:bg-red-700 "
								onClick={() => approve(id)}
							>	Approve 	</button>
						}
					</div>
			</main>


		</div>

	);


};

export default withRouter(TBillInfoEdit);
