/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By :
 */

import { useState, useEffect, useMemo } from "react";
import Table, { SelectColumnFilter } from "../utils/Table"; //
import axios from "axios";
import { withRouter, Link } from "react-router-dom";

function PaySummaryList() {
  const [data, setData] = useState([]);
  const [update, setUpdate] = useState(false);
  const [serverErrors, setServerErrors] = useState([]);
  const [search, setSearch] = useState("");
  const [inputText, setInputText] = useState("");
  const [me,setMe]=useState('');
  const [meSearch,setMeSearch]=useState('');
  const [cdaoNo,setCdaoNo]=useState('');
  const [cdaoNoSearch,setCdaoNoSearch]=useState('');

  useEffect(() => {
    let fetching = false;
    async function fetchData() {
      if (!fetching)
        await axios
          .get("/paySummarys?cdaoNo=" + cdaoNo+'&me='+me)
          .then((response) => {
            setData(response.data);
          })
          .catch((error) => {
            //console.log(error);
            //console.log(error.response.status);
            //console.log(error.response.headers);
            if (error.response) setServerErrors(error.response.data.error);
            else setServerErrors(error.Error);
          });
    }
    fetchData();
    return () => {
      fetching = true;
    };
  }, [update, cdaoNo,me]);

  async function remove(id) {
    await axios
      .delete(`/payElements/${id}`)
      .then(() => {
        //console.log(data);
        let updatedRecords = [...data].filter((i) => i.id !== id);
        console.log(updatedRecords);
        setData(updatedRecords);
        setUpdate(!update);
      })
      .catch((error) => {
        console.log(error);
        //console.log(error.response.status);
        //console.log(error.response.headers);
        if (error.response) setServerErrors(error.response.data.error);
        else setServerErrors(error.Error);
      });
  }

  const columns = useMemo(
    () => [

       {
        Header: "Action",
        Cell: ({ row }) => (
          <div>
             {row.original.action && row.original.action==='close' &&
              <button className=" w-16 m-0 p-0 "> Close </button>
            }

             {row.original.action && row.original.action==='reclose' &&
              <button className=" w-16 m-0 p-0 "> Re-Close </button>
            }

          </div>
        ),
      }, 
      {
        Header: "CDAO No",
        accessor: "employee.cdaoNo", // Change this
        Cell: ({ row }) => (

				<div>
				{row.original.employee.cdaoNo}{row.original.employee.checkDigit}<br/>


				 </div>


				)
      },

      {
        Header: "Name",
        accessor: "employee.officerName", // Change this
        Cell: ({ row }) => (

				<div>
				{row.original.employee.rank.rankName}{' '}{row.original.employee.officerName}<br/>


				 </div>


				)
      },

      {
        Header: "Month",
        accessor: "monthEnding",
      },

      {
        Header: "Opening Balance",
        accessor: "openingBalance",
      },

      {
        Header: "Gross Pay",
        accessor: "grossPay",
      },

      {
        Header: "Net Pay",
        accessor: "netPay",
      },

      {
        Header: "Record Status",
        accessor: "recordStatus",
      },

       {
        Header: "Closed",
        accessor: "closed",
      },

      /*
		{
			Header: "Login Name",
			accessor: 'loginName',
			Filter: SelectColumnFilter,  // new
			filter: 'includes',
		},
		*/
    ],
    [data]
  );

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(cdaoNoSearch);
    setCdaoNo(cdaoNoSearch);
    setMe(meSearch);
  };

  const handleKeyPress = (event) => {
    // look for the `Enter` keyCode
    if (event.keyCode === 13 || event.which === 13) {
      handleSubmit(event);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 text-gray-900">
      <main className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
        <div className="mt-2 ml-4">
          <h1 className="text-xl font-semibold">Monthly Pay Summary</h1>
          <div className="flexContainer">
          <div>
            <input
              type="text"
              name="cdaoSearch"
              onChange={(e) => setCdaoNoSearch(e.target.value)}
              onKeyPress={handleKeyPress} placeholder="cdaono"
              className="pl-2 -ml-2 inputField flex-initial"
            />
            </div>
            <div>
             <input
              type="text"
              name="meSearch"
              onChange={(e) => setMeSearch(e.target.value)}
              onKeyPress={handleKeyPress} placeholder="mm/yyyy"
              className="pl-2 -ml-2 inputField flex-initial"
            /></div>
            <button
              type="submit"
              onClick={handleSubmit}
              className="w-16 m-0 p-0"
            >
              Search
            </button>
            <div>
              <Link to={"/payElements/new"}>
                <button className=" w-32 ml-8 p-0 h-6 -mt-2">
                  {" "}
                  Add New{" "}
                </button>
              </Link>
            </div>
          </div>
        </div>
        <div className="-mt-2 max-h-1 py-0">
          <Table columns={columns} data={data} className="table-auto" />
        </div>
      </main>
    </div>
  );
}

export default withRouter(PaySummaryList);
