/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By : 
 */

import { useState, useEffect } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

//import DatePicker  from 'react-datepicker';
//import "react-datepicker/dist/react-datepicker.css";
//import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'

const schema = yup.object({
	dak: yup.object().required('Required'),

	corps: yup.object().required('Required'),


	rank: yup.object().required('Required'),
	dateOfBirth: yup.string().required('Required'),

	pcSsc: yup.string().required('Required'),
	officerName: yup.string().required('Required'),

	icNo: yup.string().required('Required'),
	icCheckDigit: yup.string().required('Required'),

	tgcOthers: yup.string().required('Required'),

	gender: yup.string().required('Required'),

});



const CommissionGoiEdit = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();

	let history = useHistory();
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});
	const [key, setKey] = useState('page1');
	const [dakId, setDakId] = useState(0);
	const [ccData, setCcData] = useState([]);
	const [ccTypeName, setCcTypeName] = useState('');
	const [goiNo, setGoiNo] = useState('');
	const [goiDate, setGoiDate] = useState(new Date());
	const [commDate, setCommDate] = useState(new Date());
	const [updateMesg, setUpdateMesg] = useState('');



	useEffect(() => {
		let isCancelled = false;
		console.log(id);
		if (id !== 'new') {
			async function fetchData() {
				let record = '';
				await axios.get('/commissionGois/' + id)
					.then((response) => {
						record = response.data;
						const fields = [
							'id'
							, 'commissionControlDak', 'commissionControl', 'commissionType', 'corps', 'reemployed', 'annualLeaveTo', 'antedatePay', 'antedatePromotion', 'tgcDateOfPermanentCommission', 'auditorDate', 'aaoDate', 'aoDate', 'approved', 'rank', 'dateOfBirth', 'dateOfCommission', 'annualLeaveFrom', 'pcSsc', 'officerName', 'monthEnding', 'icNo', 'icCheckDigit', 'cdaoNo', 'cdaoCheckDigit', 'tgcOthers', 'recordStatus', 'gender', 'reason', 'remarks'
						];
						fields.forEach(field => setValue(field, record[field]));
						console.log(record["commissionControlDak"].id)
						setDakId(record["commissionControlDak"].id);

					})

					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					});


				if (!isCancelled) {
					setEntity(record);
					setState(prev => ({ ...prev, state: record }));
				}
			}

			fetchData();
			return () => {
				isCancelled = true;
			};
		}

	}, [id, setValue]);


	useEffect(() => {
		let fetching = false;
		let unmounted = false;
		let rec = '';
		console.log(dakId);
		async function fetchCommissionData() {

			if (!fetching && dakId > 0)
				console.log(dakId);
			await axios.get(`/daks/${dakId}/commissionControls`)
				.then((response) => {
					console.log("response>>" + response.data["commissionType"].typeName);
					rec = response.data;
					setCcData(response.data);
					setValue('commissionControl', response.data);
					setValue('commissionType', response.data["commissionType"]);
					//setValue('commissionTypeName',response.data["commissionType"].typeName)
					setCcTypeName(response.data["commissionType"].typeName);
					setGoiNo(rec["goiLetterNo"]);
					setGoiDate(rec["goiLetterDate"]);
					setCommDate(rec["commissionDate"]);
					setValue('annualLeaveFrom', rec["annualLeaveFrom"]);
					setValue('annualLeaveTo', rec["annualLeaveTo"]);
					if (!unmounted) {

					}
				})
				.catch((error) => {
					//console.log(error);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					if (error.response)
						setServerErrors(error.response.data.error);
					else
						setServerErrors(error.Error);
				})
		}
		fetchCommissionData();

		return () => { fetching = true; unmounted = true; }
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [dakId]);


	const onSubmit = (data, event) => {
		event.preventDefault();
		console.log(data);
		if (data.id) {
			axios.put("/commissionGois/" + data.id, data)
				.then((response) => {
					setUpdateMesg(response.data);
					setValue('officerName', '');
				})
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		} else {
			axios.post("/commissionGois", data)
				.then((response) => {
					setUpdateMesg(response.data);
					setValue('officerName', '');
					setValue('rank',[]);
					setValue('gender', '');
					setValue('dateOfBirth', '');
					setValue('icNo', '');
					setValue('icCheckDigit', '');


				})
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		}

		//history.push("/commissionGois");
	}

	const onError = (errors, e) => console.log(errors, e);

	//All foreign keys. 
	// XXXXXX Add search fields and adjust the values as necessary
	const parentData = {

		dak: {
			title: "Commission Control Dak",
			url: "daks/commissiondaktypes",
			searchList: ['dakidNo'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "dak",
		},
		commissionControl: {
			title: "Commission Control",
			url: "commissionControls",
			searchList: ['id'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "commissionControl",
		},
		commissionType: {
			title: "Commission Type",
			url: "commissionTypes",
			searchList: ['typeName'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "commissionType",
		},
		corps: {
			title: "Corps",
			url: "corpss",
			searchList: ['unitCode', 'unitName'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "corps",
		},
		rank: {
			title: "Rank",
			url: "ranks",
			searchList: ['rankName'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "rank",
		},
	}

	//Callback for child components (Foreign Keys)
	const callback = (childData) => {
		//console.log("Parent Callback");
		setEntity(prev => ({ ...prev, [childData.fk]: childData.entity }));
		setValue(childData.fk, childData.entity);
		//console.log(errors);
		//console.log(">>>"+childData.fk);
		if (childData.fk === 'dak') {
			//console.log(childData.entity.referenceNo);
			setValue('commissionControlDak', childData.entity);
			setDakId(childData.entity.id);
			setValue('goiLetterNo', childData.entity.referenceNo);
			setValue('goiLetterDate', childData.entity.referenceDate);

		}
		clearErrors(childData.fk);
	};

	const errorCallback = (err) => {
		//console.log(err);
		setServerErrors(err);
	}

	const handleInputChange = (e) => {
		//console.log(e.target.value);
	};
	const handleInputChangeGender = (e) => {
		//	console.log(e.target.value);
		//console.log(entity.gender);
		//console.log(gender);
		setValue('entity.gender', e.target.value);
	};

	const handleInputChangePc = (e) => {
		//	console.log(e.target.value);
		//console.log(entity.gender);
		//console.log(gender);
		setValue('entity.pcSsc', e.target.value);
	};
	const handleButtonClick = (e) =>{
		history.push("/commissionGois");
	}

	return (
		<div className="max-w-xl mx-auto ">
			<div className="w-full w-3/4  mx-auto " >
				{updateMesg}
				<form onSubmit={handleSubmit(onSubmit, onError)}>
					<h1 >{id === 'new' ? 'Add' : 'Edit'} GoI - Officer Info </h1>
					<div className="text-red-500">{serverErrors}</div>


					<div className="grid grid-cols-2 gap-0">
						<div >
							<LiveSearch name="dak" onChange={handleInputChange}
								parentData={parentData.dak} parentCallback={callback}
								fkEntity={entity.dak} errCallback={errorCallback} />
							<div className="text-red-500 ">{errors.commissionControlDak?.message}</div>
						</div>



						<div>
							<label>Commission Type</label>
							<input type="text" name="commissionTypeName" value={ccTypeName} readonly
								className="form-control py-0" />

						</div>
						<div>
							<label>GoI Letter No</label>
							<input type="text" name="goiLetterNo" value={goiNo} readonly
								className="form-control py-0" />

						</div>

						<div>
							<label>GoI Letter Date</label>
							<input type="date" name="goiLetterDate" value={goiDate} readonly
								className="form-control py-0" />

						</div>

						<div>
							<label>Commission Date</label>
							<input type="date" name="commDate" value={commDate} readonly
								className="form-control py-0" />

						</div>

						<div >
							<LiveSearch name="corps" onChange={handleInputChange}
								parentData={parentData.corps} parentCallback={callback}
								fkEntity={entity.corps} errCallback={errorCallback} />
							<div className="text-red-500 ">{errors.corps?.message}</div>
						</div>

						<div>
							<label>Officer Name</label>
							<input type="text" name="officerName" {...register("officerName")}
								className="form-control py-0"
							/>
							<div className="text-red-500">{errors.officerName?.message}</div>
						</div>

						<div >
							<LiveSearch name="rank" onChange={handleInputChange}
								parentData={parentData.rank} parentCallback={callback}
								fkEntity={entity.rank} errCallback={errorCallback} />
							<div className="text-red-500 ">{errors.rank?.message}</div>
						</div>

						<div className="">
							<label >Gender</label>
							<input type="radio" value="F" name="gender" {...register("gender")}
								onChange={handleInputChangeGender}
							/> Female <span>&nbsp; &nbsp;</span>

							<input type="radio" value="M" name="gender" {...register("gender")}
								onChange={handleInputChangeGender}
							/> Male <span>&nbsp; &nbsp;</span>
						</div>
						<div>
							<label>Date of Birth</label>
							<input type="date" name="dateOfBirth" {...register("dateOfBirth")}
								className="form-control py-0" />
							<div className="text-red-500">{errors.dateOfBirth?.message}</div>
						</div>
						<div>
							<label>Ic No</label>
							<input type="text" name="icNo" {...register("icNo")}
								className="form-control py-0"
							/>
							<div className="text-red-500">{errors.icNo?.message}</div>
						</div>


						<div>
							<label>Ic Check Digit</label>
							<input type="text" name="icCheckDigit" {...register("icCheckDigit")}
								className="form-control py-0"
							/>
							<div className="text-red-500">{errors.icCheckDigit?.message}</div>
						</div>





						<div>
							<label>Annual Leave From</label>
							<input type="date" name="annualLeaveFrom" {...register("annualLeaveFrom")}
								className="form-control py-0" />
							<div className="text-red-500">{errors.annualLeaveFrom?.message}</div>
						</div>

						<div>
							<label>Annual Leave To</label>
							<input type="date" name="annualLeaveTo" {...register("annualLeaveTo")}
								className="form-control py-0" />
							<div className="text-red-500">{errors.annualLeaveTo?.message}</div>
						</div>
						<div>
							<label>Reemployed</label>
							<input type="checkbox" name="reemployed" {...register("reemployed")}

							/>
							<div className="text-red-500">{errors.reemployed?.message}</div>
						</div>



						<div>
							<label>Antedate Pay</label>
							<input type="date" name="antedatePay" {...register("antedatePay")}
								className="form-control py-0" />
							<div className="text-red-500">{errors.antedatePay?.message}</div>
						</div>


						<div>
							<label>Antedate Promotion</label>
							<input type="date" name="antedatePromotion" {...register("antedatePromotion")}
								className="form-control py-0" />
							<div className="text-red-500">{errors.antedatePromotion?.message}</div>
						</div>


						<div>
							<label>Tgc Date Of Permanent Commission</label>
							<input type="date" name="tgcDateOfPermanentCommission" {...register("tgcDateOfPermanentCommission")}
								className="form-control py-0" />
							<div className="text-red-500">{errors.tgcDateOfPermanentCommission?.message}</div>
						</div>


						<div className="">
							<label >PC or SSC</label>
							<input type="radio" value="PC" name="pcSsc" {...register("pcSsc")}
								onChange={handleInputChangePc}
							/> PC <span>&nbsp; &nbsp;</span>

							<input type="radio" value="SSC" name="pgSsc" {...register("pgSsc")}
								onChange={handleInputChangePc}
							/> SSC <span>&nbsp; &nbsp;</span>
						</div>



						<div>
							<label>Tgc Others</label>
							<input type="checkbox" name="tgcOthers" {...register("tgcOthers")} />
							<div className="text-red-500">{errors.tgcOthers?.message}</div>
						</div>





					<div className="px-2">
						<button type="submit" >Save</button>
					</div>
					<div className="px-2">
					<button type="button" onClick={handleButtonClick} >Cancel</button>
						</div>
</div>
				</form>
			</div>
		</div>
	);
};

export default withRouter(CommissionGoiEdit);
