/**
@author Raja Reddy
October 2021
*/

import { Nav, Navbar, NavDropdown, Container } from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState, useEffect, } from 'react';
import { withRouter, Link } from "react-router-dom";
import axios from "axios";

const AppNavBar = ({ menu}) => {
	//console.log(menu);

	const [data, setData] = useState([]);
	const [serverErrors, setServerErrors] = useState([]);
	const [topLevelMenu, setTopLevelMenu] = useState([]);
	const [subMenus, setSubMenus] = useState([]);
	//console.log(subMenus);
	topLevelMenu.length = 0; // Clear array
	subMenus.length = 0;// Clear array
	let arr = null;
	menu && menu.map(item => {
		//console.log(item);
		if (item.fkParentMenu === 1) {//Top level menu. Not sub menu
			topLevelMenu.push(item);
		} else if (!item.fkParentMenu) {// New sub menu
			if (arr !== null) {
				subMenus.push(arr);
				//console.log(subMenus);
			}
			arr = [];
			arr.push(item);//Title of menu
		} else {// Continuing with a sub menu
			arr.push(item);
		}

	});
	if (arr)
		subMenus.push(arr);
	//setSubMenus(subMenus);
	//console.log(topLevelMenu);
	//console.log(subMenus);


	return (
		<div className="fixed-top2" >
			<Navbar bg="success" variant="dark" expand="lg">
				<Container>
					<Navbar.Brand href="/">
						<img
							src="/falcon2.png"
							width="30"
							height="30"
							className="d-inline-block align-top"
							alt="Falcon logo"
						/>
						{'  '}
						Falcon
					</Navbar.Brand>
					<Navbar.Toggle aria-controls="basic-navbar-nav" />
					<Navbar.Collapse id="basic-navbar-nav">
						<Nav className="me-auto">
							<Nav.Link href="/">Home</Nav.Link>

							{topLevelMenu.map(item =>
								<Nav.Link key={item.id} href={'/'+item.link}>{item.menuName}</Nav.Link>
							)}


							{subMenus.map(subMenu => {
								//console.log(subMenu[0].menuName);
								return (
									<NavDropdown key={subMenu[0].id} title={subMenu[0].menuName} id="basic-nav-dropdown" className="my-0 py-0" >
										{subMenu.slice(1).map(item =>
											<NavDropdown.Item key={item.id} href={'/'+item.link} className="leading-none  pb-1 "> {item.menuName} </NavDropdown.Item>
										)}
									</NavDropdown>
								);
							}
							)}

						</Nav>

						<Nav>
							{
							!menu &&
							<Nav.Link href={`${process.env.REACT_APP_BASE_URL}/login`}>Login</Nav.Link>
							}
							{
							menu &&
							<Nav.Link eventKey={2} href={`${process.env.REACT_APP_BASE_URL}/logout`}> Logout </Nav.Link>
							}
						</Nav>

					</Navbar.Collapse>
				</Container>
			</Navbar>
		</div>
	);
};

export default AppNavBar;
