import { useState, useEffect } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';


const schema = yup.object({
	usrName: yup.string().required('User Name is required.'),
	accountNo: yup.string().required('Account no. is required'),
	loginName: yup.string().required('Login Name is required'),
	dadDesignation: yup.object().required('Designation is required'),
	password: yup.string().required('Password is required'),
	password2: yup.mixed().oneOf([yup.ref('password'), null], 'Passwords must match')
	//age: yup.number().positive().integer().required(),
}).required();


const UsrEdit = () => {
	//const { register, handleSubmit, reset, setValue, getValues, formState: { errors }, clearErrors } = useForm({
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();
	//console.log(id);

	let history = useHistory();
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});


	useEffect(() => {
		let isCancelled = false;
		console.log(id);
		if (id !== 'new') {
			async function fetchData() {

				const result = await axios('/usrs/' + id);
				//if (!ignore) 
				//setData(result.data);
				console.log(result.data);
				let d = result.data;
				//data = result.data;
				const fields = ['id', 'usrName', 'accountNo', 'loginName', 'dadDesignation', 'gender', 'section',
					'phone1', 'phone2', 'phone3', 'email', 'enabled'];
				fields.forEach(field => setValue(field, d[field]));
				//Foreign Keys
				if (!isCancelled) {
					//setValue('dadDesignation', entity['dadDesignation']);
					setEntity(result.data);
					setState(prev => ({ ...prev, state: result.data }));
				}
			}

			fetchData();
			return () => {
				isCancelled = true;
			};
		}

	}, [id, setValue]);


	//Callback for child components (Forein Keys)
	const callback = (childData) => {
		//console.log("Parent Callback");
		setEntity(prev => ({ ...prev, dadDesignation: childData.props.entity }));
		setValue('dadDesignation', childData.props.entity);
		console.log(errors);
		clearErrors("dadDesignation");
	};


	const onSubmit = (data, event) => {
		event.preventDefault();
		console.log(data);
		if (data.id) {
			axios
				.put("/usrs/" + data.id, data)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					console.log(error.response.status);
					console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		} else {
			axios.post("/usrs", data)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					console.log(error.response.status);
					console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		}

		history.push("/usrs");
	}

	const onError = (errors, e) => console.log(errors, e);

	//All foreign keys
	const parentData = {
		dadDesignation: {
			title: "Designation",
			url: "dadDesignations",
			searchList: ["designationAbbr"],
			fkEntity: "dadDesignation",
			//kid: "",
		},

		section: {
			title: "Section",
			url: "sections",
			searchList: ["sectionName"],
			fkEntity: "section",
		}
	}

	//console.log(entity);

	const handleInputChange = (e) => {
		//console.log(e.target.value);
	};

	return (
		<div className="max-w-xl mx-auto ">
			<div className="w-full w-3/4  mx-auto " >

				<form className=" bg-green-50 shadow-md rounded " onSubmit={handleSubmit(onSubmit, onError)}>
					<h1 className="text-2xl">{id === 'new' ? 'Add' : 'Edit'} User </h1>
					{serverErrors}


					<div className="grid grid-cols-2 gap-0">

						<div className="px-4 pb-0">
							<label htmlFor="usrName" className="text-sm block font-bold py-0 ">User Name</label>
							<input type="text" name="usrName" {...register("usrName")}
								className=" form-control border rounded py-0 focus:ring text-gray-700"
							/>
							<div className="text-red-500">{errors.usrName?.message}</div>
						</div>

						<div className="px-4 pb-0 ">
							<label className="text-sm block font-bold py-0 ">Account No.</label>
							<input type="text" name="accountNo" {...register("accountNo")}
								className="form-control border rounded py-0 text-gray-700 focus:ring "
							/>
							<div className="text-red-500">{errors.accountNo?.message}</div>
						</div>

						<div className="px-4 pb-0 ">
							<label className="text-sm block font-bold py-0 ">Login Name</label>
							<input type="text" name="loginName" {...register("loginName")}
								className="form-control border rounded py-0 text-gray-700 focus:ring "
							/>
							<div className="text-red-500">{errors.loginName?.message}</div>
						</div>

						<div className="px-4 pb-0">
							<LiveSearch name="dadDesignation" onChange={handleInputChange}
								parentData={parentData.dadDesignation} parentCallback={callback} fkEntity={entity.dadDesignation} />
							<div className="text-red-500 ">{errors.dadDesignation?.message}</div>
						</div>

						{id !== 'new' && <>
							<div className="px-4 pb-0 ">
								<label className="text-sm block font-bold py-0 ">Password</label>
								<input type="password" name="password" {...register("password")}
									className="form-control border rounded py-0 text-gray-700 focus:ring "
									placeholder={`Leave it blank if no change`}
								/>
								<div className="text-red-500">{errors.password?.message}</div>
							</div>

							<div className="px-4 pb-0 ">
								<label className="text-sm block font-bold py-0 ">Retype Password</label>
								<input type="password" name="password2"
									className="form-control border rounded py-0 text-gray-700 focus:ring "
									placeholder={`Leave it blank if no change`}
								/>
								<div className="text-red-500">{errors.password2?.message}</div>
							</div>
						</>}


						{id === 'new' && <>
							<div className="px-4 pb-0 ">
								<label className="text-sm block font-bold py-0 ">Password</label>
								<input type="password" name="password" {...register("password")}
									className="form-control border rounded py-0 text-gray-700 focus:ring "
								/>
								<div className="text-red-500">{errors.password?.message}</div>
							</div>

							<div className="px-4 pb-0 ">
								<label className="text-sm block font-bold py-0 ">Retype Password</label>
								<input type="password" name="password2" {...register("password2")}
									className="form-control border rounded py-0 text-gray-700 focus:ring "
								/>
								<div className="text-red-500">{errors.password2?.message}</div>
							</div>
						</>}

						<div className="px-4 pb-0 ">
							<label className="text-sm block font-bold py-0 ">Gender</label>
							<input type="radio" value="F" name="gender" {...register("gender")} checked={entity.gender === "F"}
								onChange={handleInputChange} /> Female <span>&nbsp; &nbsp;</span>
							<input type="radio" value="M" name="gender" checked={entity.gender === "M"}
								onChange={handleInputChange} /> Male <span>&nbsp; &nbsp;</span>
						</div>

						<div className="px-4 pb-0 ">
							<div > <LiveSearch name="section" onChange={handleInputChange}
								parentData={parentData.section} parentCallback={callback} fkEntity={entity.section} /> </div>
							<div className="text-red-500">{errors.section?.message}</div>
						</div>

						<div className="px-4 pb-0 ">
							<label className="text-sm block font-bold py-0 ">Phone 1</label>
							<input type="text" name="phone1" {...register("phone1")}
								className="form-control border rounded py-0 text-gray-700 focus:ring "
							/>
							<div className="text-red-500">{errors.phone1?.message}</div>
						</div>

						<div className="px-4 pb-0 ">
							<label className="text-sm block font-bold py-0 ">Phone 2</label>
							<input type="text" name="phone2" {...register("phone2")}
								className="form-control border rounded py-0 text-gray-700 focus:ring "
							/>
							<div className="text-red-500">{errors.phone2?.message}</div>
