/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By :
 */

import { useState, useEffect, useMemo } from "react";
import Table, { SelectColumnFilter } from "../utils/Table"; //
import axios from "axios";
import { withRouter, Link, useLocation, useHistory } from "react-router-dom";
import TableCopy from "../utils/TableCopy";

function BankList() {
  const [usr, setUsr] = useState({});
  const [data, setData] = useState([]);
  const [update, setUpdate] = useState(false);
  const [serverErrors, setServerErrors] = useState([]);
  const [search, setSearch] = useState("");
  const [inputText, setInputText] = useState("");
  const [mesg, setMesg] = useState("");
  const [state, setState] = useState("");

  const location = useLocation();

  const SecondPage = (props) => {
    useEffect(() => {
      console.log(">>>>>>>>" + location.state.detail);
    }, [location]);
  };

  useEffect(() => {
    let usr = JSON.parse(sessionStorage.getItem("usr"));
    setUsr(usr);
  }, []);

  useEffect(() => {
    let fetching = false;
    async function fetchData() {
      if (!fetching)
        await axios
          .get("/banks?search=" + search)
          .then((response) => {
            setData(response.data);
          })
          .catch((error) => {
            //console.log(error);
            //console.log(error.response.status);
            //console.log(error.response.headers);
            if (error.response) setServerErrors(error.response.data.error);
            else setServerErrors(error.Error);
          });
    }
    fetchData();
    return () => {
      fetching = true;
    };
  }, [update, search]);

  async function remove(id) {
    await axios
      .delete(`/banks/${id}`)
      .then(() => {
        //console.log(data);
        let updatedRecords = [...data].filter((i) => i.id !== id);
        console.log(updatedRecords);
        setData(updatedRecords);
        setUpdate(!update);
      })
      .catch((error) => {
        console.log(error);
        //console.log(error.response.status);
        //console.log(error.response.headers);
        if (error.response) setServerErrors(error.response.data.error);
        else setServerErrors(error.Error);
      });
  }

  const columns = useMemo(
    () => [
      {
        Header: "Action",
        Cell: ({ row }) => (
          <div>
            <Link to={"/banks/" + row.original.id}>
              <button className=" w-auto m-0 py-0 px-2">
                {usr?.designation?.designationLevel < 30 && "Edit"}
                {usr?.designation?.designationLevel >= 30 &&
                  usr?.designation?.designationLevel <= 40 &&
                  "Submit"}
                {usr?.designation?.designationLevel > 50 && "Approve"}
              </button>
            </Link>{" "}
            {/* <button
              className="w-16 m-0 p-0 bg-red-500 hover:bg-red-700 "
              onClick={() => remove(row.original.id)}
            >
              {" "}
              Delete{" "}
            </button> */}
          </div>
        ),
      },

      {
        Header: "Bank Name",
        accessor: "bankName",
      },

      {
        Header: "Bank Branch",
        accessor: "bankBranch",
      },

      {
        Header: "Bank Station",
        accessor: "bankStation",
      },

      {
        Header: "Ifsc",
        accessor: "ifsc",
      },

      {
        Header: "Treasury Code",
        accessor: "treasuryCode",
      },

      {
        Header: "Old Ifsc",
        accessor: "oldIfsc",
      },

      {
        Header: "Record Status",
        accessor: "recordStatus",
      },

      /*
		{
			Header: "Login Name",
			accessor: 'loginName',
			Filter: SelectColumnFilter,  // new
			filter: 'includes',
		},
		*/
    ],
    [data]
  );

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(inputText);
    setSearch(inputText);
  };

  const handleKeyPress = (event) => {
    // look for the `Enter` keyCode
    if (event.keyCode === 13 || event.which === 13) {
      handleSubmit(event);
    }
  };

  const PendingBanks = async () => {
    await axios
      .get(`/banks/pendings`)
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        //console.log(error);
        //console.log(error.response.status);
        //console.log(error.response.headers);
        if (error.response) setServerErrors(error.response.data.error);
        else setServerErrors(error.Error);
      });
  };

  return (
    <div className=" bg-gray-100 text-gray-900">
      <main className="mx-auto px-4 sm:px-6 lg:px-8 pt-4">
        <div className="mt-2 ml-4">
          <h1 className="text-xl font-semibold">Banks</h1>
          <div className="flexContainer">
            <input
              type="text"
              name="search"
              placeholder="ifsc"
              onChange={(e) => setInputText(e.target.value)}
              onKeyPress={handleKeyPress}
              className="pl-2 -ml-2 inputField flex-initial"
            />
            <button
              type="submit"
              onClick={handleSubmit}
              className="w-16 m-0 p-0"
            >
              Search
            </button>
            <div>
              <Link to={"/banks/new"}>
                <button className=" w-32 ml-8 p-0 h-6 -mt-2"> Add Bank </button>
              </Link>
            </div>
            <div>
              <button
                className=" w-32 ml-8 p-0 h-6 -mt-2"
                onClick={() => PendingBanks()}
              >
                {" "}
                Pendings{" "}
              </button>
            </div>
          </div>
          <div>
            <h1 className="text-xl font-semibold">{location.state}</h1>
          </div>
        </div>
        <div className="-mt-2 max-h-1 py-0">
          <TableCopy columns={columns} data={data} className="table-auto" />
        </div>
      </main>
    </div>
  );
}

export default withRouter(BankList);
