/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By : 
 */

import { useState, useEffect } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

//import DatePicker  from 'react-datepicker';
//import "react-datepicker/dist/react-datepicker.css";
//import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'

const schema = yup.object({

	//commissionControlDak: yup.object().required('Required'),
	dak: yup.object().required('Required'),

	basic: yup.number().integer().required('Required'),
	da: yup.number().integer().required('Required'),
	//msp: yup.number().integer().required('Required'),
	//npa: yup.number().integer().required('Required'),
	//techPay: yup.number().integer().required('Required'),

	formaLetterDate: yup.date().required('Required'),
	dateOfReporting: yup.date().required('Required'),

	formaLetterNo: yup.string().required('Required'),
	pan: yup.string().required('Required'),

	bankAccountNumber: yup.string().required('Required'),
	ifsc: yup.string().required('Required'),

});


const CommissionFormaEdit = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();

	let history = useHistory();
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});
	const [key, setKey] = useState('page1');
	const [ccDakId, setCcDakId] = useState('');
	const [goiLetterNo, setGoiLetterNo] = useState('');
	const [goiLetterDate, setGoiLetterDate] = useState('');
	const [commDate, setCommDate] = useState('');
	const [officerName, setOfficerName] = useState('');
	const [icNo, setIcNo] = useState('');
	const [dob, setDob] = useState('');
	const [rankName, setRankName] = useState('');
	const [loading, setLoading] = useState(true);


	useEffect(() => {
		let isCancelled = false;
		console.log(id);
		if (id !== 'new') {
			async function fetchData() {
				let record = '';
				await axios.get('/commissionFormas/' + id)
					.then((response) => {
						record = response.data;
						const fields = [
							'id'
							, 'dsopDate', 'commissionGoi', 'commissionControlDak', 'dak', 'lpcCredit', 'lpcDebit', 'basic', 'da', 'msp', 'npa', 'techPay', 'auditorDate', 'aaoDate', 'aoDate', 'goDate', 'approved', 'formaLetterDate', 'dateOfReporting', 'payAdvance', 'dsopWillingness', 'dsopAmount', 'orsService', 'orsServiceFrom', 'orsServiceTo', 'stipendAmount', 'cvpAmount', 'formaLetterNo', 'pan', 'uid', 'martialStatus', 'bankAccountNumber', 'ifsc', 'mobile', 'email', 'homeTown', 'nokName', 'nokRelation', 'orsServiceNo', 'recordStatus', 'reason', 'paoName', 'remarks', 'monthEnding'
						];
						setCcDakId(record['commissionGoi'].commissionControl.dak.dakidNo);
						setGoiLetterNo(record['commissionGoi'].commissionControl.goiLetterNo);
						setGoiLetterDate(record['commissionGoi'].commissionControl.goiLetterDate);
						setCommDate(record['commissionGoi'].commissionControl.commissionDate);
						setOfficerName(record['commissionGoi'].officerName);
						setIcNo(record['commissionGoi'].icNo);
						setDob(record['commissionGoi'].dateOfBirth);
						setOfficerName(record['commissionGoi'].officerName);
						setRankName(record['commissionGoi'].rank.rankName);

						fields.forEach(field => setValue(field, record[field]));
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					});


				if (!isCancelled) {
					setEntity(record);
					setState(prev => ({ ...prev, state: record }));
				}
			}

			fetchData();
			return () => {
				isCancelled = true;
			};
		}

	}, [id, setValue]);

	useEffect(() => {
		let fetching = false;
		let unmounted = false;
		console.log("daktype");
		async function fetchPayData() {
			console.log(id);
			if (!fetching && id )
				//console.log(secId);
				await axios.get(`/commissionFormas/${id}/payAndDa`)
					.then((response) => {
						console.log("response>>pay data>>" + response.data[0]+"--"+response.data[1]);
						setValue('basic',response.data[0]);
						setValue('da',response.data[1]);

						if (!unmounted) {

							setLoading(false);
						}
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		fetchPayData();

		return () => { fetching = true; unmounted = true; }
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [id]);


	useEffect(() => {
		let fetching = false;
		let unmounted = false;
		let rec = '';

		async function fetchCommissionData() {

			if (!fetching && ccDakId.length > 0)
				console.log(ccDakId);
			await axios.get('/commissionControls?filter=' + ccDakId)
				.then((response) => {
					console.log("response>>" + response.data["commissionType"].typeName);
					rec = response.data;

					setValue('commissionControlDak', response.data["commissionControlDak"]);
					setValue('commissionType', response.data["commissionType"]);
					//setValue('commissionTypeName',response.data["commissionType"].typeName)

					setGoiLetterNo(rec["goiLetterNo"]);
					setGoiLetterDate(rec["goiLetterDate"]);
					setCommDate(rec["commissionDate"]);

					if (!unmounted) {

					}
				})
				.catch((error) => {
					//console.log(error);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					if (error.response)
						setServerErrors(error.response.data.error);
					else
						setServerErrors(error.Error);
				})
		}
		fetchCommissionData();

		return () => { fetching = true; unmounted = true; }
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [ccDakId]);
	const onSubmit = (data, event) => {
		event.preventDefault();
		console.log(data);
		if (data.id) {
			axios.put("/commissionFormas/" + data.id, data)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		} else {
			axios.post("/commissionFormas", data)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		}

		history.push("/commissionFormas");
	}

	const onError = (errors, e) => console.log(errors, e);

	//All foreign keys. 
	// XXXXXX Add search fields and adjust the values as necessary
	const parentData = {



		dak: {
			title: "Dak",
			url: "daks/formaDaks",
			searchList: ['dakidNo'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "dak",
		},
		bank: {
			title: "IFSC",
			url: "banks",
			searchList: ['ifsc','bankName'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "bank",
		},
	}

	//Callback for child components (Foreign Keys)
	const callback = (childData) => {
		console.log("Parent Callback"+childData.fk+"=="+childData.entity.ifsc);
		setEntity(prev => ({ ...prev, [childData.fk]: childData.entity }));
		setValue(childData.fk, childData.entity);
		//console.log(errors);

		if (childData.fk === 'dak') {
			//console.log(childData.entity.referenceNo);

			if(childData.fk==='bank')
				setValue('ifsc',childData.entity.ifsc);
			setValue('formaLetterNo', childData.entity.referenceNo);
			setValue('formaLetterDate', childData.entity.referenceDate);

		}
		clearErrors(childData.fk);
	};

	const errorCallback = (err) => {
		//console.log(err);
		setServerErrors(err);
	}

	const handleInputChange = (e) => {
		//console.log(e.target.value);
	};

	const fixPay = (e) => {
		//console.log(e.target.value);

	};
	const handleCcDak = (e) => {
		setCcDakId(e.target.value);
		console.log(ccDakId);
	};

	return (
		<div className="max-w-xl mx-auto ">
			<div className="w-full w-3/4  mx-auto " >


				<form onSubmit={handleSubmit(onSubmit, onError)}>
					<h1 >{id === 'new' ? 'Add' : 'Edit'} Commission Forma </h1>
					<div className="text-red-500">{serverErrors}</div>
					<Tabs
						id="CommisionFormaEdit"
						activeKey={key}
						onSelect={(k) => setKey(k)}
						className="mb-3">

						<Tab eventKey="page1" title="GoI Info" className="h-120">

							<div className="grid grid-cols-2 gap-0">

								<div>
									<label>Commission Control Dak</label>
									<input type="text" name="ccDak" value={ccDakId} readonly
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.lpcCredit?.message}</div>
								</div>


								<div>
									<label>GoI Letter No</label>
									<input type="text" name="goiLetterNo" value={goiLetterNo} readonly
										className="form-control py-0" />

								</div>

								<div>
									<label>GoI Letter Date</label>
									<input type="date" name="goiLetterDate" value={goiLetterDate} readonly
										className="form-control py-0" />

								</div>

								<div>
									<label>Commission Date</label>
									<input type="date" name="commDate" value={commDate} readonly
										className="form-control py-0" />

								</div>
								<div>
									<label>Officer Name</label>
									<input type="text" name="officerName" value={officerName} readonly
										className="form-control py-0" />

								</div>
								<div>
									<label>Ic No</label>
									<input type="text" name="icNo" value={icNo} readonly
										className="form-control py-0" />

								</div>
								<div>
									<label>Rank</label>
									<input type="text" name="rankName" value={rankName} readonly
										className="form-control py-0" />

								</div>



								<div >
									<LiveSearch name="dak" onChange={handleInputChange}
										parentData={parentData.dak} parentCallback={callback}
										fkEntity={entity.dak} errCallback={errorCallback} />
									<div className="text-red-500 ">{errors.dak?.message}</div>
								</div>
								<div>
									<label>Forma Letter No</label>
									<input type="text" name="formaLetterNo" {...register("formaLetterNo")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.formaLetterNo?.message}</div>
								</div>
								<div>
									<label>Forma Letter Date</label>
									<input type="date" name="formaLetterDate" {...register("formaLetterDate")}
										className="form-control py-0" />
									<div className="text-red-500">{errors.formaLetterDate?.message}</div>
								</div>


								<div>
									<label>Date Of Reporting</label>
									<input type="date" name="dateOfReporting" {...register("dateOfReporting")}
										className="form-control py-0" />
									<div className="text-red-500">{errors.dateOfReporting?.message}</div>
								</div>





								<div>
									<label>Remarks</label>
									<textarea type="text" name="remarks" {...register("remarks")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.remarks?.message}</div>
								</div>


							</div>
						</Tab>
						<Tab eventKey="page2" title="Pay" className="h-120">
							<div className="grid grid-cols-2 gap-0 ">
								<div>
									<label>Basic</label>
									<input type="text" name="basic" {...register("basic")}  
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.basic?.message}</div>
								</div>


								<div>
									<label>Da</label>
									<input type="text" name="da" {...register("da")}  
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.da?.message}</div>
								</div>


								<div>
									<label>Msp</label>
									<input type="text" name="msp" {...register("msp")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.msp?.message}</div>
								</div>


								<div>
									<label>Npa</label>
									<input type="text" name="npa" {...register("npa")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.npa?.message}</div>
								</div>


								<div>
									<label>Tech Pay</label>
									<input type="text" name="techPay" {...register("techPay")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.techPay?.message}</div>
								</div>

								<div>
									<label>Lpc Credit</label>
									<input type="text" name="lpcCredit" {...register("lpcCredit")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.lpcCredit?.message}</div>
								</div>


								<div>
									<label>Lpc Debit</label>
									<input type="text" name="lpcDebit" {...register("lpcDebit")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.lpcDebit?.message}</div>
								</div>


								<div>
									<label>Pay Advance</label>
									<input type="text" name="payAdvance" {...register("payAdvance")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.payAdvance?.message}</div>
								</div>

								<div>
									<label>Stipend Amount</label>
									<input type="text" name="stipendAmount" {...register("stipendAmount")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.stipendAmount?.message}</div>
								</div>


								<div>
									<label>Cvp Amount</label>
									<input type="text" name="cvpAmount" {...register("cvpAmount")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.cvpAmount?.message}</div>
								</div>


							</div>
						</Tab>
						<Tab eventKey="page3" title="Bank" className="h-120">
							<div className="grid grid-cols-2 gap-0 ">


								<div>
									<label>IFSC</label>
									<input type="text" name="ifsc" {...register("ifsc")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.ifsc?.message}</div>
								</div>
								<div>
									<label>Bank Account Number</label>
									<input type="text" name="bankAccountNumber" {...register("bankAccountNumber")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.bankAccountNumber?.message}</div>
								</div>




							</div>
						</Tab>
						<Tab eventKey="page4" title="Dsop" className="h-120">
							<div className="grid grid-cols-2 gap-0 ">


								<div>
									<label>Dsop Willingness</label>
									<input type="text" name="dsopWillingness" {...register("dsopWillingness")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.dsopWillingness?.message}</div>
								</div>
								<div>
									<label>Dsop Date</label>
									<input type="date" name="dsopDate" {...register("dsopDate")}
										className="form-control py-0" />
									<div className="text-red-500">{errors.dsopDate?.message}</div>
								</div>

								<div>
									<label>Dsop Amount</label>
									<input type="text" name="dsopAmount" {...register("dsopAmount")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.dsopAmount?.message}</div>
								</div>

							</div>
						</Tab>
						<Tab eventKey="page5" title="PAO" className="h-120">
							<div className="grid grid-cols-2 gap-0 ">


							<div>
									<label>Ors Service No</label>
									<input type="text" name="orsServiceNo" {...register("orsServiceNo")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.orsServiceNo?.message}</div>
								</div>


								<div>
									<label>Ors Service From</label>
									<input type="date" name="orsServiceFrom" {...register("orsServiceFrom")}
										className="form-control py-0" />
									<div className="text-red-500">{errors.orsServiceFrom?.message}</div>
								</div>


								<div>
									<label>Ors Service To</label>
									<input type="date" name="orsServiceTo" {...register("orsServiceTo")}
										className="form-control py-0" />
									<div className="text-red-500">{errors.orsServiceTo?.message}</div>
								</div>






								<div>
									<label>Pao Name</label>
									<input type="text" name="paoName" {...register("paoName")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.paoName?.message}</div>
								</div>


							</div>
						</Tab>
						<Tab eventKey="page6" title="Other" className="h-120">
							<div className="grid grid-cols-2 gap-0 ">



								<div>
									<label>Pan</label>
									<input type="text" name="pan" {...register("pan")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.pan?.message}</div>
								</div>


								<div>
									<label>Uid</label>
									<input type="text" name="uid" {...register("uid")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.uid?.message}</div>
								</div>


								<div>
									<label>Martial Status</label>
									<input type="text" name="martialStatus" {...register("martialStatus")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.martialStatus?.message}</div>
								</div>
								<div>
									<label>Mobile</label>
									<input type="text" name="mobile" {...register("mobile")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.mobile?.message}</div>
								</div>


								<div>
									<label>Email</label>
									<input type="text" name="email" {...register("email")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.email?.message}</div>
								</div>


								<div>
									<label>Home Town</label>
									<input type="text" name="homeTown" {...register("homeTown")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.homeTown?.message}</div>
								</div>


								<div>
									<label>Nok Name</label>
									<input type="text" name="nokName" {...register("nokName")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.nokName?.message}</div>
								</div>


								<div>
									<label>Nok Relation</label>
									<input type="text" name="nokRelation" {...register("nokRelation")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.nokRelation?.message}</div>
								</div>


							</div>
						</Tab>
					</Tabs>
					<div className="px-4">
						<button type="submit" >Save</button>
					</div>

				</form>
			</div>
		</div>
	);
};

export default withRouter(CommissionFormaEdit);
