import { useState, useEffect, useMemo } from "react";
import Table, { SelectColumnFilter } from "../utils/Table"; //
import axios from "axios";

import { withRouter, Link, useLocation } from "react-router-dom";
import App from "../App";
import { includes } from "lodash";

function WithholdList() {
  const [data, setData] = useState([]);
  const [update, setUpdate] = useState(false);
  const [serverErrors, setServerErrors] = useState([]);
  const [search, setSearch] = useState("");
  const [inputText, setInputText] = useState("");

  const [cdaoNo, setCdaoNo] = useState("");
  const location = useLocation();
  const [loggedInUsr, setLoggedInUsr] = useState({});

  useEffect(() => {
    setLoggedInUsr(JSON.parse(sessionStorage.getItem("usr")));
    console.log(JSON.parse(sessionStorage.getItem("usr")))
  }, []);



  async function submitWithhold(id) {
		await axios.put(`/withholdPay/${id}/submit`)
			.then(() => {
				console.log(data);
				let updatedRecords = [...data].filter((i) => i.id !== id);
				console.log(updatedRecords);
				setData(updatedRecords);
				setUpdate(!update);
			})
			.catch((error) => {
				console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}

  async function approveWithhold(id) {
		await axios.put(`/withholdPay/${id}/approve`)
			.then(() => {
				console.log(data);
				let updatedRecords = [...data].filter((i) => i.id !== id);
				console.log(updatedRecords);
				setData(updatedRecords);
				setUpdate(!update);
			})
			.catch((error) => {
				console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}



  useEffect(() => {
    let fetching = false;
    async function fetchData() {
      if (!fetching){
          await axios
            .get("/withholdPay?search=" + search)
            .then((response) => {
              setData(response.data);
              console.log("data",response.data);
            })
            .catch((error) => {
              //console.log(error);
              //console.log(error.response.status);
              //console.log(error.response.headers);
              if (error.response) setServerErrors(error.response.data.error);
              else setServerErrors(error.Error);
            });
        }
    }
    fetchData();
    return () => {
      fetching = true;
    };
  }, [update, search]);


  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(inputText);
    setSearch(inputText);
  };

  const handleKeyPress = (event) => {
    // look for the `Enter` keyCode
    if (event.keyCode === 13 || event.which === 13) {
      handleSubmit(event);
    }
  };
  const columns = useMemo(
    () => [
      {
         Header: "Action",
         Cell: ({ row }) => (
				  <div>
           {(row.original.action === 'edit' && row.original.recordStatus==='V' && row.original.approved===true) &&
					<div>
            <Link to={"/withholdPay/"+row.original.id}>
            <button className="w-16 m-0 p-0 bg-green-500 hover:bg-green-700 "
           >	Edit 	</button>
           </Link>
        </div>

					}


					{(row.original.action === 'subm' && row.original.recordStatus==='P' && row.original.aaoDate===null) &&
						<button
							className="w-16 m-0 p-0 bg-green-500 hover:bg-green-700 "
							onClick={() => submitWithhold(row.original.id)}
						>	Submit 	</button>
					}

          {(row.original.action === 'approve' && row.original.recordStatus==='P' && row.original.aoDate===null) &&
						<button
							className="w-16 m-0 p-0 bg-green-500 hover:bg-green-700 "
							onClick={() => approveWithhold(row.original.id)}
						>	Approve 	</button>
					}
          </div>)
      },

      {
        Header: "Cdao No",
        accessor:"cdaoNo",
      },

      {
        Header: "Officer Name",
        accessor:"employee.officerName",
      },
      {
        Header: "Percentage",
        accessor:"withholdPercentage",
      },
      {
        Header: "Amount",
        accessor:"amount",
      },


      {
        Header: "Basic Pay",
        Cell: ({ row }) => (
          <div>
            {row.original.basicPay===true?<span className="text-red-500">Y</span>:<span className="text-green-500">N</span>}
          </div>
        )
      },
      {
        Header: "DA",
        Cell: ({ row }) => (
          <div>
            {row.original.da===true?<span className="text-red-500">Y</span>:<span className="text-green-500">N</span>}
          </div>
        )
      },

      {
        Header: "MSP",
        Cell: ({ row }) => (
          <div>
            {row.original.msp===true?<span className="text-red-500">Y</span>:<span className="text-green-500">N</span>}
          </div>
        )
      },

      {
        Header: "Approved",
        accessor:"approved",
    //    Filter: SelectColumnFilter,
      //  filter: "includes",
         Cell: ({ row }) => (
          <div>
            {row.original.approved===true?<span className="text-red-500">Approved</span>:<span className="text-green-500">Not Approved</span>}
          </div>
        )
      },

      {
        Header: "From Date",
        accessor:"fromDate",
      },

      {
        Header: "To Date",
        accessor:"toDate",
      },
      {
        Header: "Punch Dates",
        Cell: ({ row }) => (
          <div className="text-xs">
            {row.original.auditorDate!=null?<span className="text-blue-700 font-bold">Aud: {row.original.auditorDate}<br></br></span>:""}
            {row.original.aaoDate!=null?<span className="text-green-700 font-bold">AAO: {row.original.aaoDate}<br></br></span>:""}
            {row.original.aoDate!=null?<span className="text-red-700 font-bold">AO: {row.original.aoDate}</span>:""}
          </div>
        )
      },
      {
        Header: "Remarks",
        accessor:"remarks",
      },
      {
        Header: "Record Status",
        accessor:"recordStatus",
        Filter: SelectColumnFilter,
        filter:'includes',
      },
    ],
    [data]
  );

  return (
    <div className="min-h-screen bg-gray-100 text-gray-900">
      <main className="max-w-8xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
        <div className="mt-2 ml-4">
          <h1 className="text-xl font-semibold">Withholds</h1>
          <div className="text-red-500 font-bold font-xl my-2">**In case of new Withhold order with different "from date" create a new Withhold.
          ***In case of modification of same withhold with same "from Date", use edit facility.
          </div>

          <div className="flexContainer">
            <input
              type="text"
              name="search"
              placeholder="CDAO NO"
              onChange={(e) => setInputText(e.target.value)}
              onKeyPress={handleKeyPress}
              className="pl-2 -ml-2 inputField flex-initial"
            />
            <button
              type="submit"
              onClick={handleSubmit}
              className="w-16 m-0 p-0"
            >
              Search
            </button>

            <div>
              <Link to={"/inactive/withholdPay"}>
                <button className=" w-32 ml-8 p-0 h-6 -mt-2">Old Withholds</button>
              </Link>
            </div>

            {loggedInUsr?.designation?.designationLevel<30 ? (<div>
              <Link to={"/withholdPay/new"}>
                <button className=" bg-red-500 hover:bg-red-600 w-32 ml-8 p-0 h-6 -mt-2">Add Withhold</button>
              </Link>
            </div>):""}
          </div>

          <div>
            <h1 className="text-xl font-semibold">{location.state}</h1>
          </div>
        </div>

        <div className="-mt-2 max-h-1 py-0">
          <Table columns={columns} data={data} className="table-auto" />
          <table className="border-2 border-black">
          <tr className="border-2 border-black">

              <th className="border-2 border-black">Record Status</th>
              <th className="border-2 border-black">Meaning</th>




          </tr>
          <tbody className="border-2 border-black">
          <tr className="border-2 border-black" >
              <td className="border-2 border-black">P</td>
              <td className="border-2 border-black">Under Process</td>
            </tr>
            <tr className="border-2 border-black">
              <td className="border-2 border-black">V</td>
              <td className="border-2 border-black">Active</td>
            </tr>
            <tr className="border-2 border-black">
              <td className="border-2 border-black">O</td>
              <td className="border-2 border-black"> Inactive</td>
            </tr>
          </tbody>
         </table>
        </div>

      </main>

    </div>
  );
}

export default withRouter(WithholdList);
