import { useState, useEffect, useMemo, useCallback } from 'react';
import Table, { SelectColumnFilter } from '../utils/Table'  // 
import axios from "axios";
import { withRouter, Link } from "react-router-dom";
import { useForm } from "react-hook-form";
import debounce from 'lodash';
import useDebouncedSearch from '../utils/useDebouncedSearch'

/*
const useSearch = () => useDebouncedSearch(text => searchAsync(text))

const searchAsync = async function (text){
	console.log(text);
}
*/
function GrievanceList() {

	const [data, setData] = useState([]);
	const [update, setUpdate] = useState(false);
	const [serverErrors, setServerErrors] = useState([]);
	const {register, handleSubmit, formState: { errors }, clearErrors } = useForm({});
	const [search, setSearch] = useState('a');
	const [key, setKey] = useState('page1');

	const useSearch = () => useDebouncedSearch(text => searchAsync(text))

	const searchAsync = async function(text) {
		console.log(text);
	}

	const { inputText, setInputText, searchResults } = useSearch();

	useEffect(() => {
		let fetching = false;
		async function fetchData() {
			if (!fetching)
				await axios.get('/grievances?search=' + search)
					.then((response) => {
						console.log(response.data);
						setData(response.data);
					})
					.catch((error) => {
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		fetchData();
		return () => { fetching = true; }

	}, [update, search]);



	async function remove(id) {
		await axios.delete(`/grievances/${id}`)
			.then(() => {
				//console.log(data);
				let updatedRecords = [...data].filter((i) => i.id !== id);
				console.log(updatedRecords);
				setData(updatedRecords);
				setUpdate(!update);
			})
			.catch((error) => {
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}


	const columns = useMemo(() => [
		{
			Header: 'Action',
			Cell: ({ row }) => (
				<div>
					<Link to={"/grievances/" + row.original.id}>
						<button className=" w-16 m-0 p-0 " > Edit </button>
					</Link>
					{' '}
					{/*
					<button
						className="w-16 m-0 p-0 bg-red-500 hover:bg-red-700 "
						onClick={() => remove(row.original.id)}
					>	Delete 	</button>
					*/}
				</div>
			)
		},
		{
			Header: "Dak",
			accessor: 'dak.dakidNo',
		},
		{
			Header: "Section",
			accessor: 'section.sectionName',
		},


		{
			Header: "Cdao No",
			accessor: 'cdaoNo',
		},	

		{
			Header: "Name",
			accessor: 'employee.officerName',
		},

		{
			Header: "Subject",
			accessor: 'subject',
		},	


		{
			Header: "Disposal Date",
			accessor: 'disposalDate',
		},

		{
			Header: "Disposal Remarks",
			accessor: 'disposalRemarks',
		},		




/*



		{
			Header: "Unit",
			accessor: 'unit',
		},




		{
			Header: "Grievance Source",
			accessor: 'grievanceSource',
		},

		{
			Header: "Month Ending",
			accessor: 'monthEnding',
		},



		{
			Header: "Check Digit",
			accessor: 'checkDigit',
		},

		{
			Header: "Web Id",
			accessor: 'webId',
		},

		{
			Header: "Email Id",
			accessor: 'emailId',
		},

		{
			Header: "Grievance Date",
			accessor: 'grievanceDate',
		},



		{
			Header: "Reference No",
			accessor: 'referenceNo',
		},

		{
			Header: "Reference Date",
			accessor: 'referenceDate',
		},

		{
			Header: "Grievance Brief",
			accessor: 'grievanceBrief',
		},

		{
			Header: "Disposal No",
			accessor: 'disposalNo',
		},


		{
			Header: "Auditor Remarks",
			accessor: 'auditorRemarks',
		},

		{
			Header: "Auditor Date",
			accessor: 'auditorDate',
		},

		{
			Header: "Aao Remarks",
			accessor: 'aaoRemarks',
		},

		{
			Header: "Aao Date",
			accessor: 'aaoDate',
		},

		{
			Header: "Ao Remarks",
			accessor: 'aoRemarks',
		},

		{
			Header: "Ao Date",
			accessor: 'aoDate',
		},

		{
			Header: "Record Status",
			accessor: 'recordStatus',
		},

		{
			Header: "Approved",
			accessor: 'approved',
		},

*/
		/*
		{
			Header: "Login Name",
			accessor: 'loginName',
			Filter: SelectColumnFilter,  // new
			filter: 'includes',
		},
		*/
	], [data])



	const handleKeyPress = (event) => {
		// look for the `Enter` keyCode
		if (event.keyCode === 13 || event.which === 13) {
			handleSubmit(event)
		}
	}

	return (
		<div className="min-h-screen bg-gray-100 text-gray-900">
			<main className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">

				<div className="mt-2 ml-4">
					<h1 className="text-xl font-semibold">Grievances</h1>
					<div className="flexContainer">
						<input type="text" name="search" placeholder="CDAO No, Name, Subject"
						onChange={e => setInputText(e.target.value)}
							onKeyPress={handleKeyPress}
							className="pl-2 -ml-2 inputField flex-initial" />
						<button type="submit" onClick={handleSubmit} className="w-16 m-0 p-0">Search</button>
						<div>
							<Link to={"/grievances/new/"}>
								<button className=" w-32 ml-8 p-0 h-6 -mt-2" > Add Grievance </button>
							</Link>
						</div>						
					</div>					
				</div>
				<div className="mt-2 max-h-1">
					<Table columns={columns} data={data} className="table-auto" />
				</div>
			</main>
		</div>
	);

	return null;
}

export default withRouter(GrievanceList);
