/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By : 
 */

import { useState, useEffect } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

//import DatePicker  from 'react-datepicker';
//import "react-datepicker/dist/react-datepicker.css";
//import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'

const schema = yup.object({
      dak: yup.object().required('Required'),
      paymentMode: yup.object().required('Required'),
      section: yup.object().required('Required'),
      sectionCode: yup.number().integer().required('Required'),
      unit: yup.object().required('Required'),
      employee: yup.object().required('Required'),
      schedule3: yup.object().required('Required'),
      itemAmount: yup.number().required('Required'),
      bankAccount: yup.object().required('Required'),
      approved: yup.boolean().required('Required'),
      auditorDate: yup.string().required('Required'),
      aaoDate: yup.string().required('Required'),
      aoDate: yup.string().required('Required'),
      goDate: yup.string().required('Required'),
      cda13Date: yup.string().required('Required'),
      amount: yup.number().required('Required'),
      cdaoNo: yup.string().required('Required'),
      checkDigit: yup.string().required('Required'),
      cda13No: yup.string().required('Required'),
      multipleCmpRejection: yup.string().required('Required'),
      rejectedCmpReferenceNo: yup.string().required('Required'),
      rejectedChequeNo: yup.string().required('Required'),
      recordStatus: yup.string().required('Required'),
      reason: yup.string().required('Required'),
      remarks: yup.string().required('Required'),
      oldDakId: yup.string().required('Required'),
      paymentType: yup.string().required('Required'),
      rejectionType: yup.string().required('Required'),
});


const IafCda13Edit = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();

	let history = useHistory();
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});
	const [key, setKey] = useState('page1');


	useEffect(() => {
		let isCancelled = false;
		console.log(id);
		if (id !== 'new') {
			async function fetchData() {
				let record = '';
				await axios.get('/iafCda13s/' + id)
					.then((response) => {
						record = response.data;
						const fields = [
						'id'
, 'dak', 'paymentMode', 'section', 'sectionCode', 'unit', 'employee', 'schedule3', 'itemAmount', 'bankAccount', 'approved', 'auditorDate', 'aaoDate', 'aoDate', 'goDate', 'cda13Date', 'amount', 'cdaoNo', 'checkDigit', 'cda13No', 'multipleCmpRejection', 'rejectedCmpReferenceNo', 'rejectedChequeNo', 'recordStatus', 'reason', 'remarks', 'oldDakId', 'paymentType', 'rejectionType'				
					];
					fields.forEach(field => setValue(field, record[field]));
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if(error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					});


				if (!isCancelled) {
					setEntity(record);
					setState(prev => ({ ...prev, state: record }));
				}
			}

			fetchData();
			return () => {
				isCancelled = true;
			};
		}

	}, [id, setValue]);


	const onSubmit = (data, event) => {
		event.preventDefault();
		console.log(data);
		if (data.id) {
			axios.put("/iafCda13s/" + data.id, data)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		} else {
			axios.post("/iafCda13s", data)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		}

		history.push("/iafCda13s");
	}

	const onError = (errors, e) => console.log(errors, e);

	//All foreign keys. 
	// XXXXXX Add search fields and adjust the values as necessary
	const parentData = {

		dak: {
			title: "Dak",
			url : "daks",
			searchList : ['id'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "dak",
		},
		paymentMode: {
			title: "Payment Mode",
			url : "paymentModes",
			searchList : ['id'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "paymentMode",
		},
		section: {
			title: "Section",
			url : "sections",
			searchList : ['id'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "section",
		},
		unit: {
			title: "Unit",
			url : "units",
			searchList : ['id'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "unit",
		},
		employee: {
			title: "Employee",
			url : "employees",
			searchList : ['id'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "employee",
		},
		schedule3: {
			title: "Schedule3",
			url : "schedule3s",
			searchList : ['id'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "schedule3",
		},
		bankAccount: {
			title: "Bank Account",
			url : "bankAccounts",
			searchList : ['id'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "bankAccount",
		},
	}

	//Callback for child components (Foreign Keys)
	const callback = (childData) => {
		//console.log("Parent Callback");
		setEntity(prev => ({ ...prev, [childData.fk]: childData.entity }));
		setValue(childData.fk, childData.entity);
		//console.log(errors);
		clearErrors(childData.fk);
	};

	const errorCallback = (err) => {
		//console.log(err);
		setServerErrors(err);
	}

	const handleInputChange = (e) => {
		//console.log(e.target.value);
	};

	return (
		<div className="max-w-xl mx-auto ">
			<div className="w-full w-3/4  mx-auto " >

				<form onSubmit={handleSubmit(onSubmit, onError)}>
					<h1 >{id === 'new' ? 'Add' : 'Edit'} Iaf Cda13 </h1>
					<div className="text-red-500">{serverErrors}</div>
					<Tabs
						id="IafCda13Edit"
						activeKey={key}
						onSelect={(k) => setKey(k)}
						className="mb-3"
					>
						<Tab eventKey="page1" title="Page 1" className="h-120">
							<div className="grid grid-cols-2 gap-0">
								<div >
									<LiveSearch name="dak" onChange={handleInputChange}
										parentData={parentData.dak} parentCallback={callback} 
										fkEntity={entity.dak} errCallback={errorCallback} />
									<div className="text-red-500 ">{errors.dak?.message}</div>
								</div>						


								<div >
									<LiveSearch name="paymentMode" onChange={handleInputChange}
										parentData={parentData.paymentMode} parentCallback={callback} 
										fkEntity={entity.paymentMode} errCallback={errorCallback} />
									<div className="text-red-500 ">{errors.paymentMode?.message}</div>
								</div>						


								<div >
									<LiveSearch name="section" onChange={handleInputChange}
										parentData={parentData.section} parentCallback={callback} 
										fkEntity={entity.section} errCallback={errorCallback} />
									<div className="text-red-500 ">{errors.section?.message}</div>
								</div>						


								<div>
									<label>Section Code</label>
									<input type="text" name="sectionCode" {...register("sectionCode")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.sectionCode?.message}</div>
								</div>


								<div >
									<LiveSearch name="unit" onChange={handleInputChange}
										parentData={parentData.unit} parentCallback={callback} 
										fkEntity={entity.unit} errCallback={errorCallback} />
									<div className="text-red-500 ">{errors.unit?.message}</div>
								</div>						


								<div >
									<LiveSearch name="employee" onChange={handleInputChange}
										parentData={parentData.employee} parentCallback={callback} 
										fkEntity={entity.employee} errCallback={errorCallback} />
									<div className="text-red-500 ">{errors.employee?.message}</div>
								</div>						


								<div >
									<LiveSearch name="schedule3" onChange={handleInputChange}
										parentData={parentData.schedule3} parentCallback={callback} 
										fkEntity={entity.schedule3} errCallback={errorCallback} />
									<div className="text-red-500 ">{errors.schedule3?.message}</div>
								</div>						


								<div>
									<label>Item Amount</label>
									<input type="text" name="itemAmount" {...register("itemAmount")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.itemAmount?.message}</div>
