import { useState, useEffect} from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

//import DatePicker  from 'react-datepicker';
//import "react-datepicker/dist/react-datepicker.css";
//import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'


const schema = yup.object({

      employee: yup.object().required('Required'),





});


const LpcRegisterEdit = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();

	let history = useHistory();
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});
	const [key, setKey] = useState('page1');
	const [fsType,setFsType]=useState('');

	const [empId,setEmpId]=useState('');
	//const option=useContext(optionSelected);
	const [me,setMe]=useState('');
	const [mesg,setMesg]=useState('');
	const [disDate,setDisDate]=useState('');
	const [miscFs,setMiscFs]=useState(false);
	const [provFs,setProvFs]=useState(false);



	useEffect(() => {
		let isCancelled = false;
		console.log(id);
	//	if (id !== 'new' && id!=='newm' && id!=='newp' && id!=='newr') {
			async function fetchData() {
				let record = '';
				await axios.get('/lpcRegisters/' + id)
					.then((response) => {
						record = response.data;
						const fields = [
						'id', 'dak', 'employee', 'dischargeType', 'dischargeDate', 'issueDate', 'cdaoNo', 'checkDigit', 
						'recordStatus', 'rejectionReason', 'task', 'stopPayDescription', 'qe', 'do2No', 'entryApproved', 
						'entryAuditor', 'entryAao', 'entryAo', 'entryAuditorDate', 'entryAaoDate', 'entryAoDate', 'cancelled',
						'cancelledAo', 'cancelledAoDate', 'fsApproved', 'fsAuditor', 'fsAao', 'fsAo', 'fsAcda', 'fsDcda', 
						'fsJcda', 'fsAuditorDate', 'fsAaoDate', 'fsAoDate', 'fsAcdaDate', 'fsDcdaDate', 'fsJcdaDate', 
						'ecsGenerated', 'whetherMiscFs','whetherProvisionalFs','provisionalFsApproved','auditorRemarks',
						'aaoRemarks','aoRemarks','fsType'			
					];

					if(record.whetherMiscFs===true){
						setMiscFs(true);
						setFsType('misc');
					}


					fields.forEach(field => setValue(field, record[field]));
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if(error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					});


				if (!isCancelled) {
					setEntity(record);
					setState(prev => ({ ...prev, state: record }));
				}
			}

			fetchData();
			return () => {
				isCancelled = true;
			};
	//	}
	/*	if(id==='newm'){
			setFsType('misc');
			console.log(id);
		}

		if(id==='newr'){
			setFsType('reg');
			console.log(id);
		}*/



	}, []);
		useEffect(() => {

		let fetching = false;
		let unmounted = false;

		async function fetchEmployeeStatus() {

			if (!fetching)
				axios.get(`/employees/task/`+empId)
					.then((response) => {

						 setEmpId(response.data[4]);

					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		fetchEmployeeStatus();

		return () => { fetching = true  }
	}, [empId]);
	useEffect(() => {

		let fetching = false;
		let unmounted = false;

		async function fetchCurrentMe() {

			if (!fetching)
				axios.get(`/miscs/currentMe`)
					.then((response) => {
						 setMe(response.data);

					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		fetchCurrentMe();

		return () => { fetching = true  }
	}, []);
	const onSubmit = (data, event) => {
		event.preventDefault();
		console.log(data);
		if (data.id) {

				axios.put("/lpcRegisters/" + data.id, data)
				.then((response) => { 
					setMesg(response.data);
				//	history.push("/lpcRegisters");
				})
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});



		} else {

			axios.post("/lpcRegisters", data)
				.then((response) => { 
					setMesg(response.data);
					//history.push("/lpcRegisters");
				})
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});


		}
	}


