/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 03 April 2024
 *
 * Modified By : 
 */

import { useState, useEffect } from "react";
import { useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

//import DatePicker  from 'react-datepicker';
//import "react-datepicker/dist/react-datepicker.css";
//import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'
import styles from "./../index.css"



const BillEdit = () => {

	let { id } = useParams();

	let navigate = useHistory();
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});
	const [key, setKey] = useState('page1');
	const [responseData, setResponseData] = useState(null);
	const [count, setCount] = useState(0);

	useEffect(() => {

		let record = '';
		let isCancelled = false;
		console.log(id);
		async function fetchData() {
			await axios.get('/mbills/' + id)
				.then((response) => {
					record = response.data;
					setResponseData(response.data);

				})
				.catch((error) => {

					if (error.response)
						setServerErrors(error.response.data.error);
					else
						setServerErrors(error.Error);
				});


			if (!isCancelled) {
				setEntity(record);
				setState(prev => ({ ...prev, state: record }));
			}
		}

		fetchData();
		return () => {
			isCancelled = true;
		};


	}, []);


	return (
		<div className="max-w-4xl mx-auto p-6">
			<div className="bg-green shadow overflow-hidden sm:rounded-lg">
				<div className="px-4 py-5 sm:px-6">
					<h3 className="text-lg leading-6 font-medium text-gray-900">Bill Details</h3>
				</div>
				<div className="border-t border-gray-200">
					<div className="gap-y-6 px-4 py-5 sm:grid-cols-3 sm:gap-x-6 sm:gap-y-8">
						<div className="sm:col-span-1 flex">
							<label htmlFor="dakId" className="block text-sm font-bold text-gray-700" style={{ width: '200px' }}>
								DakId No
							</label>
							<div className="mt-1">
								{responseData && responseData.dak.dakidNo}
								<span className="block text-sm text-gray-900"></span>
							</div>
						</div>
						<div className="sm:col-span-1 flex">
							<label htmlFor="month" className="block text-sm font-bold text-gray-700" style={{ width: '200px' }}>
								Month
							</label>
							<div className="mt-1">
								{responseData && responseData.month}

								<span className="block text-sm text-gray-900"></span>
							</div>
						</div>
					</div>
				</div>
			</div>
		</div>
	);
};

export default (BillEdit);
