import { useState, useEffect, useMemo } from 'react';
import Table, { SelectColumnFilter } from '../utils/Table'  // 
import axios from "axios";
import { withRouter, Link } from "react-router-dom";

function UsrList() {

	const [data, setData] = useState([]);
	const [update, setUpdate] = useState(false);
	//setData(useFetch('usrs'));

	useEffect(() => {
		let ignore = false;
		async function fetchData() {
			const result = await axios('/usrs', {
    params: {
      _limit: 10
     }
  });
			console.log(result.data);
			if (!ignore) setData(result.data);
		}
		fetchData();
		return () => { ignore = true; }

	}, [update]);


	async function remove(id) {
		await axios.delete(`/usrs/${id}`)
			.then(() => {
				console.log(data);
				let updatedRecords = [...data].filter((i) => i.id !== id);
				console.log(updatedRecords);
				setData(updatedRecords);
				setUpdate(!update);
			});
	}


	const columns = useMemo(() => [
		{
			Header: 'Action',
			Cell: ({ row }) => (
				<div>
					<Link to={"/usrs/" + row.original.id}>
						<button
							className=" w-16 h-5 pb-1 pt-0 "
						>
							Edit
              				</button>
					</Link>
					{'    '}
					<button
						className="w-16 h-5  bg-red-500 hover:bg-red-700 "
						onClick={() => remove(row.original.id)}
					>
						Delete
              			</button>

				</div>
			)
		},
		{
			Header: "Name",
			accessor: 'usrName',
		},
		{
			Header: "Designation",
			accessor: 'dadDesignation.designationAbbr',
		},
		{
			Header: "Login Name",
			accessor: 'loginName',
		}
		/*
		{
			Header: "Login Name",
			accessor: 'loginName',
			Filter: SelectColumnFilter,  // new
			filter: 'includes',
		},
		*/
	], [])


	return (
		<div className="min-h-screen bg-gray-100 text-gray-900">
			<main className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
				<div className="">
					<h1 className="text-xl font-semibold">Users</h1>
				</div>
				<div className="mt-6 max-h-1">
					<Table columns={columns} data={data} className="table-auto" />
				</div>
			</main>
		</div>
	);
}

export default withRouter(UsrList);
