/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By : 
 */

import { useState, useEffect } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

//import DatePicker  from 'react-datepicker';
//import "react-datepicker/dist/react-datepicker.css";
//import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'
import { useLocation } from 'react-router-dom'


const schema = yup.object({

	//commissionControlDak: yup.object().required('Required'),
	dak: yup.object().required('Required'),

	basic: yup.number().integer().required('Required'),
//	da: yup.number().integer().required('Required'),
	//msp: yup.number().integer().required('Required'),
	//npa: yup.number().integer().required('Required'),
	//techPay: yup.number().integer().required('Required'),

	formaLetterDate: yup.date().required('Required'),
	//dateOfReporting: yup.date().required('Required'),

	formaLetterNo: yup.string().required('Required'),
	pan: yup.string().required('Required'),

	bankAcno: yup.string().required('Required'),
	ifsc: yup.string().required('Required'),

});


const CommissionFormaEdit = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();

	let history = useHistory();
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});
	const [key, setKey] = useState('page1');
	const [ccDakId, setCcDakId] = useState('');
	const [goiLetterNo, setGoiLetterNo] = useState('');
	const [goiLetterDate, setGoiLetterDate] = useState('');
	const [commDate, setCommDate] = useState('');
	const [officerName, setOfficerName] = useState('');
	const [icNo, setIcNo] = useState('');
	const [dob, setDob] = useState('');
	const [alfrom,setAlfrom]=useState('');
	const [alto,setAlto]=useState('');
	const [antepayDate,setAntepayDate]=useState('');
	const [antepromDate,setAntepromDate]=useState('');
	const [rankName, setRankName] = useState('');
	const [loading, setLoading] = useState(true);
	const [action,setAction]=useState('');
	const [mesg,setMesg]=useState('');
	const {actionState}=useLocation();
	const [commType,setCommType]=useState('');
	const [readOnly,setReadOnly]=useState(false);
	const [rempl,setRempl]=useState(false);
	const [fromDate,setFromDate]=useState('');
	const [total,setTotal]=useState(0);
	const [corpsName,setCorpsName]=useState('');
	const [unit,setUnit]=useState('');
	const [maritalStatus,setMaritalStatus]=useState('');
	 const [nokRel,setNokRel]=useState('');
	 const [disabled,setDisabled]=useState(false);

	useEffect(() => {
		let isCancelled = false;
		console.log(id);
		console.log(actionState);
		if(actionState && actionState==='rollback')
			setAction('rollback');

		if(actionState && (actionState==='submit' || actionState==='approve' || actionState==='rollback'))
			setReadOnly(true);

		if (id !== 'new') {
			async function fetchData() {
				let record = '';
				await axios.get('/commissionFormas/' + id)
					.then((response) => {
						record = response.data;
						const fields = [
							'id'
							, 'dsopDate', 'commissionGoi', 'commissionControlDak', 'dak', 'lpcCredit', 'lpcDebit',  'msp', 'npa', 'techPay', 
							'auditorDate', 'aaoDate', 'aoDate', 'goDate', 'approved', 'formaLetterDate', 'dateOfReporting', 'payAdvance', 'dsopWillingness', 
							'dsopAmount', 'orsService', 'orsServiceFrom', 'orsServiceTo', 'stipendAmount', 'cvpAmount', 'formaLetterNo', 'pan', 'uid', 
							'maritalStatus', 'bankAcno', 'ifsc', 'mobile', 'email', 'homeTown', 'nokName', 'nokRelation', 'orsServiceNo', 'recordStatus',
							 'reason', 'paoName', 'remarks', 'monthEnding','action','commType','payLevel','unitDetails','bank','rollbackRemarks'
						];
						setCcDakId(record['commissionGoi'].commissionControl.dak.dakidNo);
						setGoiLetterNo(record['commissionGoi'].commissionControl.goiLetterNo);
						setGoiLetterDate(record['commissionGoi'].commissionControl.goiLetterDate);
						setCommDate(record['commissionGoi'].commissionControl.commissionDate);
						setOfficerName(record['commissionGoi'].officerName);
						setIcNo(record['commissionGoi'].icNo);
						setDob(record['commissionGoi'].dateOfBirth);
						setOfficerName(record['commissionGoi'].officerName);
						setRankName(record['commissionGoi'].rank.rankName);
						if(record['commissionGoi'].commissionControl.commissionType)
						setCommType(record['commissionGoi'].commissionControl.commissionType.typeName);
						//setId(record['id']);
						console.log(record['commType']);
						if(record['commissionGoi'].annualLeaveFrom)
							setAlfrom(record['commissionGoi'].annualLeaveFrom);
						if(record['commissionGoi'].annualLeaveTo)
							setAlto(record['commissionGoi'].annualLeaveTo);
						if(record['commissionGoi'].antedatePay)
							setAntepayDate(record['commissionGoi'].antedatePay);
						if(record['commissionGoi'].antedatePromotion)
							setAntepromDate(record['commissionGoi'].antedatePromotion);
						if(record['maritalStatus'])
						 	setMaritalStatus(record['maritalStatus']);
						if(record['nokRelation'])
							setNokRel(record['nokRelation']);
						if(record['commissionGoi'].commissionControl.totalOfficers)
							setTotal(record['commissionGoi'].commissionControl.totalOfficers);
						if(record['commissionGoi'].corps)
							setCorpsName(record['commissionGoi'].corps.unitName);
						if(record['unitDetails'])
							setUnit(record['unitDetails']);

						if((actionState && actionState!=='rollback') || !actionState)
								setAction(record['action']);

						if(record['commType'] && record['commType']==='REMPL'){
							setRempl(true);
							if(record['commissionGoi'].effectDate)
								setFromDate(record['commissionGoi'].effectDate);
							}

						fields.forEach(field => setValue(field, record[field]));
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					});


				if (!isCancelled) {
					setEntity(record);
					setState(prev => ({ ...prev, state: record }));
				}
			}

			fetchData();
			return () => {
				isCancelled = true;
			};
		}

	}, [id, setValue]);

	useEffect(() => {
		let fetching = false;
		let unmounted = false;
		console.log("daktype");
		async function fetchPayData() {
			console.log(id);
			if (!fetching && id )
				//console.log(secId);
				await axios.get(`/commissionFormas/${id}/payAndDa`)
					.then((response) => {
						console.log("response>>pay data>>" + response.data[0]+"--"+response.data[1]+"--");
						setValue('basic',response.data[0]);
						setValue('da',response.data[1]);
						if(response.data[2])
							setValue('cvpAmount',response.data[2]);


						if (!unmounted) {

							setLoading(false);
						}
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		fetchPayData();

		return () => { fetching = true; unmounted = true; }
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [id]);


	useEffect(() => {
		let fetching = false;
		let unmounted = false;
		let rec = '';

		async function fetchCommissionData() {

			if (!fetching && ccDakId.length > 0)
				console.log(ccDakId);
			await axios.get('/commissionControls?filter=' + ccDakId)
				.then((response) => {
					console.log("response>>" + response.data["commissionType"].typeName);
					rec = response.data;

					setValue('commissionControlDak', response.data["commissionControlDak"]);
					setValue('commissionType', response.data["commissionType"]);
					//setValue('commissionTypeName',response.data["commissionType"].typeName)

					setGoiLetterNo(rec["goiLetterNo"]);
					setGoiLetterDate(rec["goiLetterDate"]);
					setCommDate(rec["commissionDate"]);

					if (!unmounted) {

					}
				})
				.catch((error) => {
					//console.log(error);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					if (error.response)
						setServerErrors(error.response.data.error);
					else
						setServerErrors(error.Error);
				})
		}
		fetchCommissionData();

		return () => { fetching = true; unmounted = true; }
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [ccDakId]);

	async function submitForma(id) {
    await axios
      .put(`/commissionFormas/submission/${id}`)
      .then((response) => {
        console.log(response.data);
        if(response.data)
        	setServerErrors(response.data);

      //  console.log(response.data);

      })
      .catch((error) => {
        console.log(error);
        //console.log(error.response.status);
        //console.log(error.response.headers);
       // if (error.response) setServerErrors(error.response.data.error);
        	//else setServerErrors(error.Error);
      });
  }




  async function approveForma(id) {
    await axios
      .put(`/commissionFormas/approve/${id}`)
      .then((response) => {
        console.log(response.data);
        if(response.data)
        	setServerErrors(response.data);

      //  console.log(response.data);

      })
      .catch((error) => {
        console.log(error);
        //console.log(error.response.status);
        //console.log(error.response.headers);
       // if (error.response) setServerErrors(error.response.data.error);
        	//else setServerErrors(error.Error);
      });
  }

	const onSubmit = (data, event) => {
		event.preventDefault();
		console.log(data);
		if(disabled)
			return;

		setDisabled(true);

		if (data.id) {
			if(action && action==='rollback'){
				axios.put('/commissionFormas/rollback/'+ data.id, data)
				.then((response) => {
					console.log(response.data);
					if(response.data)
						setServerErrors(response.data);

					setDisabled(false);
				 })
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
			}else{
			axios.put(`/commissionFormas/${id}`, data)
				.then((response) => {
					console.log(response.data);
					if(response.data)
						setServerErrors(response.data.reason);

					setDisabled(false);
				 })
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
				}
		} else {
			axios.post("/commissionFormas", data)
				.then((response) => { 
					if(response.data)
						setServerErrors(response.data.reason);

						setDisabled(false);
				})
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		}


	}

	const onError = (errors, e) => console.log(errors, e);

	//All foreign keys. 
	// XXXXXX Add search fields and adjust the values as necessary
	const parentData = {



		dak: {
			title: "Forma Dak",
			url: "daks/formaDaks",
			searchList: ['dakidNo'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "dak",
		},
		bank: {
			title: "IFSC",
			url: "banks",
			searchList: ['ifsc','bankName','bankStation'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "bank",
		},
	}

	//Callback for child components (Foreign Keys)
	const callback = (childData) => {
		console.log("Parent Callback"+childData.fk+"=="+childData.entity.ifsc);
		setEntity(prev => ({ ...prev, [childData.fk]: childData.entity }));
		setValue(childData.fk, childData.entity);
		//console.log(errors);

		if (childData.fk === 'dak') {
			//console.log(childData.entity.referenceNo);

			setValue('formaLetterNo', childData.entity.referenceNo);
			setValue('formaLetterDate', childData.entity.referenceDate);

		}

		if(childData.fk==='bank'){
			setValue('ifsc',childData.entity.ifsc);
		}
		clearErrors(childData.fk);
	};

	const errorCallback = (err) => {
		//console.log(err);
		setServerErrors(err);
	}

	const handleInputChange = (e) => {
		//console.log(e.target.value);
	};
	const handleRollback = (e) => {
		//console.log(e.target.value);
		setValue("rollbackRemarks",e.target.value);
	};

	const handleDsopWillingness = (e) => {
		//console.log(e.target.value);
		let dsop=e.target.value;
		if(dsop==='Y')
			setValue('dsopWillingness',"true");
		else if(dsop==='N')
			setValue('dsopWillingness',"false");
	};

	const fixPay = (e) => {
		//console.log(e.target.value);

	};
	const handleMaritalStatus = (e) => {
		console.log(e.target.value);
		setMaritalStatus(e.target.value);
		setValue("maritalStatus",e.target.value);
	};
	const handleNokRelation = (e) => {
		console.log(e.target.value);
		setNokRel(e.target.value);
		setValue("nokRelation",e.target.value);
	};
	const handleCcDak = (e) => {
		setCcDakId(e.target.value);
		console.log(ccDakId);
	};
	const returnToList =() => {

			history.push("/commissionFormas");

	}

	return (
		    <div className="min-h-full bg-gray-100 text-gray-900">
<div className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">


				<form onSubmit={handleSubmit(onSubmit, onError)}>
					<h1 >{id === 'new' ? 'Add' : 'Edit'} Commission Forma </h1>
					<div className="text-red-500">{serverErrors}</div>
					<Tabs
						id="CommisionFormaEdit"
						activeKey={key}
						onSelect={(k) => setKey(k)}
						className="mb-3">

						<Tab eventKey="page1" title="GoI Info" className="h-120">

							<div className="grid grid-cols-2 gap-0">

								<div>
									<label>Commission Control Dak</label>
									<input type="text" name="ccDak" value={ccDakId} readonly
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.lpcCredit?.message}</div>
								</div>


								<div>
									<label>GoI Letter No</label>
									<input type="text" name="goiLetterNo" value={goiLetterNo} readonly
										className="form-control py-0" />

								</div>

								<div>
									<label>GoI Letter Date</label>
									<input type="date" name="goiLetterDate" value={goiLetterDate} readonly
										className="form-control py-0" />

								</div>

								<div>
									<label>Commission Date</label>
									<input type="date" name="commDate" value={commDate} readonly
										className="form-control py-0" />

								</div>
								<div>
									<label>Commission Type</label>
									<input type="text" name="commissionType" value={commType} readonly
										className="form-control py-0" />

								</div>
								<div>
									<label>Total Officers</label>
									<input type="text" name="totalOfficers" value={total} readonly
										className="form-control py-0" />

								</div>

<div>
									<label>Corps</label>
									<input type="text" name="corps" value={corpsName} readonly
										className="form-control py-0" />

								</div>	
								<div>
									<label>Unit</label>
									<input type="text" name="unit" value={unit} readonly
										className="form-control py-0" />

								</div>
															<div>
									<label>Officer Name</label>
									<input type="text" name="officerName" value={officerName} readonly
										className="form-control py-0" />

								</div>
								<div>
									<label>Ic No</label>
									<input type="text" name="icNo" value={icNo} readonly
										className="form-control py-0" />

								</div>
								<div>
									<label>Rank</label>
									<input type="text" name="rankName" value={rankName} readonly
										className="form-control py-0" />

								</div>
								<div>
									<label>DoB</label>
									<input type="date" name="dob" value={dob} readonly
										className="form-control py-0" />

								</div>
								<div>
									<label>Annual Leave From</label>
									<input type="date" name="alFrom" value={alfrom} readonly
										className="form-control py-0" />

								</div>

								<div>
									<label>Annual Leave To</label>
									<input type="date" name="alTo" value={alto} readonly
										className="form-control py-0" />

								</div>
								<div>
									<label>AntePay Date</label>
									<input type="date" name="antepay" value={antepayDate} readonly
										className="form-control py-0" />

								</div>
<div>
									<label>AnteProm Date</label>
									<input type="date" name="anteprom" value={antepromDate} readonly
										className="form-control py-0" />

								</div>

								<div >
									<LiveSearch name="dak" onChange={handleInputChange}   
										parentData={parentData.dak} parentCallback={callback}
										fkEntity={entity.dak} errCallback={errorCallback} />
									<div className="text-red-500 ">{errors.dak?.message}</div>
								</div>
								<div>
									<label>Forma Letter No</label>
									<input type="text" name="formaLetterNo" {...register("formaLetterNo")} readOnly={readOnly}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.formaLetterNo?.message}</div>
								</div>
								<div>
									<label>Forma Letter Date</label>
									<input type="date" name="formaLetterDate" {...register("formaLetterDate")} readOnly={readOnly}
										className="form-control py-0" />
									<div className="text-red-500">{errors.formaLetterDate?.message}</div>
								</div>


								<div>
									<label>Date Of Reporting</label>
									<input type="date" name="dateOfReporting" {...register("dateOfReporting")} readOnly={readOnly}
										className="form-control py-0" />
									<div className="text-red-500">{errors.dateOfReporting?.message}</div>
								</div>

								{rempl &&  <>

									<div>
									<label>From Date(Remploy or Rev)</label>
									<input type="date" name="fromDate" value={fromDate} readOnly={readOnly}
										className="form-control py-0" />
									<div className="text-red-500">{errors.dateOfReporting?.message}</div>
								</div>
								</>
								}



								<div>
									<label>Remarks</label>
									<textarea type="text" name="remarks" {...register("remarks")} readOnly={readOnly}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.remarks?.message}</div>
								</div>

								<div>
									<label>Rollback Remarks</label>
									<textarea type="text" name="rollbackRemarks" {...register("rollbackRemarks")}   readOnly={!readOnly}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.rollbackRemarks?.message}</div>
								</div>



							</div>
						</Tab>
						<Tab eventKey="page2" title="Pay" className="h-120">
