import { useState } from 'react'
import { Menu, MenuItem, MenuButton, SubMenu } from "@szhsin/react-menu";
import "@szhsin/react-menu/dist/index.css";
import "@szhsin/react-menu/dist/transitions/slide.css";
import {Nav} from 'react-bootstrap'

function BootMenu() {

const [text, setText] = useState('');

const handleMenuClick = e => {
    setText(t => t + `[Menu] ${e.value} clicked\n\n`);
    console.log(e.value);
    console.log(e.target);
    alert('hi');
};

const handleFileClick = e => {
    setText(t => t + `[MenuItem] ${e.value} clicked\n`);
    console.log(e.value);
    console.log(e.target); 
    alert('hi');   
};

const handleSaveClick = e => {
    setText(t => t + `[MenuItem] ${e.value} clicked\n\n`);
    e.stopPropagation = true;
};


  return (
    <Menu menuButton={<MenuButton>Dummy Submenu</MenuButton>} onItemClick={handleMenuClick}>
      <MenuItem onClick={handleFileClick}><Nav.Link href="/usrs">Users</Nav.Link></MenuItem>
      <SubMenu label="Open">
        <MenuItem value="index" onClick={handleFileClick}>index.html</MenuItem>
        <MenuItem>example.js</MenuItem>
        <SubMenu label="Styles">
          <MenuItem>about.css</MenuItem>
          <MenuItem>home.css</MenuItem>
          <MenuItem>index.css</MenuItem>
        </SubMenu>
      </SubMenu>
      <MenuItem>Save</MenuItem>

    </Menu>

  );
}
export default BootMenu;
