/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By : 
 */

import { useState, useEffect } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

//import DatePicker  from 'react-datepicker';
//import "react-datepicker/dist/react-datepicker.css";
//import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'

const schema = yup.object({
      commissionControlDak: yup.object().required('Required'),
      commissionControl: yup.object().required('Required'),
      commissionType: yup.object().required('Required'),
      corps: yup.object().required('Required'),
      reemployed: yup.boolean().required('Required'),
      annualLeaveTo: yup.string().required('Required'),
      antedatePay: yup.string().required('Required'),
      antedatePromotion: yup.string().required('Required'),
      tgcDateOfPermanentCommission: yup.string().required('Required'),
      auditorDate: yup.string().required('Required'),
      aaoDate: yup.string().required('Required'),
      aoDate: yup.string().required('Required'),
      approved: yup.boolean().required('Required'),
      rank: yup.object().required('Required'),
      dateOfBirth: yup.string().required('Required'),
      dateOfCommission: yup.string().required('Required'),
      annualLeaveFrom: yup.string().required('Required'),
      pcSsc: yup.string().required('Required'),
      officerName: yup.string().required('Required'),
      monthEnding: yup.string().required('Required'),
      icNo: yup.string().required('Required'),
      icCheckDigit: yup.string().required('Required'),
      cdaoNo: yup.string().required('Required'),
      cdaoCheckDigit: yup.string().required('Required'),
      tgcOthers: yup.string().required('Required'),
      recordStatus: yup.string().required('Required'),
      gender: yup.string().required('Required'),
      reason: yup.string().required('Required'),
      remarks: yup.string().required('Required'),
});



const CommissionGoiEdit = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();

	let history = useHistory();
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});
	const [key, setKey] = useState('page1');
	const [dakId,setDakId]=useState(0);
	const [ccData,setCcData]=useState([]);



	useEffect(() => {
		let isCancelled = false;
		console.log(id);
		if (id !== 'new') {
			async function fetchData() {
				let record = '';
				await axios.get('/commissionGois/' + id)
					.then((response) => {
						record = response.data;
						const fields = [
						'id'
, 'commissionControlDak', 'commissionControl', 'commissionType', 'corps', 'reemployed', 'annualLeaveTo', 'antedatePay', 'antedatePromotion', 'tgcDateOfPermanentCommission', 'auditorDate', 'aaoDate', 'aoDate', 'approved', 'rank', 'dateOfBirth', 'dateOfCommission', 'annualLeaveFrom', 'pcSsc', 'officerName', 'monthEnding', 'icNo', 'icCheckDigit', 'cdaoNo', 'cdaoCheckDigit', 'tgcOthers', 'recordStatus', 'gender', 'reason', 'remarks'				
					];
					fields.forEach(field => setValue(field, record[field]));
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if(error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					});


				if (!isCancelled) {
					setEntity(record);
					setState(prev => ({ ...prev, state: record }));
				}
			}

			fetchData();
			return () => {
				isCancelled = true;
			};
		}

	}, [id, setValue]);


	useEffect(() => {
		let fetching = false;
		let unmounted = false;
		console.log(dakId);
		async function fetchCommissionData() {

			if (!fetching && dakId>0)
				console.log(dakId);
				await axios.get(`/daks/${dakId}/commissionControls`)
					.then((response) => {
						console.log("response>>" + response.data);
						for(var k in response.data)
						console.log(k);
						setCcData(response.data);
						setValue('commissionControl',response.data);
						if (!unmounted) {

						}
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		fetchCommissionData();

		return () => { fetching = true; unmounted = true; }
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [dakId]);


	const onSubmit = (data, event) => {
		event.preventDefault();
		console.log(data);
		if (data.id) {
			axios.put("/commissionGois/" + data.id, data)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		} else {
			axios.post("/commissionGois", data)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		}

		history.push("/commissionGois");
	}

	const onError = (errors, e) => console.log(errors, e);

	//All foreign keys. 
	// XXXXXX Add search fields and adjust the values as necessary
	const parentData = {

		dak: {
			title: "Commission Control Dak",
			url: "daks/commissiondaktypes",
			searchList : ['dakidNo'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "dak",
		},
		commissionControl: {
			title: "Commission Control",
			url : "commissionControls",
			searchList : ['id'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "commissionControl",
		},
		commissionType: {
			title: "Commission Type",
			url : "commissionTypes",
			searchList : ['typeName'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "commissionType",
		},
		corps: {
			title: "Corps",
			url : "corpss",
			searchList : ['unitCode','unitName'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "corps",
