/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By : 
 */

import { useState, useEffect } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'


const schema = yup.object({

	//aaoDate: yup.string().required('Required'),
	//aoRemarks: yup.string().required('Required'),
	//aoDate: yup.string().required('Required'),

});


const DakEdit = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();
	//console.log(id);

	let history = useHistory();
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});
	const [referenceDate, setReferenceDate] = useState(new Date())

	const [key, setKey] = useState('Page1');




	useEffect(() => {
		let isCancelled = false;
		console.log(id);
		if (id !== 'new') {
			async function fetchData() {
				let record = '';
				await axios.get('/daks/' + id)
					.then((response) => {
						record = response.data;
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					});

				const fields = [
					'id', 'dakidNo', 'dakYear', 'dakType', 'unit', 'section', 'employee', 'cdaoNo',
					'checkDigit', 'amount', 'referenceNo', 'referenceDate', 'subject',
					'billNo', 'billDate', 'disposalDate', 'disposalDate', 'disposalSummary', 'taskUsr',
					'reason', 'aaoDate', 'auditorDate', 'aoDate', 'recordStatus', 'approved'
				];
				fields.forEach(field => setValue(field, record[field]));
				if (!isCancelled) {
					setEntity(record);
					setState(prev => ({ ...prev, state: record }));
				}
			}

			fetchData();
			return () => {
				isCancelled = true;
			};
		}

	}, [id, setValue]);


	const onSubmit = (data, event) => {
		event.preventDefault();
		console.log(data);
		if (data.id) {
			axios.put("/grievances/" + data.id, data)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		} else {
			axios.post("/grievances", data)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					if (error.response)
						setServerErrors(error.response.data);
					else
						setServerErrors(error);
				});
		}

		history.push("/grievances");
	}

	const onError = (errors, e) => console.log(errors, e);

	//All foreign keys. 
	// XXXXXX Add search fields and adjust the values as necessary
	const parentData = {

		dak: {
			title: "Dak",
			url: "daks",
			searchList: ['dakidNo'], //XXXXXXXXX Add search fields 
			fkEntity: "dak",
			preload: false, //XXXXXX Set this to true for small tables like designations
		},
		unit: {
			title: "Unit",
			url: "units",
			searchList: ['unitCode', 'unitName'], //XXXXXXXXX Add search fields
			fkEntity: "unit",
			preload: false, //XXXXXX Set this to true for small tables like designations
		},
		section: {
			title: "Section",
			url: "sections",
			searchList: ['sectionName'], //XXXXXXXXX Add search fields
			fkEntity: "section",
			preload: false, //XXXXXX Set this to true for small tables like designations
		},
		employee: {
			title: "Employee",
			url: "employees",
			searchList: ['cdaoNo', 'officerName'], //XXXXXXXXX Add search fields
			fkEntity: "employee",
			preload: false, //XXXXXX Set this to true for small tables like designations
		},
	}

	//Callback for child components (Foreign Keys)
	const callback = (childData) => {
		//console.log("Parent Callback");
		setEntity(prev => ({ ...prev, [childData.fk]: childData.entity }));
		setValue(childData.fk, childData.entity);
		//console.log(errors);
		clearErrors(childData.fk);
	};

	const errorCallback = (err) => {
		//console.log(err);
		setServerErrors(err);
	}

	const handleInputChange = (e) => {
		//console.log(e.target.value);
	};

	return (
		<div className="max-w-xl mx-auto ">
			<div className="w-full w-3/4  mx-auto " >

				<form onSubmit={handleSubmit(onSubmit, onError)}>
					<h1 >{id === 'new' ? 'Add' : 'Edit'} Grievance </h1>
					<div className="text-red-500">{serverErrors}</div>

					<Tabs
						id="GrievanceEdit"
						activeKey={key}
						onSelect={(k) => setKey(k)}
						className="mb-3"
					>
						<Tab eventKey="page1" title="Page 1" className="h-120">
							<div className="grid grid-cols-2 gap-0 ">

								<div >
									<LiveSearch name="employee" onChange={handleInputChange}
										parentData={parentData.employee} parentCallback={callback}
										fkEntity={entity.employee} errCallback={errorCallback} />
									<div className="text-red-500 ">{errors.employee?.message}</div>
								</div>


								<div >
									<LiveSearch name="unit" onChange={handleInputChange}
										parentData={parentData.unit} parentCallback={callback}
										fkEntity={entity.unit} errCallback={errorCallback} />
									<div className="text-red-500 ">{errors.unit?.message}</div>
								</div>


							{/*
								<div>
									<label>* CDAO No</label>
									<input type="text" name="cdaoNo" {...register("cdaoNo")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.cdaoNo?.message}</div>
								</div>


								<div>
									<label>Check Digit</label>
									<input type="text" name="checkDigit" {...register("checkDigit")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.checkDigit?.message}</div>
								</div>
								*/}
								<div>
									<label>Grievance Source</label>
									<input type="text" name="grievanceSource" {...register("grievanceSource")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.grievanceSource?.message}</div>
								</div>


								<div>
									<label>Month Ending</label>
									<input type="text" name="monthEnding" {...register("monthEnding")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.monthEnding?.message}</div>
								</div>

								<div>
									<label>Reference No</label>
									<input type="text" name="referenceNo" {...register("referenceNo")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.referenceNo?.message}</div>
								</div>


								<div>
									<label>Reference Date</label>
									<DatePicker type="text" name="referenceDate" {...register("referenceDate")}
										selected={referenceDate} onChange={(date) => setReferenceDate(date)}
										dateFormat="dd/MM/yyyy"
										className=" -ml-2 pl-2 pr-2" />
								</div>

								<div>
									<label>Email Id</label>
									<input type="text" name="emailId" {...register("emailId")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.emailId?.message}</div>
								</div>
								<div>
									<label>Grievance Date</label>
									<DatePicker type="text" name="grievanceDate" {...register("grievanceDate")}
										selected={grievanceDate} onChange={(date) => setGrievanceDate(date)}
										dateFormat="dd/MM/yyyy"
										className=" -ml-2 pl-2 pr-2"
									/>
								</div>
								<div>
									<label>Subject</label>
									<textarea type="text" name="subject" {...register("subject")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.subject?.message}</div>
								</div>

								<div className="col-span-1 ...">
									<label>Grievance Brief</label>
									<textarea type="text" name="grievanceBrief" {...register("grievanceBrief")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.grievanceBrief?.message}</div>
								</div>



								<div >
									<LiveSearch name="dak" onChange={handleInputChange}
										parentData={parentData.dak} parentCallback={callback}
										fkEntity={entity.dak} errCallback={errorCallback} />
									<div className="text-red-500 ">{errors.dak?.message}</div>
								</div>


								<div >
									<LiveSearch name="section" onChange={handleInputChange}
										parentData={parentData.section} parentCallback={callback}
										fkEntity={entity.section} errCallback={errorCallback} />
									<div className="text-red-500 ">{errors.section?.message}</div>
								</div>



								<div>
									<label>Disposal No</label>
									<input type="text" name="disposalNo" {...register("disposalNo")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.disposalNo?.message}</div>
								</div>
								<div>
									<label>Disposal Date</label>
									<DatePicker type="text" name="disposalDate" {...register("disposalDate")}
										selected={disposalDate} onChange={(date) => { setDisposalDate(date); }}
										dateFormat="dd/MM/yyyy"
										className=" -ml-2 pl-2 pr-2" />
								</div>

								<div>
									<label>Disposal Remarks</label>
									<textarea type="text" name="disposalRemarks" {...register("disposalRemarks")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.disposalRemarks?.message}</div>
								</div>								
							</div>

						</Tab>


						<Tab eventKey="page2" title="Page 2" className="h-120">
							<div className="grid grid-cols-2 gap-0">

								<div>
									<label>Web Id</label>
									<input type="text" name="webId" {...register("webId")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.webId?.message}</div>
								</div>




								<div>
									<label>Auditor Remarks</label>
									<textarea type="text" name="auditorRemarks" {...register("auditorRemarks")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.auditorRemarks?.message}</div>
								</div>
								<div>
									<label>Aao Remarks</label>
									<textarea type="text" name="aaoRemarks" {...register("aaoRemarks")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.aaoRemarks?.message}</div>
								</div>
								<div>
									<label>Ao Remarks</label>
									<textarea type="text" name="aoRemarks" {...register("aoRemarks")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.aoRemarks?.message}</div>
								</div>


								{/*
						<div>
							<label>Record Status</label>
							<input type="text" name="recordStatus" {...register("recordStatus")}
								className="form-control py-0"
							/>
							<div className="text-red-500">{errors.recordStatus?.message}</div>
						</div>
						*/}

							</div>

						</Tab>

						<Tab eventKey="contact" title="Help" >
							<h1>Help</h1>
							<ul className="list-disc">
							<li>Fill required fields in both page 1 and page 2. Save only once.</li>
							<li>Dak field gets auto-filled on saving.</li>

							</ul>
						</Tab>
					</Tabs>

					<div className="px-4 ...">
						<button type="submit" >Save</button>
					</div>

				</form>
			</div>
		</div>
	);
};

export default withRouter(GrievanceEdit);
