/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By : 
 */

import { useState, useEffect } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

//import DatePicker  from 'react-datepicker';
//import "react-datepicker/dist/react-datepicker.css";
//import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'

const schema = yup.object({
      //dak: yup.object().required('Required'),

      bank: yup.object().required('Required'),

      bankAccountNo: yup.string().required('Required'),

      //ifsc: yup.string().required('Required'),
});


const BankAccountEdit = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();

	let history = useHistory();
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});
	const [key, setKey] = useState('page1');


	useEffect(() => {
		let isCancelled = false;
		console.log(id);
		if (id !== 'new') {
			async function fetchData() {
				let record = '';
				await axios.get('/bankAccounts/' + id)
					.then((response) => {
						record = response.data;
						const fields = [
						'id'
, 'dak', 'auditorDate', 'aaoDate', 'aoDate', 'approved', 'bank', 'employee', 'unit', 'imprest', 'familyDetail', 'batchNo', 'beneficiaryCategory', 'bankAccountNo', 'remarks', 'reason', 'accountType', 'recordStatus', 'ifsc'				
					];
					fields.forEach(field => setValue(field, record[field]));
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if(error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					});


				if (!isCancelled) {
					setEntity(record);
					setState(prev => ({ ...prev, state: record }));
				}
			}

			fetchData();
			return () => {
				isCancelled = true;
			};
		}

	}, [id, setValue]);


	const onSubmit = (data, event) => {
		event.preventDefault();
		console.log(data);
		if (data.id) {
			axios.put("/bankAccounts/" + data.id, data)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		} else {
			axios.post("/bankAccounts", data)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		}

		history.push("/bankAccounts");
	}

	const onError = (errors, e) => console.log(errors, e);

	//All foreign keys. 
	// XXXXXX Add search fields and adjust the values as necessary
	const parentData = {

		dak: {
			title: "Dak",
			url : "daks",
			searchList : ['dakidNo'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "dak",
		},
		bank: {
			title: "Bank",
			url : "banks",
			searchList : ['ifsc','bankName'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "bank",
		},
		employee: {
			title: "Employee",
			url : "employees",
			searchList : ['cdaoNo','officerName'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "employee",
		},
		unit: {
			title: "Unit",
			url : "units",
			searchList : ['id'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "unit",
		},
		imprest: {
			title: "Imprest",
			url : "imprests",
			searchList : ['id'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "imprest",
		},
		familyDetail: {
			title: "Family Detail",
			url : "familyDetails",
			searchList : ['id'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "familyDetail",
		},
	}

	//Callback for child components (Foreign Keys)
	const callback = (childData) => {
		//console.log("Parent Callback");
		setEntity(prev => ({ ...prev, [childData.fk]: childData.entity }));
		setValue(childData.fk, childData.entity);
		//console.log(errors);
		clearErrors(childData.fk);
	};

	const errorCallback = (err) => {
		//console.log(err);
		setServerErrors(err);
	}

	const handleInputChange = (e) => {
		//console.log(e.target.value);
	};

	return (
		<div className="max-w-xl mx-auto ">
			<div className="w-full w-3/4  mx-auto " >

				<form onSubmit={handleSubmit(onSubmit, onError)}>
					<h1 >{id === 'new' ? 'Add' : 'Edit'} Bank Account </h1>
					<div className="text-red-500">{serverErrors}</div>

							<div className="grid grid-cols-2 gap-0">
								<div >
									<LiveSearch name="dak" onChange={handleInputChange}
										parentData={parentData.dak} parentCallback={callback} 
										fkEntity={entity.dak} errCallback={errorCallback} />
									<div className="text-red-500 ">{errors.dak?.message}</div>
								</div>						




								<div >
									<LiveSearch name="bank" onChange={handleInputChange}
										parentData={parentData.bank} parentCallback={callback} 
										fkEntity={entity.bank} errCallback={errorCallback} />
									<div className="text-red-500 ">{errors.bank?.message}</div>
								</div>						


								<div >
									<LiveSearch name="employee" onChange={handleInputChange}
										parentData={parentData.employee} parentCallback={callback} 
										fkEntity={entity.employee} errCallback={errorCallback} />
									<div className="text-red-500 ">{errors.employee?.message}</div>
								</div>						



								<div>
									<label>Bank Account No</label>
									<input type="text" name="bankAccountNo" {...register("bankAccountNo")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.bankAccountNo?.message}</div>
								</div>





								<div>
									<label>Account Type</label>
									<input type="text" name="accountType" {...register("accountType")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.accountType?.message}</div>
								</div>




							</div>

					<div className="px-4">
						<button type="submit" >Save</button>
					</div>

				</form>
			</div>
		</div>
	);
};

export default withRouter(BankAccountEdit);
