/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 18 April 2022
 *
 * Modified By : 
 */

import { useState, useEffect, useMemo } from 'react';
import Table, { SelectColumnFilter } from '../utils/Table'  // 
import axios from "axios";
import { format } from 'date-fns'
import { withRouter, Link } from "react-router-dom";

function AuditCageList() {

	const [data, setData] = useState([]);
	const [update, setUpdate] = useState(false);
	const [serverErrors, setServerErrors] = useState([]);
	const [search, setSearch] = useState("");
	const [inputText, setInputText] = useState('');
	const [desc,setDesc]=useState('');
	const [descSearch,setDescSearch]=useState('');
	const [loading,setLoading]=useState(false);

	useEffect(() => {

		let fetching = false;
		async function fetchData() {
			if(!fetching)
			await axios.get('/auditCages?search='+search+'&desc='+descSearch)
				.then((response) => {
					console.log(response.data);
					setData(response.data);
					//setLoading(false);
				})
				.catch((error) => {
					//console.log(error);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					if (error.response)
						setServerErrors(error.response.data.error);
					else
						setServerErrors(error.Error);
				})
		}
		fetchData();
		return () => { fetching = true; }

	}, [update, search,descSearch]);


	async function remove(id) {
		await axios.delete(`/auditCages/${id}`)
			.then(() => {
				//console.log(data);
				let updatedRecords = [...data].filter((i) => i.id !== id);
				console.log(updatedRecords);
				setData(updatedRecords);
				setUpdate(!update);
			})
			.catch((error) => {
				console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}


	const columns = useMemo(() => [

		{
			Header: "AuditCage Id",
			accessor: 'id',// Change this
		},
		{
			Header: "Employee",
			accessor: 'ano',
			Cell: ({ row }) => (
				<div>
				<div>
				{row.original.ano}<br/>



				 </div>

				</div>

				)
		},

		{
			Header: "Do2",
			accessor: 'description',// Change this
		},
		{
			Header: "Qe",
			accessor: 'qe',
		},
		{
			Header: "From Date",
			accessor: 'fromdate',
			Cell: ({ row }) => (
				<div>
				<div>
				<label>{row.original.fromdate!==null && format(new Date(row.original.fromdate.toString()),'dd/MM/yyyy')}</label>
				 </div>

				</div>

				)


		},
		{
			Header: "To Date",
			accessor: 'todate',
			Cell: ({ row }) => (
				<div>
				<div>
				<label>{row.original.todate!==null && format(new Date(row.original.todate.toString()),'dd/MM/yyyy')}</label>
				 </div>

				</div>

				)

		},

		{
			Header: "Basic Pay",
			accessor: 'basicpay',
		},
		{
			Header: "Pay Level",
			accessor: 'paylevel',
		},
		{
			Header: "Dp",
			accessor: 'dp',
		},

		{
			Header: "Grade Pay",
			accessor: 'gradepay',
		},





		{
			Header: "Ms Pay",
			accessor: 'mspay',
		},

		{
			Header: "X Group Pay",
			accessor: 'xgrouppay',
		},

		{
			Header: "Class Pay",
			accessor: 'classpay',
		},

		{
			Header: "Da",
			accessor: 'da',
		},

		{
			Header: "Transport",
			accessor: 'transport',
		},

		{
			Header: "Pmha",
			accessor: 'pmha',
		},

		{
			Header: "Lra",
			accessor: 'lra',
		},
		{
			Header: "Pay Stopped",
			accessor: 'absentflag',
				Cell: ({ row }) => (
				<div>
				<div>
				{(row.original.absentflag && row.original.absentflag===true)? 'Y':'N'}
				 </div>

				</div>

				)
		},
		{
			Header: "Deputation Flag",
			accessor: 'deputationflag',

				Cell: ({ row }) => (
				<div>
				<div>
				{(row.original.deputationflag && row.original.deputationflag===true)? 'Y':'N'}
				 </div>

				</div>

				)
		},

		{
			Header: "Old Basic Pay",
			accessor: 'oldbasicpay',
		},

		{
			Header: "Old Class Pay",
			accessor: 'oldclasspay',
		},







		{
			Header: "Record Status",
			accessor: 'recordstatus',
		},






		{
			Header: "Effective",
			accessor: 'effective',

				Cell: ({ row }) => (
				<div>
				<div>
				{(row.original.effective && row.original.effective===true)? 'Y':'N'}
				 </div>

				</div>

				)
		},


		{
			Header: "Old Grade Pay",
			accessor: 'oldgradepay',
		},




		/*
		{
			Header: "Login Name",
			accessor: 'loginName',
			Filter: SelectColumnFilter,  // new
			filter: 'includes',
		},
		*/
	], [data])

	const handleSubmit = (event) => {
		event.preventDefault();
		console.log(inputText);
		setSearch(inputText);
		setDescSearch(desc);
	}

	const handleKeyPress = (event) => {
		// look for the `Enter` keyCode
		if (event.keyCode === 13 || event.which === 13) {
			handleSubmit(event)
		}
	}

	return (
		<div className="min-h-screen bg-gray-100 text-gray-900">
		<main className="max-w-8xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
				<div className="mt-2 ml-4">
					<h1 className="text-xl font-semibold">Audit Cages</h1>
					<div className="flexContainer">
					<div>
						<input type="text" name="search" placeholder="army no" 
						onChange={e => setInputText(e.target.value)}
							onKeyPress={handleKeyPress}
							className="pl-2 -ml-2 inputField flex-initial" />
							</div>

						<div>
						<input type="text" name="descSearch" placeholder="desc" 
						onChange={e => setDesc(e.target.value)}
							onKeyPress={handleKeyPress}
							className="pl-2 -ml-2 inputField flex-initial" />
							</div>	

						<button type="submit" onClick={handleSubmit} className="w-16 m-0 p-0">Search</button>

					</div>					
				</div>
				<div className="-mt-2 max-h-1 py-0">
				 {loading && <>

						 <div className="flex justify-center items-center">
  <div className="spinner-border animate-spin inline-block w-8 h-8 border-4 rounded-full" role="status">
    <span className="visually-hidden">Loading...</span>
  </div>
</div>
						</>	
						}
					<Table columns={columns} data={data} className="table-auto" />
				</div>
			</main>
		</div>
	);
}

export default withRouter(AuditCageList);
