import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Home from "./components/Home";
import Footer from "./components/Footer";

import { useState, useEffect, } from 'react';
import axios from "axios";
import Users from './gql/Users';
import AppNavBar from "./menu/AppNavBar";
import { HookFormYup } from "./examples/forms/HookFormYup";
import KitchenSink from './examples/KitchenSink';

import PayElementList from './components/PayElementList';
import CbillTadaLtcList from './components/CbillTadaLtcList';
import CbillTadaLtcEdit from './components/CbillTadaLtcEdit';
import EmployeeList from './components/EmployeeList';
import EmployeeEdit from './components/EmployeeEdit';
import PayElementEdit from './components/PayElementEdit';
import GrievanceList from './components/GrievanceList';
import GrievanceEdit from './components/GrievanceEdit';
import CommissionControlList from './components/CommissionControlList';
import CommissionControlEdit from './components/CommissionControlEdit';


import PrintPmCs from './components/PrintPmCs';
import SectionList from './components/SectionList';
import UsrSectionList from './components/UsrSectionList';
import SectionEdit from './components/SectionEdit';
import UsrSectionEdit from './components/UsrSectionEdit';
import RolList from './components/RolList';
import RolEdit from './components/RolEdit';
import UnitList from './components/UnitList';
import UnitEdit from './components/UnitEdit';
import CorpsList from './components/CorpsList';
import CorpsEdit from './components/CorpsEdit';
import UsrList from './components/UsrList';
import UsrEdit from './components/UsrEdit';
import PasswordEdit from './components/PasswordEdit';
import UsrRolList from './components/UsrRolList';
import AllowanceRateList from './components/AllowanceRateList';
import DakEdit from './components/DakEdit';
import DakList from './components/DakList';
import DakListEdit from './components/DakListEdit';
import DakTypeEdit from './components/DakTypeEdit';
import DakTypeList from './components/DakTypeList';
import PayCodeEdit from './components/PayCodeEdit';
import PayCodeList from './components/PayCodeList';
import RankEdit from './components/RankEdit';
import RankList from './components/RankList';
import BankEdit from './components/BankEdit';
import BankList from './components/BankList';
import MenuList from './components/MenuList';
import MenuEdit from './components/MenuEdit';
import BankAccountEdit from './components/BankAccountEdit';
import BankAccountList from './components/BankAccountList';
import BillTypeEdit from './components/BillTypeEdit';
import BillTypeList from './components/BillTypeList';
import CommissionGoiEdit from './components/CommissionGoiEdit';
import CommissionGoiList from './components/CommissionGoiList';
import CommissionFormaEdit from './components/CommissionFormaEdit';
import CommissionFormaList from './components/CommissionFormaList';
import DpSheetList from './components/DpSheetList';
import CmpGeneration from './components/CmpGeneration';
import CmpSummary from './components/CmpSummary';
import FileList from './components/FileList';
import TaskList from './components/TaskList';
import TaskEdit from './components/TaskEdit';
import FamilyDetailList from './components/FamilyDetailList';
import FamilyDetailEdit from './components/FamilyDetailEdit';



const App = () => {

	const [serverErrors, setServerErrors] = useState([]);
	const [menu, setMenu] = useState();

	useEffect(() => {
		console.log('From App');
		let fetching = false;
		async function fetchData() {
			if (!fetching)
				await axios.get('/menus?filter=usr')
					.then((response) => {
						//console.log(response.data);
						//console.log(typeof response.data);
						if (typeof response.data!=='string') {//json menu data is an object
							//setData(response.data);
							setMenu(response.data);
							//console.log(menu);
						}
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		fetchData();
		//console.log(menu);
		return () => { fetching = true; }

	}, []);

	return (

		<Router basename={process.env.REACT_APP_ROUTER_BASE || ''}>
			<AppNavBar menu={menu} />
			<Switch>
				<Route path="/" exact={true} component={Home} />				
				<Route path="/kitchen" component = {KitchenSink} />
				<Route path="/files" component = {FileList} />
				<Route path="/payments/paymentSummary" exact={true}  component = {CmpSummary} />
				<Route path="/schedule3s/cmpGen" exact={true}  component = {CmpGeneration} />
				<Route path="/chequeSlips/dpsList" exact={true}  component = {DpSheetList} />

				<Route path="/pms" exact={true} component = {PrintPmCs} />					
<Route path="/payElements" exact = {true} component = {PayElementList} />
				<Route path="/cbilltadaltcs" exact = {true} component = {CbillTadaLtcList} />
				<Route path="/cbilltadaltcs/:id"  component = {CbillTadaLtcEdit} />					
				<Route path="/employees" exact = {true} component = {EmployeeList} />
				<Route path="/employees/:id"  component = {EmployeeEdit} />	
				<Route path="/payElements/:id"  component = {PayElementEdit} />					
				<Route path="/grievances" exact = {true} component = {GrievanceList} />
				<Route path="/grievances/:id"  component = {GrievanceEdit} />
				<Route path="/rols" exact = {true} component = {RolList} />
