/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By : 
 */

import { useState, useEffect, useMemo } from 'react';
import Table, { SelectColumnFilter } from '../utils/Table'  // 
import axios from "axios";
import { withRouter, Link } from "react-router-dom";
import TablePage from '../utils/TablePage';


function RefundTransList() {

	const [data, setData] = useState([]);
	const [update, setUpdate] = useState(false);
	const [serverErrors, setServerErrors] = useState([]);
	const [search, setSearch] = useState("");
	const [inputText, setInputText] = useState('');
	 const [page,setPage]=useState(0);
	  const [pageSize, setPageSize] = useState(0);
	  const[usrLevel,setUsrLevel]=useState(0);
	  const[mesg,setMesg]=useState('');
	  const[approved,setApproved]=useState(true);
	  const[rentBillType,setRentBillType]=useState('');
	  const [disabled,setDisabled]=useState(false);
	  const [validRec,setValidRec]=useState('');
	  const [invalidRec,setInvalidRec]=useState('');
	   const[iors,setIors]=useState('');

	useEffect(() => {
		let fetching = false;
		async function fetchData() {
			if(!fetching)
			console.log(rentBillType);
			await axios.get('/refundTrans?search='+search)

				.then((response) => {
					setData(response.data);

				})
				.catch((error) => {
					//console.log(error);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					if (error.response)
						setServerErrors(error.response.data.error);
					else
						setServerErrors(error.Error);
				})
		}
		fetchData();
		return () => { fetching = true; }

	}, [update, search]);


	useEffect(() => {
		let fetching = false;
		async function fetchUsrData() {
			if(!fetching)
			console.log(">>>>Usr Level is -----:" + usrLevel);
			await axios.get(`/refundTrans/userDesg`)
				.then((response) => {
					console.log(">>>>Usr Level is----:" + response.data);
					setUsrLevel(response.data);

				})
				.catch((error) => {
					//console.log(error);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					if (error.response)
						setServerErrors(error.response.data.error);
					else
						setServerErrors(error.Error);
				})
		}
		fetchUsrData();
		return () => { fetching = true; }

	}, []);
	async function submit(id) {
		console.log("-------------sub--ref--id---:" + id);
		await axios.put(`/refundTrans/submitRefund/${id}`)
			.then((response) => {
				console.log(data);
				setMesg(response.data);
				let updatedRecords = [...data].filter((i) => i.id !== id);
				//console.log(updatedRecords);
				setData(updatedRecords);

			})
			.catch((error) => {
				console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}
	async function approve(id) {
		console.log("---------------id----vac---------:" + id);
		await axios.put(`/refundTrans/approveRefund/${id}`)
			.then((response) => {
				setMesg(response.data);
				let updatedRecords = [...data].filter((i) => i.id !== id);
				console.log(updatedRecords);
				setData(updatedRecords);
				setUpdate(!update);
			})
			.catch((error) => {
				console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}

	async function rollBack(id) {
		await axios.put(`/refundTrans/rollBack/${id}`)
			.then((response) => {
				console.log(data);
				setMesg(response.data);
				let updatedRecords = [...data].filter((i) => i.id !== id);
				//console.log(updatedRecords);
				setData(updatedRecords);

			})
			.catch((error) => {
				console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}

	async function submitBulk() {


		let proceed=window.confirm("You are about to process file containing "+data.length+" records.");
		if(!proceed)
			return;

		if(disabled)
			return;

			setDisabled(true);
		await axios.put(`/refundTrans/bulkSubmitRef`, data)
			.then((response) => {
				setDisabled(false);
				setMesg(response.data[0]);
				if(response.data[1]!==null)
				setData(response.data[1]);
				console.log("reponse status--------------"+response.status);


			})
			.catch((error) => {
			//	console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}

	async function approveBulk() {


		let proceed=window.confirm("You are about to approve file containing "+data.length+" records.");
		if(!proceed)
			return;

		if(disabled)
			return;

			setDisabled(true);
		await axios.put(`/refundTrans/bulkApproveRef`, data)
			.then((response) => {
				setValidRec(response.data[1]);
				setInvalidRec(response.data[2]);
				if(response.data[2].startsWith("Selected")){
					setMesg(response.data[3]);
				}else{
					setMesg(response.data[0]);
				}
				console.log("reponse status--------------"+response.status);
			//	setFundList([]);
			let updatedRecords = [...data];
				console.log(updatedRecords);
				setData(updatedRecords);
				setUpdate(!update);

			})
			.catch((error) => {
			//	console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}
	const updateCheckBoxAll = (e) =>{
		  // console.log("..."+e.target.checked+"--"+index);

			  let newData=[...data];
			for(var k in newData){
				newData[k].select=e.target.checked;
			}
			setData(newData);


	} 
	const handleCheckBox = index=>(e) =>{
		//console.log(Table.page)
		   console.log(e.target.checked+"--"+index);



		   console.log(e.target.checked);
			let item = data[index];

		item['select'] = e.target.checked;
		let newData = [...data];
		newData[index] = item;
		setData(newData);



	}
	const columns = useMemo(() => [



		{
			Header: 'Action',
			Cell: ({ row }) => (
				<div>
				<div>
					 {row.original.action!=null && row.original.action.includes("edit") &&
					<Link to={"/refundTrans/" + row.original.id}>
						<button className=" w-16 m-0 p-0 " > Edit </button>
					</Link>
					}

				</div>
				<div>

					{(usrLevel<30 && (row.original.transactionType!=null && row.original.transactionType==='S')) &&
						<Link to={"/refundTrans/reject/" + row.original.id}>
							<button className="w-18 m-0 p-0 bg-yellow-500 hover:bg-yellow-700 " > Reject </button>
						</Link>
					}
				</div>
				<div>
				{(usrLevel===30 && row.original.recordStatus==='V' && row.original.action==='submission') &&
					<button
						className="w-16 m-0 p-0 bg-red-500 hover:bg-red-700 "
						onClick={() => submit(row.original.id)}
					>	Submit 	</button>

				}
				</div>
				<div>
				{(usrLevel===30 && row.original.recordStatus==='R' && row.original.action==='reject') &&
					<button
						className="w-24 m-0 p-0 bg-yellow-500 hover:bg-yellow-700 "
						onClick={() => submit(row.original.id)}
					>	Submit-Rej 	</button>

				}
				</div>
				<div>
				{(usrLevel>30 && row.original.recordStatus==='V' && row.original.action==='approval') &&
					<button
						className="w-16 m-0 p-0 bg-red-500 hover:bg-red-700 "
						onClick={() => approve(row.original.id)}
					>	Approve 	</button>

				}
				</div>
				<div>
				{(usrLevel>30 && row.original.recordStatus==='R' && row.original.action==='reject') &&
					<button
						className="w-26 m-0 p-0 bg-red-500 hover:bg-red-700 "
						onClick={() => approve(row.original.id)}
					>	Approve-Rej 	</button>

				}
				</div>
				<div>

					{(usrLevel>=30 && (row.original.recordStatus==='V' || row.original.recordStatus==='R')) &&
						<button
						className="w-18 m-0 p-0 bg-gray-500 hover:bg-gray-700 "
						onClick={() => rollBack(row.original.id)}
					>	Roll Back 	</button>
					}

				</div>
				</div>
			)
		},


		 {
        Header: "Officer Details",
        accessor: "cdaoNo",

        Cell: ({ row }) => (
          <div>
            <div>
              <label>CdaO No : {row.original.cdaoNo} -- {row.original.checkDigit}</label>
            </div>
            <div>
              <label>Personal No : {row.original.personelNo}</label>
            </div>
            <div>
            {row.original.employee &&
              <label>Officer Name : {row.original.employee.officerName}</label>
              }
            </div>
            <div>
            {row.original.employee &&
              <label>Rank : {row.original.employee.rank.rankName}</label>
              }
            </div>
          </div>
        ),
      },

       {
        Header: "Rent Details",
        accessor: "uabsoCode",

        Cell: ({ row }) => (
          <div>
            <div>
              <label>Uabso Code : {row.original.uabsoCode}</label>
            </div>
            <div>
              <label>Building No : {row.original.buildingNo}</label>
            </div>
            <div>
              <label>Station : {row.original.station}</label>
            </div>
            <div>
            {row.original.employee &&
              <label>Rank : {row.original.employee.rank.rankName}</label>
              }
            </div>
          </div>
        ),
      },



		{
			Header: "Occupation Date",
			accessor: 'occupationDate' ,
			 Cell: ({ row }) => (
			 <div>
              <label>{row.original.occupationDate}</label>
            </div>
            ),
		},

		{
			Header: "Vacation Date",
			accessor: 'vacationDate',
			Cell: ({ row }) => (
			 <div>
              <label>{row.original.vacationDate}</label>
            </div>
            ),
		},

		 {
        Header: "Rent Bill Details",
        accessor: "rentBillNo",

        Cell: ({ row }) => (
          <div>
            <div>
              <label>Reference Bill No : {row.original.referenceBillNo}</label>
            </div>
            <div>
              <label>Reference Bill Date : {row.original.referenceBillDate}</label>
            </div>
            <div>
              <label>Rent Bill No : {row.original.rentBillNo}</label>
            </div>
            <div>
              <label>Rent Bill Date : {row.original.rentBillDate}</label>
            </div>
          </div>
        ),
      },

		 {
        Header: "Licence Fee Details",
        accessor: "lfeeFromDate",

        Cell: ({ row }) => (
          <div>
            <div>
              <label>Lf From Date : {row.original.lfeeFromDate}</label>
            </div>
            <div>
              <label>Lf To Date : {row.original.lfeeToDate}</label>
            </div>
            <div>
              <label>Lf Amount : {row.original.lfeeAmount}</label>
            </div>
          </div>
        ),
      },
		{
        Header: "Furniture Details",
        accessor: "furFromDate",

        Cell: ({ row }) => (
          <div>
            <div>
              <label>Fur From Date : {row.original.furFromDate}</label>
            </div>
            <div>
              <label>Fur To Date : {row.original.furToDate}</label>
            </div>
            <div>
              <label>Fur Amount : {row.original.furAmount}</label>
            </div>
            <div>
              <label>Excess Fur From Date : {row.original.excessFurFromDate}</label>
            </div>
            <div>
              <label>Excess Fur To Date : {row.original.excessFurToDate}</label>
            </div>
            <div>
              <label>Excess Fur Amount : {row.original.excessFurAmount}</label>
            </div>
          </div>
        ),
      },

       {
        Header: "Fan Details",
        accessor: "fanFromDate",

        Cell: ({ row }) => (
          <div>
            <div>
              <label>Fan From Date : {row.original.fanFromDate}</label>
            </div>
            <div>
              <label>Fan To Date : {row.original.fanToDate}</label>
            </div>
            <div>
              <label>Fan Amount : {row.original.fanAmount}</label>
            </div>
          </div>
        ),
      },

       {
        Header: "Fridge Details",
        accessor: "fridgeFromDate",

        Cell: ({ row }) => (
          <div>
            <div>
              <label>Fridge From Date : {row.original.fridgeFromDate}</label>
