/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By : 
 */

import { useState, useEffect, useRef } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'


const schema = yup.object({




});



const DakListEdit = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();
	//console.log(id);

	let history = useHistory();

	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});

	const [dakList,setDakList]=useState([]);

	const [loading, setLoading] = useState(true);


	const [key, setKey] = useState('Page1');




	useEffect(() => {
		let fetching = false;
		let unmounted = false;

		async function fetchData() {

			if (!fetching)
				//console.log(secId);
				await axios.get(`/daks/dakLists`)
					.then((response) => {
						console.log("response>>" + response.data);
						setDakList(response.data);

						if (!unmounted) {

							setLoading(false);
						}
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		fetchData();

		return () => { fetching = true; unmounted = true; }
		// eslint-disable-next-line react-hooks/exhaustive-deps
	},[]);



	const onSubmit = (data, event) => {
		event.preventDefault();
		console.log(data);
		console.log("data id--------------"+data.id);

			axios.put("/daks/dakList", dakList)
				.then((response) => { 
					console.log("reponse status--------------"+response.status+"--"+response.statusText+"----"+"-h--"+response.headers+"--"+response.data);
					if(response.status===201){

						//history.push("/daks/view/"+response.data);
						//history.replace({pathname:'/daks/new/'+response.data});



					}
				})
				.catch((error) => {
					//console.log(error.response.data);
					console.log("response--------"+error.response.status);
					if(error.response.status!==201)
						history.push("/daks");
					//console.log(error.response.headers);
					if (error.response)
						setServerErrors(error.response.data);
					else
						setServerErrors(error);
				});


		//history.push("/daks");
	}



	const onError = (errors, e) => console.log(errors, e);

	//All foreign keys. 
	// XXXXXX Add search fields and adjust the values as necessary


	const errorCallback = (err) => {
		//console.log(err);
		setServerErrors(err);
	}

	const handleCheckBox = (e,index) =>{

			dakList[index].select=e.target.checked;

	}

	const handleInputChange = (e) => {
		console.log(e.target.value);
	//	console.log("handle input change");


	};


	return (
		<div className="max-w-xl mx-auto ">
			<div className="w-full w-3/4  mx-auto " >

				<form onSubmit={handleSubmit(onSubmit, onError)}>
					<h1 >  Dak List</h1>
					<div className="text-red-500">{serverErrors}</div>


					  			 <div className="flex-container">  


        <table className="table table-striped table-bordered">  
            <tr> 
            <th><input type="checkbox" ></input></th> 
                <th>Sl</th>  
                <th>DakId</th>  
                <th>Section</th>  
                 <th>Ref No</th>
                  <th>Ref Data</th>
            </tr>  

            {dakList.map((data, index) => (  
              <tr key={index}> 
              <td><input type="checkbox" onClick={(e)=>handleCheckBox(e,index)} ></input></td>
              <td>{index+1}</td>  
                <td>{data.dakidNo}</td>  
                <td>{data.section.sectionName}</td>  
                <td>{data.referenceNo}</td>  
                 <td>{data.referenceDate}</td>  
              </tr>  
            ))}  

        </table>  

    </div>
    <div className="px-4">
						<button type="submit" >DakList</button>
					</div>


				</form>
			</div>
		</div>
	);
};


export default withRouter(DakListEdit);
