                     {tBillCsData && tBillCsData.map(tc =>
                      <tr key={tc.dakidNo}>
                        <td>{tc.bankDetails}</td>
                            <td>{tc.ifscCode}</td>
                            <td>{tc.bankAccountNo}</td>
