/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By : 
 */

import { useState, useEffect, useMemo } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import Table, { SelectColumnFilter } from '../utils/Table'  // 

import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'


const schema = yup.object({




});



const DailyProgressReport = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();
	//console.log(id);

	let history = useHistory();

 	const [data,setData]=useState([]);
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});

	 const[fromDate,setFromDate]=useState(new Date());
	 const[toDate, setToDate]=useState(new Date());
	 const [fromDateStr,setFromDateStr]=useState('');
	 const [toDateStr,setToDateStr]=useState('');



	const [loading, setLoading] = useState(true);
	 const [mesg,setMesg]=useState();

	const [key, setKey] = useState('Page1');




	 const generateDpr = () => {

		let saving = false;

		async function fetchData() {

			if (!saving)
				//console.log(secId);
				await axios.get(`/cbillTadaLtcs/${fromDateStr}/${toDateStr}/dpr`)
					.then((response) => {
						console.log("response>>" + response.data);
						//setSh3List(response.data);
						 setData(response.data);;

					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		fetchData();

		return () => { saving = true; }
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}


	const handleInputFromDateChange =  (e) =>{

		   console.log(e.target.value);
			 //setFromDate(e.target.value);	
			 setFromDateStr(e.target.value);

	}

	 const handleInputToDateChange =  (e) =>{

		   console.log(e.target.value);
			 //setToDate(e.target.value);
			 setToDateStr(e.target.value);	

	}




	const onError = (errors, e) => console.log(errors, e);

	//All foreign keys. 
	// XXXXXX Add search fields and adjust the values as necessary


	const errorCallback = (err) => {
		//console.log(err);
		setServerErrors(err);
	}

	const handleCheckBox = index => (e) =>{

		   console.log(e.target.checked);
			let item = data[index];

		item['select'] = e.target.checked;
		let newData = [...data];
		newData[index] = item;
		setData(newData);
		console.log(data);	

	}



	const handleInputChange = (e) => {
		console.log(e.target.value);
	//	console.log("handle input change");


	};

	const ShowDpr = () => {


		const columns=useMemo(()=>[



		{
			Header: "Section",
			accessor: 'sectionname',
		},
		{
			Header: "Task",
			accessor: 'task',
		},
		{
			Header: "Usr",
			accessor: 'usrName',
		},
		{
			Header: "Opening Balance",
			accessor: 'openingBalance',
		},

		{
			Header: "Receipts",
			accessor: 'receipts',
		},

		{
			Header: "Disposal",
			accessor: 'disposal',
		},
		{
			Header: "Closing Balance",
			accessor: 'closingBalance',
		},



		{
			Header: "Oldest Date",
			accessor: 'oldestDate',
		},


	],[data])

 	return (
		<div className="min-h-screen bg-gray-100 text-gray-900">
			<main className="max-w-8xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
				<div className=" ">


					<h1 className="text-xl font-semibold ml-4">Daily Progress Report</h1>




				</div>
				<div className="-mt-2 max-h-1 py-0 ml-0">
					<Table columns={columns} data={data} page={50} className="table-auto" />
				</div>
			</main>

		</div>
	);
	}

	return (
		<div className="min-h-screen bg-gray-100 text-gray-900">
			<main className="max-w-xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
				<div className=" ">


					<h1 className="text-xl font-semibold ml-4">Select From Date--- To Date For Daily Progress Report</h1>

					<div>
						<input type="date" name="FromDate" placeholder="dd-mm-yyyy"
						onChange={e=>handleInputFromDateChange(e)}							 
							className="form-control py-0" />
							</div>


					<div>
						<input type="date" name="ToDate" placeholder="dd-mm-yyyy"
						onChange={e=>handleInputToDateChange(e)}							 
							className="form-control py-0" />
							</div>







					<div  

							className="pl-1 -ml-2 inputField flex-initial" />
						<button type="submit" onClick={generateDpr} className="w-32 m-0 p-0">Generate DPR</button>

					</div>		
					</main> 

		<main className="max-w-8xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
					 <div className="flex-container">  


       					<ShowDpr/>
    				</div>


			</main>

		</div>
	);

};


export default withRouter(DailyProgressReport);
