/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 03 April 2024
 *
 * Modified By : 
 */

import { useState, useEffect, useMemo } from 'react';
import Table, { SelectColumnFilter } from '../utils/Table'  // 
import axios from "axios";
import { Link } from "react-router-dom";

function BillList() {

	const [data, setData] = useState([]);
	const [update, setUpdate] = useState(false);
	const [serverErrors, setServerErrors] = useState([]);
	/*  const [search, setSearch] = useState({
	financialYear: '',
	dakNo: '',
	unitCode: ''
  });*/
	const [search, setSearch] = useState('');

	const [inputText, setInputText] = useState('');

	useEffect(() => {
		let fetching = false;
		async function fetchData() {
			if (!fetching)
				await axios.get('/mbills?search=' + search)
					.then((response) => {
						setData(response.data);
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		fetchData();
		return () => { fetching = true; }

	}, [update, search]);


	async function remove(id) {
		await axios.delete(`/bills/${id}`)
			.then(() => {
				//console.log(data);
				let updatedRecords = [...data].filter((i) => i.id !== id);
				console.log(updatedRecords);
				setData(updatedRecords);
				setUpdate(!update);
			})
			.catch((error) => {
				console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}


	const columns = useMemo(() => [
		{
			Header: 'Action',
			Cell: ({ row }) => (
				<div>
					<Link to={"/mbills/" + row.original.id}>
						<button className=" w-16 m-0 p-0 " > View More </button>
					</Link>
					{' '}
					<Link to={"/mbills/" + row.original.id + "/edit"}>
						<button className=" w-16 m-0 p-0 " > Edit </button>
					</Link>
				</div>
			)
		},
		{
			Header: "DakId No",
			accessor: 'dak.dakidNo',
		},

		{
			Header: "Month",
			accessor: 'month',// Change this
		},

		{
			Header: "Unit",
			accessor: 'unit.unitCode',// Change this
		},

		{
			Header: "Record Status",
			accessor: 'recordStatus',// Change this
		},

		{
			Header: "Reason",
			accessor: 'reason',// Change this
		},

		{
			Header: "Type Of Bill",
			accessor: 'billType.typeOfBill',// Change this
		},

		{
			Header: "Vendor",
			accessor: 'vendor.vendorName',// Change this
		},

		{
			Header: "Amount Claimed",
			accessor: 'amountClaimed',// Change this
		},

		{
			Header: "Amount Passed",
			accessor: 'amountPassed',// Change this
		},

		{
			Header: "Task",
			accessor: 'askUsr.usrName'
		}
	], [data])

	const handleSubmit = (event) => {
		event.preventDefault();
		console.log(inputText);
		setSearch(inputText);
	}

	const handleKeyPress = (event) => {
		// look for the `Enter` keyCode
		if (event.keyCode === 13 || event.which === 13) {
			handleSubmit(event)
		}
	}

	return (
		<div className="min-h-screen bg-gray-100 text-gray-900">
			<main className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
				<div className="mt-2 ml-4">
					<h1 className="text-xl font-semibold">M Bills Search Filter</h1>
					<div className="flexContainer">
					<label>Financial Year</label>
						<input type="text" name="financialYear"
							onChange={e => setInputText(e.target.value)}
							onKeyPress={handleKeyPress}
							className="pl-2 -ml-2 inputField flex-initial" />
						<input type="text" name="dakNo"
							onChange={e => setInputText(e.target.value)}
							onKeyPress={handleKeyPress}
							className="pl-2 -ml-2 inputField flex-initial" />
						<input type="text" name="unitCode"
							onChange={e => setInputText(e.target.value)}
							onKeyPress={handleKeyPress}
							className="pl-2 -ml-2 inputField flex-initial" />

						<button type="submit" onClick={handleSubmit} className="w-16 m-0 p-0">Search</button>
						<div>
							<Link to={"/bills/new"}>
								<button className=" w-32 ml-8 p-0 h-6 -mt-2" > Add Bill </button>
							</Link>
						</div>
					</div>
				</div>
				<div className="-mt-2 max-h-1 py-0">
					<Table columns={columns} data={data} className="table-auto" />
				</div>
			</main>
		</div>
	);
}

export default (BillList);
