import { useState, useEffect, useMemo } from 'react';
import Table, { SelectColumnFilter } from '../utils/Table'  // 
import axios from "axios";

import { withRouter, Link,useLocation  } from "react-router-dom";

function OmroList() {

	const [data, setData] = useState([]);
	const [update, setUpdate] = useState(false);
	const [serverErrors, setServerErrors] = useState([]);
	const [search, setSearch] = useState("");
	const [inputText, setInputText] = useState('');

	const [cdaoNo, setCdaoNo] = useState('');
	const [name, setName] = useState('');
	const [empId, setEmpId] = useState('');
	const [selectionType,setSelectionType]=useState('');
	const location=useLocation();

		useEffect(() => {
		let fetching = false;
		async function fetchData() {
			if (!fetching)
			if(selectionType==='approved'){
				await axios.get('/omros?filter=approved&search=' + search)
					.then((response) => {
						setData(response.data);
						setSelectionType('pending');
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
			}else{
				await axios.get('/omros?search=' + search)
					.then((response) => {
						setData(response.data);
						setSelectionType('pending');
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
				}
		}
		fetchData();
		return () => { fetching = true; }

	}, [update, search]);


	async function submitMro(id) {
		await axios.put(`/omros/${id}/submitMro`)
			.then(() => {
				//console.log(data);
				let updatedRecords = [...data].filter((i) => i.id !== id);
				console.log(updatedRecords);
				setData(updatedRecords);
				setUpdate(!update);
			})
			.catch((error) => {
				console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}

	async function approveMro(id) {
		await axios.put(`/omros/${id}/approveMro`)
			.then(() => {
				//console.log(data);
				let updatedRecords = [...data].filter((i) => i.id !== id);
				console.log(updatedRecords);
				setData(updatedRecords);
				setUpdate(!update);
			})
			.catch((error) => {
				console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}
	async function remove(id) {
		await axios.delete(`/omros/${id}`)
			.then(() => {
				//console.log(data);
				let updatedRecords = [...data].filter((i) => i.id !== id);
				console.log(updatedRecords);
				setData(updatedRecords);
				setUpdate(!update);
			})
			.catch((error) => {
				console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}

	const handleClick = () => {
		let fetching = false;
		async function fetchData() {
			if (!fetching)
				await axios.get(`/omros?filter=approved`)
					.then((response) => {
						console.log(response.data);
						setData(response.data);
						setSelectionType('approved');

					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		fetchData();
		return () => { fetching = true; }
	}
	const handleSubmit = (event) => {
		event.preventDefault();
		console.log(inputText);
		setSearch(inputText);
	}

	const handleKeyPress = (event) => {
		// look for the `Enter` keyCode
		if (event.keyCode === 13 || event.which === 13) {
			handleSubmit(event)
		}
	}
	const columns = useMemo(() => [
		{
			Header: 'Action',
			Cell: ({ row }) => (
				<div>
					{(row.original.action != null && !(row.original.action.includes('Subm')) && !(row.original.action.includes('Appr'))) &&
						<Link to={"/omros/" + row.original.id}>
							<button className=" w-16 m-0 p-0 " > Edit </button>
						</Link>
					}
					{' '}
					{(row.original.action === 'Submission by AAO.') &&
						<button
							className="w-16 m-0 p-0 bg-red-500 hover:bg-red-700 "
							onClick={() => submitMro(row.original.id)}
						>	Submit 	</button>
					}
					{' '}
					{(row.original.action === 'Approval by AO.')   &&
						<button
							className="w-16 m-0 p-0 bg-red-500 hover:bg-red-700 "
							onClick={() => approveMro(row.original.id)}
						>	Approve 	</button>
					}

				</div>
			)
		},
		{
			Header: "Dak",
			accessor: 'dakidNo',// Change this
		},

		{
			Header: "Officer Name",
			accessor: 'name',// Change this
		},

		{
			Header: "Cda No",
			accessor: 'cdaoNo',
		},

		{
			Header: "Check Digit",
			accessor: 'checkDigit',
		},

		{
			Header: "Min No",
			accessor: 'minNo',
		},

		{
			Header: "MRO Date",
			accessor: 'mroDate',
		},

		 	{
			Header: "MRO Amount",
			accessor: 'amount',
		},



	], [data])


	return (
		<div className="min-h-screen bg-gray-100 text-gray-900">
			<main className="max-w-8xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
				<div className="mt-2 ml-4">
					<h1 className="text-xl font-semibold">Omros</h1>
					<div className="flexContainer">
						<input type="text" name="search"
							onChange={e => setInputText(e.target.value)}
							onKeyPress={handleKeyPress}
							className="pl-2 -ml-2 inputField flex-initial" />
						<button type="submit" onClick={handleSubmit} className="w-16 m-0 p-0">Search</button>
						<div>

								<button onClick={handleClick} className=" w-24 m-0 p-0 " > Processed </button>

						</div>

					</div>

					<div>
						 <h1 className="text-xl font-semibold">{location.state}</h1>
						</div>	
				</div>

				<div className="-mt-2 max-h-1 py-0">
					<Table columns={columns} data={data} className="table-auto" />
				</div>
			</main>
		</div>
	);
}

export default withRouter(OmroList);
