import { useState, useEffect,useMemo } from "react";
import { withRouter, useParams, useHistory,useLocation } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

//import DatePicker  from 'react-datepicker';
//import "react-datepicker/dist/react-datepicker.css";
//import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'
import Table from '../utils/Table';

const schema = yup.object({



});


const NdcEdit = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();

	let history = useHistory();
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});
	const [key, setKey] = useState('page1');
	const [dakId,setDakId]=useState('');
	const [secName,setSecName]=useState('');
	const [drData,setDrData]=useState([]);
	const [cbillData,setCbillData]=useState([]);
	const [omroData,setOmroData]=useState([]);



	useEffect(() => {
		let isCancelled = false;
		console.log(id);
		if (id !== 'new') {
			async function fetchData() {
				let record = '';
				await axios.get('/ndcs/' + id)
					.then((response) => {
						record = response.data;
						setDakId(record['dak'].dakidNo);
						setSecName(record['section'].sectionName);
						const fields = [
						'id'
, 'dak', 'auditorDate', 'aaoDate', 'aoDate', 'approved', 'section', 'employee', 'cdaoNo', 'recordStatus', 'checkDigit',
 'amountOutstanding', 'ndcDate', 'letterNo', 'me','remarks','reason'			
					];
					fields.forEach(field => setValue(field, record[field]));
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if(error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					});


				if (!isCancelled) {
					setEntity(record);
					setState(prev => ({ ...prev, state: record }));
				}
			}

			fetchData();
			return () => {
				isCancelled = true;
			};
		}

	}, [id, setValue]);

	useEffect(() => {
		let fetching = false;
		let unmounted = false;
		async function fetchDrData() {
			//console.log("loading dis " + dakId);
			 if (!fetching)
					await axios.get(`/ndcs/pendingDr/${id}`)
						.then((response) => {
								//console.log("disallowance code data >>>> "+response.data);

							setDrData(response.data);

						})
						.catch((error) => {
							//console.log(error);
							//console.log(error.response.status);
							//console.log(error.response.headers);
							if (error.response)
								setServerErrors(error.response.data.error);
							else
								setServerErrors(error.Error);
						})
		}
		fetchDrData();

		return () => { fetching = true; unmounted = true; }
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [id]);

	useEffect(() => {
		let fetching = false;
		let unmounted = false;
		async function fetchCbillData() {
			//console.log("loading dis " + dakId);
			 if (!fetching)
					await axios.get(`/ndcs/pendingCbill/${id}`)
						.then((response) => {
								//console.log("disallowance code data >>>> "+response.data);

							setCbillData(response.data);

						})
						.catch((error) => {
							//console.log(error);
							//console.log(error.response.status);
							//console.log(error.response.headers);
							if (error.response)
								setServerErrors(error.response.data.error);
							else
								setServerErrors(error.Error);
						})
		}
		fetchCbillData();

		return () => { fetching = true; unmounted = true; }
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [id]);

	useEffect(() => {
		let fetching = false;
		let unmounted = false;
		async function fetchOmroData() {
			//console.log("loading dis " + dakId);
			 if (!fetching)
					await axios.get(`/ndcs/pendingOmro/${id}`)
						.then((response) => {
								//console.log("disallowance code data >>>> "+response.data);

							setOmroData(response.data);

						})
						.catch((error) => {
							//console.log(error);
							//console.log(error.response.status);
							//console.log(error.response.headers);
							if (error.response)
								setServerErrors(error.response.data.error);
							else
								setServerErrors(error.Error);
						})
		}
		fetchOmroData();

		return () => { fetching = true; unmounted = true; }
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [id]);

const ShowDrData = () => {

	const columns = useMemo(() => [




		{
			Header: "DakId",
			accessor: 'dakidNo',
		},
		{
			Header: "DemandType",
			accessor: 'demandType',
		},
		{
			Header: "Origin",
			accessor: 'Origin',
		},

		{
			Header: "Amount",
			accessor: 'amount',
		},
		{
			Header: "Demand Date",
			accessor: 'demandDate',
		},
		{
			Header: "Journey Date",
			accessor: 'journeyStartDate',
			Cell: ({ row }) => (
				<div>

				<div>
				{row.original.journeyStartDate &&
				<label>Start Date : {row.original.journeyStartDate}</label>
				}
				 </div>
				 <div>
				{row.original.journeyEndDate &&
				<label>End Date : {row.original.journeyEndDate}</label>
				}
				 </div>
				 <div>
				{row.original.stationFrom &&
				<label>From : {row.original.stationFrom}</label>
				}
				 </div>
				 <div>
				{row.original.stationTo &&
				<label>To : {row.original.stationTo}</label>
				}
				 </div>

				</div>

				)
		},


	], [drData])


	return (
		<div className="h-96 bg-green-100 text-gray-900">
			<main className="max-w-5xl mx-auto px-2 sm:px-6 lg:px-8 pt-4">
				<div className=" ">
				</div>
				<div className="-mt-2 max-h-0 py-0 ml-0">
					<Table columns={columns} data={drData} className="table-auto" />
				</div>
			</main>

		</div>
	);

	}

	const ShowCbillData = () => {

	const columns = useMemo(() => [



		{
			Header: "CDAO No",
			accessor: 'cdaoNo',
		},
		{
			Header: "DakId",
			accessor: 'dakId',
		},

		{
			Header: "Amount",
			accessor: 'amountClaimed',
		},

		{
			Header: "Journey Date",
			accessor: 'journeyStartDate',
			Cell: ({ row }) => (
				<div>

				<div>
				{row.original.journeyStartDate &&
				<label>Start Date : {row.original.journeyStartDate}</label>
				}
				 </div>
				 <div>
				{row.original.journeyEndDate &&
				<label>End Date : {row.original.journeyEndDate}</label>
				}
				 </div>
				 <div>
				{row.original.journeyStationFrom &&
				<label>From : {row.original.journeyStationFrom}</label>
				}
				 </div>
				 <div>
				{row.original.journeyStationTo &&
				<label>To : {row.original.journeyStationTo}</label>
				}
				 </div>

				</div>

				)
		},
		{
			Header: "Record Status",
			accessor: 'recordStatus',
		},


	], [cbillData])


	return (
		<div className="h-96 bg-green-100 text-gray-900">
			<main className="max-w-5xl mx-auto px-2 sm:px-6 lg:px-8 pt-4">
				<div className=" ">
				</div>
				<div className="-mt-2 max-h-0 py-0 ml-0">
					<Table columns={columns} data={cbillData} className="table-auto" />
				</div>
			</main>

		</div>
	);

	}

	const ShowMroData = () => {

	const columns = useMemo(() => [



		{
			Header: "CDAO No",
			accessor: 'cdaoNo',
		},
		{
			Header: "DakId",
			accessor: 'dakId',
		},

		{
			Header: "Amount",
			accessor: 'amount',
		},


		{
			Header: "Record Status",
			accessor: 'recordStatus',
		},


	], [omroData])


	return (
		<div className="h-96 bg-green-100 text-gray-900">
			<main className="max-w-5xl mx-auto px-2 sm:px-6 lg:px-8 pt-4">
				<div className=" ">
				</div>
				<div className="-mt-2 max-h-0 py-0 ml-0">
					<Table columns={columns} data={omroData} className="table-auto" />
				</div>
			</main>

		</div>
	);

	}



	const onSubmit = (data, event) => {
		event.preventDefault();
		console.log(data);
		if (data.id) {
			axios.put("/banks/" + data.id, data)
				.then((response) => {
					if(response.status===200)
						history.push("/banks");
				 })
				.catch((error) => {
					//console.log(error.response.data);
					console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		} else {
			axios.post("/banks", data)
				.then((response) => {
					console.log(response.data);
					if(response.status===200)
						history.push({pathname : '/banks',state: response.data});
				 })
				.catch((error) => {

					//console.log(error.response.data);

					console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		}


	}

	const onError = (errors, e) => console.log(errors, e);

	//All foreign keys. 
	// XXXXXX Add search fields and adjust the values as necessary
	const parentData = {

		employee: {
			title: "Employee",
			url : "employees",
			searchList : ['cdaoNo','officerName'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "employee",
		},


	}

	//Callback for child components (Foreign Keys)
	const callback = (childData) => {
		//console.log("Parent Callback");
		setEntity(prev => ({ ...prev, [childData.fk]: childData.entity }));
		setValue(childData.fk, childData.entity);
		//console.log(errors);
		clearErrors(childData.fk);
	};

	const errorCallback = (err) => {
		//console.log(err);
		setServerErrors(err);
	}

	const handleInputChange = (e) => {
		//console.log(e.target.value);
	};

	return (
		<div className="min-h-screen bg-gray-100 text-gray-900">
			<div  className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 pt-4" >

				<form onSubmit={handleSubmit(onSubmit, onError)}>
					<h1 >{id === 'new' ? 'Add' : 'Edit'} NDC </h1>
					<div className="text-red-500">{serverErrors}</div>


							<div className="grid grid-cols-2 gap-0">





								<div>
									<label>Dak Id</label>
									<input type="text" name="dakIdNo"  value={dakId} readonly
										className="form-control py-0"
									/>

								</div>
								<div>
									<label>Section</label>
									<input type="text" name="secName"  value={secName} readonly
										className="form-control py-0"
									/>

								</div>
								<div >
									<LiveSearch name="employee" onChange={handleInputChange}
										parentData={parentData.employee} parentCallback={callback} 
										fkEntity={entity.employee} errCallback={errorCallback} />
									<div className="text-red-500 ">{errors.employee?.message}</div>
								</div>	

						  	<div>
									<label>Ndc Date</label>
									<input type="date" name="ndcDate" {...register("ndcDate")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.ndcDate?.message}</div>
								</div>
								<div>
									<label>Amount Outstanding</label>
									<input type="text" name="amountOutstanding"  {...register("amountOutstanding")}  
										className="form-control py-0"
									/>

								</div>

								<div>
									<label>Remarks</label>
									<textarea type="text" name="remarks"  {...register("remarks")} 

										className="form-control py-0"
									/>

								</div>




							</div>


						 <div className="max-w-5xl mx-auto">
							<ShowDrData/>
							</div>

								<div className="max-w-5xl mx-auto ">
							<ShowCbillData/>
							</div>

								<div>
							<ShowMroData/>
							</div>




					<div className="px-8">
						<button type="submit" >Save</button>
					</div>

				</form>
			</div>


		</div>
	);
};

export default withRouter(NdcEdit);
