 @author Raja Reddy
 Date : 13 Oct 2021

 */


import { useState, useEffect } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';


const schema = yup.object({
	usrName: yup.string().required('User Name is required.'),
	accountNo: yup.string().required('Account no. is required'),
	loginName: yup.string().required('Login Name is required'),
	dadDesignation: yup.object().required('Designation is required'),
	password: yup.string(),
	password2: yup.mixed().oneOf([yup.ref('password'), null], 'Passwords must match')
	//age: yup.number().positive().integer().required(),
}).required();


const UsrEdit = () => {
	//const { register, handleSubmit, reset, setValue, getValues, formState: { errors }, clearErrors } = useForm({
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();
	//console.log(id);

	let history = useHistory();
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});


	useEffect(() => {
		let isCancelled = false;
		console.log(id);
		if (id !== 'new') {
			async function fetchData() {
				let record = '';
				await axios.get('/usrs/' + id)
					.then((response) => {
						record = response.data;
					})
					.catch((error) => {
						console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						setServerErrors(error.response.data.error)
					})
					;

				//console.log(result.data);
				//let d = result.data;
				const fields = ['id', 'usrName', 'accountNo', 'loginName', 'dadDesignation', 'gender', 'section',
					'phone1', 'phone2', 'phone3', 'email', 'enabled'];
				fields.forEach(field => setValue(field, record[field]));
				if (!isCancelled) {
					setEntity(record);
					setState(prev => ({ ...prev, state: record }));
				}
			}

			fetchData();
			return () => {
				isCancelled = true;
			};
		}

	}, [id, setValue]);


	//Callback for child components (Forein Keys)
	const callback = (childData) => {
		//console.log("Parent Callback");
		setEntity(prev => ({ ...prev, dadDesignation: childData.props.entity }));
