/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By :
 */

import { useState, useEffect, useMemo } from "react";
import Table, { SelectColumnFilter } from "../utils/Table"; //
import axios from "axios";
import { withRouter, Link } from "react-router-dom";

function TaskLogList() {
  const [data, setData] = useState([]);
  const [update, setUpdate] = useState(false);
  const [serverErrors, setServerErrors] = useState([]);
  const [search, setSearch] = useState("");
  const [inputText, setInputText] = useState("");

  useEffect(() => {
    let fetching = false;
    async function fetchData() {
      if (!fetching)
        await axios
          .get(`/tasks?search=${search}&active=${false}`)
          .then((response) => {
            setData(response.data);
          })
          .catch((error) => {
            //console.log(error);
            //console.log(error.response.status);
            //console.log(error.response.headers);
            if (error.response) setServerErrors(error.response.data.error);
            else setServerErrors(error.Error);
          });
    }
    fetchData();
    return () => {
      fetching = true;
    };
  }, [update, search]);

  async function remove(id) {
    await axios
      .delete(`/tasks/${id}`)
      .then(() => {
        //console.log(data);
        let updatedRecords = [...data].filter((i) => i.id !== id);
        console.log(updatedRecords);
        setData(updatedRecords);
        setUpdate(!update);
      })
      .catch((error) => {
        console.log(error);
        //console.log(error.response.status);
        //console.log(error.response.headers);
        if (error.response) setServerErrors(error.response.data.error);
        else setServerErrors(error.Error);
      });
  }

  const columns = useMemo(
    () => [
      /* {
        Header: "Action",
        Cell: ({ row }) => (
          <div>
            <Link to={"/tasks/" + row.original.id}>
              <button className=" w-16 m-0 p-0 "> Edit </button>
            </Link>{" "}
            <button
              className="w-16 m-0 p-0 bg-red-500 hover:bg-red-700 "
              onClick={() => remove(row.original.id)}
            >
              {" "}
              Delete{" "}
            </button>
          </div>
        ),
      }, */
      {
        Header: "Section",
        accessor: "section.sectionName", // Change this
        Filter: SelectColumnFilter,
      },
      {
        Header: "Task No",
        accessor: "taskNo",
        Filter: SelectColumnFilter,
      },
      {
        Header: "Task Usr",
        accessor: "usrName",
        Filter: SelectColumnFilter,
      },
      {
        Header: "From Date",
        accessor: "fromDate",
      },

      {
        Header: "To Date",
        accessor: "toDate",
      },

      {
        Header: "Enabled",
        accessor: "enabled",
        Cell: ({ row }) => (
          <div>{data[row.index]["enabled"] === true ? "Y" : "N"}</div>
        ),
      },

      /*
		{
			Header: "Login Name",
			accessor: 'loginName',
			Filter: SelectColumnFilter,  // new
			filter: 'includes',
		},
		*/
    ],
    [data]
  );

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(inputText);
    setSearch(inputText);
  };

  const handleKeyPress = (event) => {
    // look for the `Enter` keyCode
    if (event.keyCode === 13 || event.which === 13) {
      handleSubmit(event);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 text-gray-900">
      <main className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
        <div className="mt-2 ml-4">
          <h1 className="text-xl font-semibold">Task Log</h1>
          <div className="flexContainer">
            <input
              type="text"
              name="search"
              placeholder="Task No"
              onChange={(e) => setInputText(e.target.value)}
              onKeyPress={handleKeyPress}
              className="pl-2 -ml-2 inputField flex-initial"
            />
            <button
              type="submit"
              onClick={handleSubmit}
              className="w-16 m-0 p-0"
            >
              Search
            </button>
            {/* <div>
              <Link to={"/tasks/new"}>
                <button className=" w-32 ml-8 p-0 h-6 -mt-2"> Add Task </button>
              </Link>
            </div> */}
          </div>
        </div>
        <div className="max-h-full py-0 mt-2">
          <Table columns={columns} data={data} className="table-auto" />
        </div>
      </main>
    </div>
  );
}

export default withRouter(TaskLogList);
