/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By : 
 */

import { useState, useEffect } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

//import DatePicker  from 'react-datepicker';
//import "react-datepicker/dist/react-datepicker.css";
//import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'

const schema = yup.object({
      dsopDate: yup.string().required('Required'),
      commissionGoi: yup.object().required('Required'),
      commissionControlDak: yup.object().required('Required'),
      dak: yup.object().required('Required'),
      lpcCredit: yup.number().integer().required('Required'),
      lpcDebit: yup.number().integer().required('Required'),
      basic: yup.number().integer().required('Required'),
      da: yup.number().integer().required('Required'),
      msp: yup.number().integer().required('Required'),
      npa: yup.number().integer().required('Required'),
      techPay: yup.number().integer().required('Required'),
      auditorDate: yup.string().required('Required'),
      aaoDate: yup.string().required('Required'),
      aoDate: yup.string().required('Required'),
      goDate: yup.string().required('Required'),
      approved: yup.boolean().required('Required'),
      formaLetterDate: yup.string().required('Required'),
      dateOfReporting: yup.string().required('Required'),
      payAdvance: yup.number().integer().required('Required'),
      dsopWillingness: yup.boolean().required('Required'),
      dsopAmount: yup.number().integer().required('Required'),
      orsService: yup.boolean().required('Required'),
      orsServiceFrom: yup.string().required('Required'),
      orsServiceTo: yup.string().required('Required'),
      stipendAmount: yup.number().integer().required('Required'),
      cvpAmount: yup.number().integer().required('Required'),
      formaLetterNo: yup.string().required('Required'),
      pan: yup.string().required('Required'),
      uid: yup.string().required('Required'),
      martialStatus: yup.string().required('Required'),
      bankAccountNumber: yup.string().required('Required'),
      ifsc: yup.string().required('Required'),
      mobile: yup.string().required('Required'),
      email: yup.string().required('Required'),
      homeTown: yup.string().required('Required'),
      nokName: yup.string().required('Required'),
      nokRelation: yup.string().required('Required'),
      orsServiceNo: yup.string().required('Required'),
      recordStatus: yup.string().required('Required'),
      reason: yup.string().required('Required'),
      paoName: yup.string().required('Required'),
      remarks: yup.string().required('Required'),
      monthEnding: yup.string().required('Required'),
});


const CommissionFormaEdit = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();

	let history = useHistory();
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});
	const [key, setKey] = useState('page1');


	useEffect(() => {
		let isCancelled = false;
		console.log(id);
		if (id !== 'new') {
			async function fetchData() {
				let record = '';
				await axios.get('/commissionFormas/' + id)
					.then((response) => {
						record = response.data;
						const fields = [
						'id'
, 'dsopDate', 'commissionGoi', 'commissionControlDak', 'dak', 'lpcCredit', 'lpcDebit', 'basic', 'da', 'msp', 'npa', 'techPay', 'auditorDate', 'aaoDate', 'aoDate', 'goDate', 'approved', 'formaLetterDate', 'dateOfReporting', 'payAdvance', 'dsopWillingness', 'dsopAmount', 'orsService', 'orsServiceFrom', 'orsServiceTo', 'stipendAmount', 'cvpAmount', 'formaLetterNo', 'pan', 'uid', 'martialStatus', 'bankAccountNumber', 'ifsc', 'mobile', 'email', 'homeTown', 'nokName', 'nokRelation', 'orsServiceNo', 'recordStatus', 'reason', 'paoName', 'remarks', 'monthEnding'				
					];
					fields.forEach(field => setValue(field, record[field]));
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if(error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					});


				if (!isCancelled) {
					setEntity(record);
					setState(prev => ({ ...prev, state: record }));
				}
			}

			fetchData();
			return () => {
				isCancelled = true;
			};
		}

	}, [id, setValue]);


	const onSubmit = (data, event) => {
		event.preventDefault();
		console.log(data);
		if (data.id) {
			axios.put("/commissionFormas/" + data.id, data)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		} else {
			axios.post("/commissionFormas", data)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		}

		history.push("/commissionFormas");
	}

	const onError = (errors, e) => console.log(errors, e);

	//All foreign keys. 
	// XXXXXX Add search fields and adjust the values as necessary
	const parentData = {

		commissionGoi: {
			title: "Commission Goi",
			url : "commissionGois",
			searchList : ['id'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "commissionGoi",
		},
		commissionControlDak: {
			title: "Commission Control Dak",
			url : "commissionControlDaks",
			searchList : ['id'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "commissionControlDak",
		},
		dak: {
			title: "Dak",
			url : "daks",
			searchList : ['id'], //XXXXXXXXX Add search fields here and also in the corresponding repository
			fkEntity: "dak",
		},
	}

	//Callback for child components (Foreign Keys)
	const callback = (childData) => {
		//console.log("Parent Callback");
		setEntity(prev => ({ ...prev, [childData.fk]: childData.entity }));
		setValue(childData.fk, childData.entity);
		//console.log(errors);
		clearErrors(childData.fk);
	};

	const errorCallback = (err) => {
		//console.log(err);
		setServerErrors(err);
	}

	const handleInputChange = (e) => {
		//console.log(e.target.value);
	};

	return (
		<div className="max-w-xl mx-auto ">
			<div className="w-full w-3/4  mx-auto " >

				<form onSubmit={handleSubmit(onSubmit, onError)}>
					<h1 >{id === 'new' ? 'Add' : 'Edit'} Commission Forma </h1>
					<div className="text-red-500">{serverErrors}</div>
					<Tabs
						id="CommissionFormaEdit"
						activeKey={key}
						onSelect={(k) => setKey(k)}
						className="mb-3"
					>
						<Tab eventKey="page1" title="Page 1" className="h-120">
							<div className="grid grid-cols-2 gap-0">

								<div>
									<label>Dsop Date</label>
									<input type="date" name="dsopDate" {...register("dsopDate")}
									className="form-control py-0" />
									<div className="text-red-500">{errors.dsopDate?.message}</div>
								</div>						

								<div >
									<LiveSearch name="commissionGoi" onChange={handleInputChange}
										parentData={parentData.commissionGoi} parentCallback={callback} 
										fkEntity={entity.commissionGoi} errCallback={errorCallback} />
									<div className="text-red-500 ">{errors.commissionGoi?.message}</div>
								</div>						


								<div >
									<LiveSearch name="commissionControlDak" onChange={handleInputChange}
										parentData={parentData.commissionControlDak} parentCallback={callback} 
										fkEntity={entity.commissionControlDak} errCallback={errorCallback} />
									<div className="text-red-500 ">{errors.commissionControlDak?.message}</div>
								</div>						


								<div >
									<LiveSearch name="dak" onChange={handleInputChange}
										parentData={parentData.dak} parentCallback={callback} 
										fkEntity={entity.dak} errCallback={errorCallback} />
									<div className="text-red-500 ">{errors.dak?.message}</div>
								</div>						


								<div>
									<label>Lpc Credit</label>
									<input type="text" name="lpcCredit" {...register("lpcCredit")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.lpcCredit?.message}</div>
								</div>


								<div>
									<label>Lpc Debit</label>
									<input type="text" name="lpcDebit" {...register("lpcDebit")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.lpcDebit?.message}</div>
								</div>


								<div>
									<label>Basic</label>
									<input type="text" name="basic" {...register("basic")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.basic?.message}</div>
								</div>


								<div>
									<label>Da</label>
									<input type="text" name="da" {...register("da")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.da?.message}</div>
								</div>


								<div>
									<label>Msp</label>
									<input type="text" name="msp" {...register("msp")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.msp?.message}</div>
								</div>


								<div>
									<label>Npa</label>
									<input type="text" name="npa" {...register("npa")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.npa?.message}</div>
								</div>


								<div>
									<label>Tech Pay</label>
									<input type="text" name="techPay" {...register("techPay")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.techPay?.message}</div>
								</div>



								<div>
									<label>Auditor Date</label>
									<input type="date" name="auditorDate" {...register("auditorDate")}
									className="form-control py-0" />
									<div className="text-red-500">{errors.auditorDate?.message}</div>
								</div>						


								<div>
									<label>Aao Date</label>
									<input type="date" name="aaoDate" {...register("aaoDate")}
									className="form-control py-0" />
									<div className="text-red-500">{errors.aaoDate?.message}</div>
								</div>						


								<div>
									<label>Ao Date</label>
									<input type="date" name="aoDate" {...register("aoDate")}
									className="form-control py-0" />
									<div className="text-red-500">{errors.aoDate?.message}</div>
								</div>						


								<div>
									<label>Go Date</label>
									<input type="date" name="goDate" {...register("goDate")}
									className="form-control py-0" />
									<div className="text-red-500">{errors.goDate?.message}</div>
								</div>						

								<div>
									<label>Approved</label>
									<input type="text" name="approved" {...register("approved")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.approved?.message}</div>
								</div>



								<div>
									<label>Forma Letter Date</label>
									<input type="date" name="formaLetterDate" {...register("formaLetterDate")}
									className="form-control py-0" />
									<div className="text-red-500">{errors.formaLetterDate?.message}</div>
								</div>						


								<div>
									<label>Date Of Reporting</label>
									<input type="date" name="dateOfReporting" {...register("dateOfReporting")}
									className="form-control py-0" />
									<div className="text-red-500">{errors.dateOfReporting?.message}</div>
								</div>						

								<div>
									<label>Pay Advance</label>
									<input type="text" name="payAdvance" {...register("payAdvance")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.payAdvance?.message}</div>
								</div>


								<div>
									<label>Dsop Willingness</label>
									<input type="text" name="dsopWillingness" {...register("dsopWillingness")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.dsopWillingness?.message}</div>
								</div>


								<div>
									<label>Dsop Amount</label>
									<input type="text" name="dsopAmount" {...register("dsopAmount")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.dsopAmount?.message}</div>
								</div>


								<div>
									<label>Ors Service</label>
									<input type="text" name="orsService" {...register("orsService")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.orsService?.message}</div>
								</div>



								<div>
									<label>Ors Service From</label>
									<input type="date" name="orsServiceFrom" {...register("orsServiceFrom")}
									className="form-control py-0" />
									<div className="text-red-500">{errors.orsServiceFrom?.message}</div>
								</div>						


								<div>
									<label>Ors Service To</label>
									<input type="date" name="orsServiceTo" {...register("orsServiceTo")}
									className="form-control py-0" />
									<div className="text-red-500">{errors.orsServiceTo?.message}</div>
								</div>						

								<div>
									<label>Stipend Amount</label>
									<input type="text" name="stipendAmount" {...register("stipendAmount")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.stipendAmount?.message}</div>
								</div>


								<div>
									<label>Cvp Amount</label>
									<input type="text" name="cvpAmount" {...register("cvpAmount")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.cvpAmount?.message}</div>
								</div>


								<div>
									<label>Forma Letter No</label>
									<input type="text" name="formaLetterNo" {...register("formaLetterNo")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.formaLetterNo?.message}</div>
								</div>


								<div>
									<label>Pan</label>
									<input type="text" name="pan" {...register("pan")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.pan?.message}</div>
								</div>


								<div>
									<label>Uid</label>
									<input type="text" name="uid" {...register("uid")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.uid?.message}</div>
								</div>


								<div>
									<label>Martial Status</label>
									<input type="text" name="martialStatus" {...register("martialStatus")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.martialStatus?.message}</div>
								</div>


								<div>
									<label>Bank Account Number</label>
									<input type="text" name="bankAccountNumber" {...register("bankAccountNumber")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.bankAccountNumber?.message}</div>
								</div>


								<div>
									<label>Ifsc</label>
									<input type="text" name="ifsc" {...register("ifsc")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.ifsc?.message}</div>
								</div>


								<div>
									<label>Mobile</label>
									<input type="text" name="mobile" {...register("mobile")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.mobile?.message}</div>
								</div>


								<div>
									<label>Email</label>
									<input type="text" name="email" {...register("email")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.email?.message}</div>
								</div>


								<div>
									<label>Home Town</label>
									<input type="text" name="homeTown" {...register("homeTown")}
										className="form-control py-0"
									/>
									<div className="text-red-500">{errors.homeTown?.message}</div>
								</div>


								<div>
									<label>Nok Name</label>
									<input type="text" name="nokName" {...register("nokName")}
										className="form-control py-0"
									/>
