/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By : 
 */

import { useState, useEffect, useMemo } from "react";
import Table, { SelectColumnFilter } from '../utils/Table'  // 
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'
import { Link } from "react-router-dom";


const Pro = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		//resolver: yupResolver(schema)
	});



	let history = useHistory();
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});
	const [key, setKey] = useState('page1');
	const [action, setAction] = useState('');
	const [tBillData, setTBillData] = useState([]);
	const [tBillFamData, setTBillFamData] = useState([]);
	const [mroData, setMroData] = useState([]);
	const [bankAccountData, setBankAccountData] = useState([]);
	const [emp,setEmp]=useState({});


 	const [cdaoNo,setCdaoNo]=useState('');
	const [dakType,setDakType]=useState('');
	const [billType,setBillType]=useState('');
	const [rank,setRank]=useState('');
	const [sectionName,setSectionName]=useState('');
	const [lightTheme, setLightTheme] = useState(true);
	const [disabled,setDisabled]=useState(false);

async function generateIntimationSlip(id) {
	console.log(id);
		await axios.get(`/cbillTadaLtcs/${id}/intimationSlip`)
			.then((response) => {
				//console.log(data);
				console.log(response.data);
						 const url = window.open(`${process.env.REACT_APP_BASE_URL}/files/${response.data}`);

			})
			.catch((error) => {
				console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}

	async function tBillRejectionMemo(id) {
		console.log("===Id for Rejection Memo====:" + id);
		await axios.put(`/cbillTadaLtcs/${id}/tBillRejectionMemo`)
			.then((response) => {
				//console.log(data);
				console.log(response.data);
						 const url = window.open(`${process.env.REACT_APP_BASE_URL}/files/${response.data}`);
			})
			.catch((error) => {
				console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});

	}

	 const generatePro = () => {
		setDisabled(true);

		let saving = false;
		console.log(cdaoNo);

		async function fetchProData() {


			await axios.get(`/pros/${cdaoNo}/`)
				.then((response) => {
					//console.log("&&&&&&&&:" + response.data);
					setTBillData([]);
					setTBillFamData([]);
					setBankAccountData([]);
					setMroData([]);
					setEmp({});

					if (response.data['cbillList'] !== null) 
						setTBillData(response.data['cbillList']);
						console.log("-------"+tBillData[0]);
					if (response.data['fdList'] !== null)
						setTBillFamData(response.data['fdList']);
					if (response.data['baList'] !== null)
						setBankAccountData(response.data['baList']);
					if (response.data['mroList'] !== null)
						setMroData(response.data['mroList']);
					if(response.data['emp']!==null)
						setEmp(response.data['emp']);
						setDisabled(false);


				})
				.catch((error) => {
					setDisabled(false);
					console.log(error);
					//	console.log(error.response.status);
					//	console.log(error.response.headers);
					if (error.response)
						setServerErrors(error.response.data.error);
					else
						setServerErrors(error.Error);
				})



		}
		fetchProData();

		 return () => { saving = true; }
	} 


 const handleInputChange =  (e) =>{

		   console.log(e.target.value);
			 setCdaoNo(e.target.value);	

	}



	const onError = (errors, e) => {
		console.log(errors, e);
	}




	return (
		<div className={lightTheme ? 'theme-light' : 'theme-dark'} style={disabled ?{pointerEvents: "none",opacity :"0.4"}:{}}>
		<div className="min-h-screen bg-gray-100 text-gray-900">
			<div  className="max-w-10xl mx-auto px-4 sm:px-6 lg:px-8 pt-4" >
			<h1 className="text-xl font-semibold ml-4">General Information Display Screen(Public Relation Office)</h1>
					<div className="flexContainer">
						<input type="text" name="search" placeholder="CDAO No"
						onChange={e=>handleInputChange(e)}

							className="pl-2 -ml-2 inputField flex-initial" />
						<button type="submit" onClick={generatePro} className="w-24 m-0 p-0">Search</button>

					</div>


				  <div className="container">
							<h1 class="text-blue-600" align="center">Employee Details</h1>
           <table class="table-auto">
                <thead>
                    <tr>
                        <th class="bg-green-200 border text-left px-8 py-4">CDAO No</th>
                        <th class="bg-green-200 border text-left px-8 py-4">Personal No</th>
                        <th class="bg-green-200 border text-left px-8 py-4">Name</th>
                        <th class="bg-green-200 border text-left px-8 py-4">Gender</th>
                         <th class="bg-green-200 border text-left px-8 py-4">Unit</th>
                        <th class="bg-green-200 border text-left px-8 py-4">Date of Birth</th>
                        <th class="bg-green-200 border text-left px-8 py-4">Date of Commission</th>
                        <th class="bg-green-200 border text-left px-8 py-4">Date of Reporting</th>
                        <th class="bg-green-200 border text-left px-8 py-4">Date of Retirement</th>
                        <th class="bg-green-200 border text-left px-8 py-4">PAN</th>

                    </tr>
                      {emp.length!== 0 && emp.rank &&
                      <tr>

                        <td class="bg-green-50 border text-left ">{emp.cdaoNo}{emp.checkDigit}</td>
                            <td class="bg-green-50 border text-left ">{emp.icNo}{emp.icCheckDigit}</td>

                            <td class="bg-green-50 border text-left ">{emp.rank.rankName} {' '} {emp.officerName}</td>
                            <td class="bg-green-50 border text-left ">{emp.gender}</td>
                            {emp.presentUnit && 
                            <td class="bg-green-50 border text-left ">{emp.presentUnit.unitName}</td>
                            }
                            {!emp.presentUnit &&
                            <td></td>

}
                            <td class="bg-green-50 border text-left ">{emp.dateOfBirth}</td>
                            <td class="bg-green-50 border text-left ">{emp.dateOfCommission}</td>
                            <td class="bg-green-50 border text-left ">{emp.dateOfReporting}</td>
                            <td class="bg-green-50 border text-left ">{emp.fsDueDate}</td>
                            <td class="bg-green-50 border text-left ">{emp.pan}</td> 
                        </tr>

                    }


                </thead>

				<tbody>

                    {emp.length===0 &&
                    <tr>
                            <td colspan="4" align="center">No Employee Details Avaialble </td>

                        </tr>
                    }
                </tbody>

		 </table>
		 <br/>
		 </div>




 		<div className="container">
							<h1 class="text-blue-600" align="center">Bank Details</h1>
          <table class="table-auto">
                <thead>
                    <tr>
                        <th class="bg-green-200 border text-left px-8 py-4">Bank Name</th>
                        <th class="bg-green-200 border text-left px-8 py-4">Bank Branch</th>
                        <th class="bg-green-200 border text-left px-8 py-4">Bank Station</th>
                        <th class="bg-green-200 border text-left px-8 py-4">Bank Account No</th>
                        <th class="bg-green-200 border text-left px-8 py-4">IFSC</th>


                    </tr>
                     {bankAccountData && bankAccountData.map(tb =>
                      <tr key={tb.cdaoNo}>
                        <td class="bg-green-50 border text-left ">{tb.bankName}</td>
                            <td class="bg-green-50 border text-left ">{tb.bankBranch}</td>
                            <td class="bg-green-50 border text-left ">{tb.bankStation}</td>
                            <td class="bg-green-50 border text-left ">{tb.bankAccountNo}</td>
                            <td class="bg-green-50 border text-left ">{tb.ifsc}</td>

                        </tr>
                    )}



                </thead>

				<tbody>

                    {bankAccountData.length===0 &&
                    <tr>
                            <td colspan="4" align="center">No Bank Details  Avaialble </td>

                        </tr>
                    }
                </tbody>

		 </table>
		 <br/>
		 </div>




		 				 <div className="container">
							<h1 class="text-blue-600" align="center">Family Infomation</h1>
            <table class="table-auto">
                <thead>
                    <tr>
                        <th class="bg-green-200 border text-left px-8 py-4">Name Of Family Member</th>
                        <th class="bg-green-200 border text-left px-8 py-4">Gender</th>
                        <th class="bg-green-200 border text-left px-8 py-4">Relation</th>
                        <th class="bg-green-200 border text-left px-8 py-4">Date Of Birth</th>

                    </tr>
                     {tBillFamData && tBillFamData.map(tf =>
                      <tr key={tf.dakidNo}>
                        <td class="bg-green-50 border text-left ">{tf.nameOfFamilyMember}</td>
                            <td class="bg-green-50 border text-left ">{tf.gender}</td>
                            <td class="bg-green-50 border text-left ">{tf.relation}</td>
                            <td class="bg-green-50 border text-left ">{tf.dob}</td>

                        </tr>
                    )}



                </thead>

				<tbody>

                    {tBillFamData.length===0 &&
                    <tr>
                            <td colspan="4" align="center">No Family Details Avaialble </td>

                        </tr>
                    }
                </tbody>

		 </table>
		 <br/>
		 </div>





							 <div className="container">
							<h1 class="text-blue-600" align="center">Bill Details</h1>

            <table class="table-auto">

                <thead>
                    <tr>
                    	<th class="bg-green-200 border text-left px-8 py-4">SlNo</th>
                        <th class="bg-green-200 border text-left px-8 py-4">DakId/AdvId/TransId</th>

                        <th class="bg-green-200 border text-left px-8 py-4">Claim</th>
                         <th class="bg-green-200 border text-left px-8 py-4">Journey</th>
                         <th class="bg-green-200 border text-left px-8 py-4">Fare</th>
                           <th class="bg-green-200 border text-left px-8 py-4">Passing Details</th>
                           <th class="bg-green-200 border text-left px-8 py-4">Users</th>
                           <th class="bg-green-200 border text-left px-8 py-4">Link</th>

                    </tr>
                     {tBillData && tBillData.map((tb,index) =>

                        <tr key={tb.dakidNo} >
                        <td class="bg-green-50 border text-left ">{index+1}<br/>{ }<br/>{ }<br/>{  }<br/><br/>{ }<br/>{ }<br/>{  }<br/></td>
                            <td class="bg-green-50 border text-left ">DakId : {tb.dakidNo} <br/>Adv Id : {tb.advId}<br/>Dts Id : {tb.transId}<br/>Section :{tb.sectionName}<br/> Task :{tb.taskNo}<br/>Receipt Date : {tb.receiptDate}

                            <br/>{ }<br/>{ }<br/>{  }<br/><br/>{ }<br/>{ }<br/></td>
                            <td class="bg-green-50 border text-left">{tb.typeOfClaim}<br/> Claim Date : {tb.billDate }<br/>Block Yr : {tb.blockYear }<br/> Move Order No {tb.movementOrderNo}<br/>
                              Move Date : {tb.movementOrderDate}<br/> No. of Members : {tb.noOfMembers}<br/>TA Rule : {tb.taRule}<br/>{ }<br/>{ }<br/><br/>{ }<br/>{ }<br/></td>
                             <td class="bg-green-50 border text-left ">Station From : {tb.journeyStationFrom}<br/> Station To : {tb.journeyStationTo }<br/> From Date : {tb.journeyFromDateStr }<br/> To Date : {tb.journeyToDateStr } <br/>{ }<br/>Rejection : {tb.rejectionReason }<br/>{  }<br/>{  }<br/><br/>{ }<br/>{ }<br/>{ }<br/></td>
                             <td class="bg-green-50 border text-left ">Fare:{tb.totalFare}<br/>Food:{tb.foodCharges}<br/>Lodging:{tb.lodgingCharges}<br/>
                            Distance:{tb.distance}<br/>CTG:{tb.ctg}<br/>Baggage Qty:{tb.baggageQuantity}
                            <br/>Baggage Amount:{tb.baggageAmount}<br/>Conveyance Type:{tb.conveyanceType}
                            <br/>Conveyance Amount:{tb.conveyanceAmount}<br/>{ }<br/>{ }<br/></td>
                              <td class="bg-green-50 border text-left  ">Amount Claimed : {tb.amountClaimed}<br/>Amount Passed:{tb.amountPassed}
                            <br/>Amount Disallowed:{tb.amountDisallowed}<br/>Advance Amount:{tb.advanceAmount}
                            <br/>Penal Interest:{tb.penalInterest}<br/>Adjustment Amount:{tb.adjustmentAmount}<br/>MRO Amount : {tb.mroAmount }<br/>DV No : {tb.dvNo }<br/>
                            DP Sheet No: {tb.dpSheetNo}<br/>DP Sheet Date : {tb.dpSheetDate }<br/>CMP Date : {tb.cmpDate }<br/>
                            Record Status : {tb.recordStatus}</td>
                            <td class="bg-green-50 border text-left  ">Aud : {tb.auditorName}<br/>Auditor Date: {tb.auditorDate}<br/> AAO : {tb.aaoName}<br/>AAO Date: {tb.aaoDate}<br/>
                            SAO/AO : {tb.aoName}<br/>SAO/AO Date: {tb.aoDate}<br/><br/>{ }<br/>{ }<br/></td>

                              <td class="bg-green-50 border text-left  "> {tb.recordStatus==='R' &&
                             <button
							className="w-32 m-0 p-0 bg-yellow-500 hover:bg-red-700 "

							onClick={() => tBillRejectionMemo(tb.cbId)}
						>	Print Rej Memo 	</button>

                             }

                             {tb.recordStatus==='V' &&

                             <button
							className="w-32 m-0 p-0 bg-yellow-500 hover:bg-red-700 "

							onClick={() => generateIntimationSlip(tb.cbId)}
						>	Intimation Slip 	</button>


                             }

                             </td>
                        </tr>
                    )}




                </thead>

				<tbody>

                    {tBillData==null || (tBillData && tBillData.length===0) &&
                    <tr>
                            <td colspan="4" align="center">No Bill Details Avaialble </td>

                        </tr>
                    }
                </tbody>

		 </table>
		 <br/>
		 </div>












		 <div className="container">
							<h1 class="text-blue-600" align="center">MRO Details</h1>
             <table class="table-auto">
                <thead>
                    <tr>
                        <th class="bg-green-200 border text-left px-8 py-4">DakId</th>
                        <th class="bg-green-200 border text-left px-8 py-4">Section Code</th>
                        <th class="bg-green-200 border text-left px-8 py-4">Min No/MRO Date</th>
                        <th class="bg-green-200 border text-left px-8 py-4">Received Month/Adjusted Month</th>
                        <th class="bg-green-200 border text-left px-8 py-4">Vr No/Vr Date</th>
                        <th class="bg-green-200 border text-left px-8 py-4">Amount</th>
                        <th class="bg-green-200 border text-left px-8 py-4">Remittance Detail/Settlement Dak Id</th>


                    </tr>
                     {mroData && mroData.map(tm =>
                      <tr key={tm.dakidNo}>
                        <td class="bg-green-50 border text-left ">{tm.dakidNo}<br/>{ }<br/>{ }<br/>{  }<br/></td>
                            <td class="bg-green-50 border text-left ">{tm.sectionCode}</td>
                            <td class="bg-green-50 border text-left "> Min No : {tm.minNo} <br/>
                            MRO Date : {tm.mroDate}<br/>{ }<br/>{ }<br/>
                             </td>
                           <td class="bg-green-50 border text-left "> Received Month : {tm.recdMonth} <br/>
                            Adjusted Month : {tm.adjMonth}<br/>{ }<br/>{ }<br/>
                             </td>
                             <td class="bg-green-50 border text-left "> Vr. No. : {tm.vrNo} <br/>
                            Vr. Date : {tm.vrDate}<br/>{ }<br/>{ }<br/>
                             </td>
                              <td class="bg-green-50 border text-left ">{tm.amount}<br/>{ }<br/>{ }<br/>{  }<br/></td>
                               <td class="bg-green-50 border text-left "> Remittance Detail : {tm.remittanceDetail} <br/>
                            Settlement Dak Id : {tm.settlementDakidNo}<br/>{ }<br/>{ }<br/>
                             </td>

                        </tr>
                    )}



                </thead>

				<tbody>

                    {mroData.length===0 &&
                    <tr>
                            <td colspan="4" align="center">No MRO Details  Avaialble </td>

                        </tr>
                    }
                </tbody>

		 </table>
		 <br/>
		 </div>











			</div>


		</div>
		</div>

	);


};

export default withRouter(Pro);
