function CommissionList() {

	const [data, setData] = useState([]);
	const [update, setUpdate] = useState(false);
	const [serverErrors, setServerErrors] = useState([]);
	const {register, handleSubmit, formState: { errors }, clearErrors } = useForm({});
	const [search, setSearch] = useState('a');
	const [key, setKey] = useState('page1');

	const useSearch = () => useDebouncedSearch(text => searchAsync(text))

	const searchAsync = async function(text) {
		console.log(text);
	}

	const { inputText, setInputText, searchResults } = useSearch();

	useEffect(() => {
		let fetching = false;
		async function fetchData() {
			if (!fetching)
				await axios.get('/commissions?search=' + search)
					.then((response) => {
						console.log(response.data);
						setData(response.data);
					})
					.catch((error) => {
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		fetchData();
		return () => { fetching = true; }

	}, [update, search]);



	async function remove(id) {
		await axios.delete(`/commissions/${id}`)
			.then(() => {
				//console.log(data);
				let updatedRecords = [...data].filter((i) => i.id !== id);
				console.log(updatedRecords);
				setData(updatedRecords);
				setUpdate(!update);
			})
			.catch((error) => {
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}


	const columns = useMemo(() => [
		{
			Header: 'Action',
			Cell: ({ row }) => (
				<div>
					<Link to={"/commissions/" + row.original.id}>
						<button className=" w-16 m-0 p-0 " > Edit </button>
					</Link>
					{' '}
					<button
						className="w-16 m-0 p-0 bg-red-500 hover:bg-red-700 "
						onClick={() => remove(row.original.id)}
					>	Delete 	</button>
