import { useState, useEffect, useMemo, useForm } from 'react';
import Table, { SelectColumnFilter } from '../utils/Table'  // 
import axios from "axios";
import { withRouter, Link } from "react-router-dom";



function EmployeeList() {

	const [data, setData] = useState([]);
	const [update, setUpdate] = useState(false);
	const [serverErrors, setServerErrors] = useState([]);
	const [search, setSearch] = useState("");
	const [inputText, setInputText] = useState('');
	//const { register, setValue, handleSubmit, formState: { errors } } = useForm({});

	useEffect(() => {
		let ignore = false;
		async function fetchData() {
			await axios.get('/employees?search='+search)
				.then((response) => {
					if (!ignore) setData(response.data);
				})
				.catch((error) => {
					if (error.response)
						setServerErrors(error.response.data.error);
					else
						setServerErrors(error.Error);
				})
		}
		fetchData();
		return () => { ignore = true; }

	}, [update, search]);


	async function remove(id) {
		await axios.delete(`/employees/${id}`)
			.then(() => {
				//console.log(data);
				let updatedRecords = [...data].filter((i) => i.id !== id);
				console.log(updatedRecords);
				setData(updatedRecords);
				setUpdate(!update);
			})
			.catch((error) => {
				console.log(error);
				//console.log(error.response.status);
				//console.log(error.response.headers);
				if (error.response)
					setServerErrors(error.response.data.error);
				else
					setServerErrors(error.Error);
			});
	}


	const columns = useMemo(() => [
		{
			Header: 'Action',
			Cell: ({ row }) => (
				<div>
					<Link to={"/employees/" + row.original.id}>
						<button className=" w-16 m-0 p-0 " > Edit </button>
					</Link>
					{' '}
					<button
						className="w-16 m-0 p-0 bg-red-500 hover:bg-red-700 "
						onClick={() => remove(row.original.id)}
					>	Delete 	</button>
				</div>
			)
		},
		{
			Header: "CDAO No",
			accessor: 'cdaoNo',
			className: "sticky",
			headerClassName: "sticky"
		},

		{
			Header: "Check Digit",
			accessor: 'checkDigit',
		},

		{
			Header: "Ic No",
			accessor: 'icNo',
		},

		{
			Header: "Ic Check Digit",
			accessor: 'icCheckDigit',
		},

		{
			Header: "Officer Name",
			accessor: 'officerName',
		},

		{
			Header: "Present Corps",
			accessor: 'presentCorps.unitCode',
		},

		{
			Header: "Present Core Date",
			accessor: 'presentCoreDate',
		},

		{
			Header: "Present Unit",
			accessor: 'presentUnit.unitCode',
		},

		{
			Header: "Present Unit Date",
			accessor: 'presentUnitDate',
		},

		{
			Header: "Previous Unit",
			accessor: 'previousUnit.unitCode',
		},

		{
			Header: "Previous Unit Date",
			accessor: 'previousUnitDate',
		},

		{
			Header: "Transcription Type",
			accessor: 'transcriptionType',
		},

		{
			Header: "Status Code",
			accessor: 'statusCode.statusName',
		},

		{
			Header: "Status Effect Date",
			accessor: 'statusEffectDate',
		},

		{
			Header: "Rank",
			accessor: 'rank.rankName',
		},

		{
			Header: "Employee Category",
			accessor: 'employeeCategory.categoryName',
		},

		{
			Header: "Sus No",
			accessor: 'susNo',
		},

		{
			Header: "Gso Code",
			accessor: 'gsoCode',
		},

		{
			Header: "Terms Max Age Retention",
			accessor: 'termsMaxAgeRetention',
		},

		{
			Header: "Date Of Birth",
			accessor: 'dateOfBirth',
		},

		{
			Header: "Fs Due Date",
			accessor: 'fsDueDate',
		},

		{
			Header: "Regular Ta",
			accessor: 'regularTa',
		},

		{
			Header: "Ta Appointment Category",
			accessor: 'taAppointmentCategory',
		},

		{
			Header: "Pan",
			accessor: 'pan',
		},

		{
			Header: "Month Ending",
			accessor: 'monthEnding',
		},

		{
			Header: "Current Record",
			accessor: 'currentRecord',
		},

		{
			Header: "Dni",
			accessor: 'dni',
		},

		{
			Header: "Fs Status Code",
			accessor: 'fsStatusCode',
		},

		{
			Header: "Gender",
			accessor: 'gender',
		},

		{
			Header: "Auditor Date",
			accessor: 'auditorDate',
		},

		{
			Header: "Aao Date",
			accessor: 'aaoDate',
		},

		{
			Header: "Ao Date",
			accessor: 'aoDate',
		},

		{
			Header: "Approved",
			accessor: 'approved',
		},

		{
			Header: "Record Status",
			accessor: 'recordStatus',
		},

		{
			Header: "Old Ic No",
			accessor: 'oldIcNo',
		},

		{
			Header: "Old Check Digit",
			accessor: 'oldIcCheckDigit',
		},

		{
			Header: "Date Of Reporting",
			accessor: 'dateOfReporting',
		},


		/*
		{
			Header: "Login Name",
			accessor: 'loginName',
			Filter: SelectColumnFilter,  // new
			filter: 'includes',
		},
		*/
	], [])

	const handleSubmit = (event) => {
		event.preventDefault();
		console.log(inputText);
		setSearch(inputText);
	}

	const handleKeyPress = (event) => {
		// look for the `Enter` keyCode
		if (event.keyCode === 13 || event.which === 13) {
			handleSubmit(event)
		}
	}

	return (
		<div className="min-h-screen bg-gray-100 text-gray-900">
			<main className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">

				<div className="mt-2 ml-4 ">
					<h1 className="text-xl font-semibold">Employees</h1>

					<div className="flexContainer">
						<input type="text" name="search" 
						onChange={e => setInputText(e.target.value)}
							onKeyPress={handleKeyPress}
							className="pl-2 -ml-2 inputField flex-initial" />
						<button type="submit" onClick={handleSubmit} className="w-16 m-0 p-0">Search</button>
					</div>

				</div>

				<div className="-mt-2 max-h-1 py-0">
					<Table columns={columns} data={data} className="table-auto" />
				</div>
			</main>

			<br />
			<br />
			<br />
		</div>

	);
}

export default withRouter(EmployeeList);
