/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By : 
 */

import { useState, useEffect, useRef } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'


const schema = yup.object({




});



const DpSheetList = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();
	//console.log(id);

	let history = useHistory();

	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});

	const [secList,setSecList]=useState([]);
	const [csList,setCsList]=useState([]);
	const [secItem,setSecItem]=useState();
	const [secCodeItems,setSecCodeItems]=useState([]);
	 const [secItems,setSecItems]=useState([]);
	const [loading, setLoading] = useState(true);
	 const [mesg,setMesg]=useState();
	 const [secCodeList,setSecCodeList]=useState([]);
	 const [secCode,setSecCode]=useState();

	const [key, setKey] = useState('Page1');




	useEffect(() => {
		let fetching = false;
		let unmounted = false;

		async function fetchUsrSectionData() {

			if (!fetching)
				//console.log(secId);
				await axios.get(`/usrs/0/loggedInUsrSections`)
					.then((response) => {
						console.log("response>>" + response.data);
						setSecList(response.data);

						if (!unmounted) {
							 setSecItems(
								response.data.map(({ id, sectionName }) => ({ id: id, label: sectionName }))
							);
							setLoading(false);
						}
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		fetchUsrSectionData();

		return () => { fetching = true; unmounted = true; }
		// eslint-disable-next-line react-hooks/exhaustive-deps
	},[]);

	useEffect(() => {
		let fetching = false;
		let unmounted = false;

		async function fetchSectionCodeData() {
			 console.log(secItem);
			if (!fetching && secItem)
				//console.log(secId);
				await axios.get(`/sections/${secItem}/pmSectionCodes`)
					.then((response) => {
						console.log("response section codce >>" + response.data);
						setSecCodeList(response.data);

						if (!unmounted) {

							setLoading(false);
						}
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		fetchSectionCodeData();

		return () => { fetching = true; unmounted = true; }
		// eslint-disable-next-line react-hooks/exhaustive-deps
	},[secItem]);

useEffect(() => {
		let fetching = false;
		let unmounted = false;

		async function fetchCsData() {
			 console.log(secCode+"-"+secItem);
			if (!fetching && secCode && secItem){
				 await axios.get(`/sections/${secItem}/pmSection/${secCode}`)
					.then((response) => {
						console.log("response cs list >>" + response.data);
						setCsList(response.data);

						if (!unmounted) {

							setLoading(false);
						}
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		}
		fetchCsData();

		return () => { fetching = true; unmounted = true; }
		// eslint-disable-next-line react-hooks/exhaustive-deps
	},[secCode,secItem]);

	const onSubmit = (data, event) => {
		event.preventDefault();
		data=csList;
		console.log(data);
		//console.log("data id--------------"+data.id);

			axios.put(`/chequeSlips/${secCode}/dpSheet`,data)
				.then((response) => { 
					console.log("reponse status--------------"+response.status+"--"+response.statusText+"----"+"-h--"+response.headers+"--"+response.data);
					if(response.status===200){
						 setMesg(response.data);
						 setCsList([]);

						//history.push("/daks/view/"+response.data);
						//history.replace({pathname:'/daks/new/'+response.data});



					}
				})
				.catch((error) => {
					//console.log(error.response.data);
					//console.log("response--------"+error.response.status);
					//if(error.response.status!==200)
						//history.push("/chequeSlips/dpsList");
					//console.log(error.response.headers);
					if (error.response)
						setServerErrors(error.response.data);
					else
						setServerErrors(error);
				});


		//history.push("/daks");
	}



	const onError = (errors, e) => console.log(errors, e);

	//All foreign keys. 
	// XXXXXX Add search fields and adjust the values as necessary


	const errorCallback = (err) => {
		//console.log(err);
		setServerErrors(err);
	}

	const handleCheckBox = (e,index) =>{

			csList[index].select=e.target.checked;

	}

	const handleSectionChange =(e)=>{
		console.log(e.target.selectedIndex+"--"+e.target.value);
		setSecItem(e.target.value);
	}

		const handleSectionCodeChange =(e)=>{
		console.log(e.target.selectedIndex+"-->>"+e.target.value);
		setSecCode(e.target.value);
	}




	const updateCheckBoxAll = (e) =>{
		  // console.log("..."+e.target.checked+"--"+index);

			for(var k in csList){
				csList[k].select=e.target.checked;
			}


	}

	const handleInputChange = (e) => {
		console.log(e.target.value);
	//	console.log("handle input change");


	};


	return (
		<div className="max-w-xl mx-auto ">
			<div className="w-full w-3/4  mx-auto " >

				<form onSubmit={handleSubmit(onSubmit, onError)}>
					<h1 >  DP Sheet List Generation</h1>
					{mesg}
					<div className="text-red-500">{serverErrors}</div>

					 <div className="grid grid-cols-2 gap-0">

						<div>
							<b>Section</b>

							<select className="form-control py-0"
								disabled={loading}
								value={secItem}		
								onChange={handleSectionChange}>
								<option key='0' value={''}>--select section--</option>
								{secItems.map(({ id,label }) => (
									<option  key={id} value={id}> {label} </option>
								))}
							</select>
						</div>

						<div>
							<b>PM Section</b>
							<select className="form-control py-0"
								disabled={loading}
								value={secCode}
								onChange={handleSectionCodeChange}>
								<option key='0' value={''}>--select pm section--</option>
								{secCodeList.map((item,index) => (
									<option key={index} value={item.toString()}> {item.toString()} </option>
								))}
							</select>
						</div>
						</div>

					  			 <div className="container">  


        <table className="table table-striped table-bordered">  
        <tbody>
            <tr> 
            <th><input type="checkbox" onClick={(e)=>updateCheckBoxAll(e)} ></input></th> 
                <th>Sl</th>  
                <th>DakId</th>  
                <th>Month</th>  
                 <th>Amount</th>
                  <th>Beneficiary</th>
                  <th>Bank Details</th>

            </tr>  

            {csList.map((data, index) => (  
              <tr key={index}> 
              <td><input type="checkbox" onClick={(e)=>handleCheckBox(e,index)} ></input></td>
              <td>{index+1}</td>  
                <td>{data.dakId}</td>  
                <td>{data.me}</td>  
                <td>{data.amount}</td>  
                <td>{data.beneficiary}</td>
