import { useState, useEffect, useMemo } from "react";
import { withRouter, useParams, useHistory, Link  } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import Table, { SelectColumnFilter } from '../utils/Table'  // 

import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'


const schema = yup.object({




});



const DailyProgressReport = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();
	//console.log(id);

	let history = useHistory();

 	const [data,setData]=useState([]);
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});

	 const[fromDate,setFromDate]=useState(new Date());
	 const[toDate, setToDate]=useState(new Date());
	 const [fromDateStr,setFromDateStr]=useState('');
	 const [toDateStr,setToDateStr]=useState('');
	 const[formErrors, setFormErrors]=useState({});



	const [loading, setLoading] = useState(true);
	 const [mesg,setMesg]=useState();
	 const[task,setTask]=useState('');
	 const[sectionname,setSectionname]=useState('');

	const [key, setKey] = useState('Page1');
	const[audData, setAudData]=useState([]);
const[aaoData, setAaoData]=useState([]);	
 const[aoData, setAoData]=useState([]);
 const[approvedData, setApprovedData]=useState([]);




	 const generateDpr = () => {

		let saving = false;

		async function fetchData() {

			if (!saving)
				//console.log(secId);
				await axios.get(`/reports/${fromDateStr}/${toDateStr}/dpr`)
					.then((response) => {
						console.log("response>>" + response.data);
						//setSh3List(response.data);
						 setData(response.data);
						 console.log("---Task No>>" + data[1].task);
						 setTask(data[3].task);
						   console.log("---Section No>>" + data[1].sectionname);
						   setSectionname(data[1].sectionname);

					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		fetchData();

		return () => { saving = true; }
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}

	async function breakUp(task,sectionname,fromDateStr, toDateStr) {


			 let record='';

				console.log(">>>>>>>>:" + task);
				console.log(">>>Section Name--:" +sectionname);
				await axios.get(`/reports/${task}/${sectionname}/${fromDateStr}/${toDateStr}/dprBreakup`)
					.then((response) => {
						console.log("response>>" + response.data[1]);


			  if(response.data[0]){
							console.log(">>>>>aud Data--:" + response.data[0]);
							setAudData(response.data[0]);
			} if(response.data[1]){
							console.log(">>>>>aao Data--:" + response.data[1]);
							setAaoData(response.data[1]);
				}if(response.data[2]){
							setAoData(response.data[2]);
						}if(response.data[3]){
							setApprovedData(response.data[3]);
						}

					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})


		// eslint-disable-next-line react-hooks/exhaustive-deps
	};

	const handleInputFromDateChange =  (e) =>{

		   console.log(e.target.value);
			 //setFromDate(e.target.value);	
			// validateFromDate(e.target.value);
			 setFromDateStr(e.target.value);

	}

	 const handleInputToDateChange =  (e) =>{

		   console.log(e.target.value);
			 //setToDate(e.target.value);
			// validateToDate(e.target.value);

				 setToDateStr(e.target.value);	

	}

	const onError = (errors, e) => console.log(errors, e);

	//All foreign keys. 
	// XXXXXX Add search fields and adjust the values as necessary


	const errorCallback = (err) => {
		//console.log(err);
		setServerErrors(err);
	}




	const handleInputChange = (e) => {
		console.log(e.target.value);
	//	console.log("handle input change");


	};

	const ShowDpr = () => {


		const columns=useMemo(()=>[



		{
			Header: "Section",
			accessor: 'sectionname',
		},
		{
			Header: "Task",
			accessor: 'task',
		},
		{
			Header: "Usr",
			accessor: 'usrName',
		},
		{
			Header: "Opening Balance",
			accessor: 'openingBalance',
		},

		{
			Header: "Receipts",
			accessor: 'receipts',
		},

		{
			Header: "Disposal",
			accessor: 'disposal',
		},
		{
			Header: "Closing Balance",
			accessor: 'closingBalance',
			Cell: ({ row }) => (
				<div>

					<a href="#"
							className="w-32 m-0 p-0 bg-green-500 hover:bg-green-700 "
							onClick={() => breakUp(row.original.task,row.original.sectionname,fromDateStr,toDateStr)}
						>	{row.original.closingBalance}	</a>

				</div>
			)
		},



		{
			Header: "Oldest Date",
			accessor: 'oldestDate',
		},


	],[data])

 	return (
		<div className="min-h-screen bg-gray-100 text-gray-900">
			<main className="max-w-8xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
				<div className=" ">


					<h1 className="text-xl font-semibold ml-4">Daily Progress Report</h1>




				</div>
				<div className="-mt-2 max-h-1 py-0 ml-0">
					<Table columns={columns} data={data} page={50} className="table-auto" />
				</div>
			</main>

		</div>
	);
	}
	const ShowBreakupAud = () => {

	let date = new Date("createdAt");
	let dateMDY = `${date.getDate()}-${date.getMonth() + 1}-${date.getFullYear()}`;

		const columns=useMemo(()=>[



		{
			Header: "Dak Id",
			accessor: 'dak.dakidNo',
		},
		{
			Header: "CDAo_No",
			accessor: 'cdaoNo',
		},
		{
			Header: "Bill Type",
			accessor: 'billType.description',
		},
		{
			Header: "Amount",
			accessor: 'amountClaimed',
		},

		{
			Header: "Oldest Date",
			accessor: 'createdAt',
		},

		{
			Header: "Reason",
			accessor: 'reason',
		},



	],[audData])

 	return (
		<div className="min-h-screen bg-gray-100 text-gray-900">
			<main className="max-w-8xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
				<div className=" ">


					<h1 className="text-xl font-semibold ml-4">Breakup Details of Auditor</h1>




				</div>
				<div className="-mt-2 max-h-1 py-0 ml-0">
					<Table columns={columns} data={audData} page={50} className="table-auto" />
				</div>
			</main>

		</div>
	);
	}

 	   	const ShowBreakupAao = () => {

	let date = new Date("createdAt");
	let dateMDY = `${date.getDate()}-${date.getMonth() + 1}-${date.getFullYear()}`;

		const columns=useMemo(()=>[



		{
			Header: "Dak Id",
			accessor: 'dak.dakidNo',
		},
		{
			Header: "CDAo_No",
			accessor: 'cdaoNo',
		},
		{
			Header: "Bill Type",
			accessor: 'billType.description',
		},
		{
			Header: "Amount",
			accessor: 'amountClaimed',
		},

		{
			Header: "Oldest Date",
			accessor: 'createdAt',
		},

		{
			Header: "Reason",
			accessor: 'reason',
		},



	],[aaoData])

 	return (
		<div className="min-h-screen bg-gray-100 text-gray-900">
			<main className="max-w-8xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
				<div className=" ">


					 <h1 className="text-xl font-semibold ml-4">Breakup Details of AAO</h1>




				</div>
				<div className="-mt-2 max-h-1 py-0 ml-0">
					<Table columns={columns} data={aaoData} page={50} className="table-auto" />
				</div>
			</main>

		</div>
	);
