 */

import { useState, useEffect } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

import DatePicker  from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'

const schema = yup.object({
      unitCode: yup.string().required('Required'),
      task: yup.number().integer().required('Required'),
      unitName: yup.string().required('Required'),
      susNo: yup.string().required('Required'),
      address1: yup.string().required('Required'),
      address2: yup.string().required('Required'),
      address3: yup.string().required('Required'),
      station: yup.string().required('Required'),
      pinCode: yup.string().required('Required'),
      email: yup.string().required('Required'),
      phone1: yup.string().required('Required'),
      phone2: yup.string().required('Required'),
      fax: yup.string().required('Required'),
      coRank: yup.string().required('Required'),
      coName: yup.string().required('Required'),
      unitCategory: yup.object().required('Required'),
      unitSubarea: yup.object().required('Required'),
      unitBrigade: yup.object().required('Required'),
      unitCommand: yup.object().required('Required'),
      unitCorps: yup.object().required('Required'),
      unitBattalion: yup.object().required('Required'),
      fieldPeace: yup.boolean().required('Required'),
      raisedDate: yup.string().required('Required'),
      closedDate: yup.string().required('Required'),
      regularTa: yup.string().required('Required'),
      interService: yup.boolean().required('Required'),
      paraUnit: yup.boolean().required('Required'),
      enrollPublish: yup.boolean().required('Required'),
      auditorDate: yup.string().required('Required'),
      aaoDate: yup.string().required('Required'),
      aoDate: yup.string().required('Required'),
      recordStatus: yup.string().required('Required'),
      approved: yup.boolean().required('Required'),
});


const UnitEdit = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();

	let history = useHistory();
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});
	const [key, setKey] = useState('page1');























    const [ raisedDate, setRaisedDate] = useState(new Date());

    const [ closedDate, setClosedDate] = useState(new Date());





    const [ auditorDate, setAuditorDate] = useState(new Date());

    const [ aaoDate, setAaoDate] = useState(new Date());

    const [ aoDate, setAoDate] = useState(new Date());






	useEffect(() => {
		let isCancelled = false;
		console.log(id);
		if (id !== 'new') {
			async function fetchData() {
				let record = '';
				await axios.get('/units/' + id)
					.then((response) => {
						record = response.data;
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if(error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					});

				const fields = [
				'id'
, 'unitCode', 'task', 'unitName', 'susNo', 'address1', 'address2', 'address3', 'station', 'pinCode', 'email', 'phone1', 'phone2', 'fax', 'coRank', 'coName', 'unitCategory', 'unitSubarea', 'unitBrigade', 'unitCommand', 'unitCorps', 'unitBattalion', 'fieldPeace', 'raisedDate', 'closedDate', 'regularTa', 'interService', 'paraUnit', 'enrollPublish', 'auditorDate', 'aaoDate', 'aoDate', 'recordStatus', 'approved'				
					];
				fields.forEach(field => setValue(field, record[field]));
				if (!isCancelled) {
					setEntity(record);
					setState(prev => ({ ...prev, state: record }));
				}
			}

			fetchData();
			return () => {
				isCancelled = true;
			};
		}

	}, [id, setValue]);


	const onSubmit = (data, event) => {
		event.preventDefault();
		console.log(data);
		if (data.id) {
			axios.put("/units/" + data.id, data)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		} else {
			axios.post("/units", data)
				.then((response) => { })
				.catch((error) => {
					//console.log(error.response.data);
					//console.log(error.response.status);
					//console.log(error.response.headers);
					setServerErrors(error.response.data)
				});
		}

		history.push("/units");
	}

	const onError = (errors, e) => console.log(errors, e);

	//All foreign keys. 
	// XXXXXX Add search fields and adjust the values as necessary
	const parentData = {

		unitCategory: {
			title: "Unit Category",
			url : "unitCategorys",
			searchList : ['id'], //XXXXXXXXX Add search fields
			fkEntity: "unitCategory",
			preload: false, //XXXXXX Set this to true for small tables like designations
		},
		unitSubarea: {
			title: "Unit Subarea",
			url : "unitSubareas",
			searchList : ['id'], //XXXXXXXXX Add search fields
			fkEntity: "unitSubarea",
			preload: false, //XXXXXX Set this to true for small tables like designations
		},
		unitBrigade: {
			title: "Unit Brigade",
			url : "unitBrigades",
			searchList : ['id'], //XXXXXXXXX Add search fields
			fkEntity: "unitBrigade",
			preload: false, //XXXXXX Set this to true for small tables like designations
		},
		unitCommand: {
			title: "Unit Command",
			url : "unitCommands",
			searchList : ['id'], //XXXXXXXXX Add search fields
			fkEntity: "unitCommand",
			preload: false, //XXXXXX Set this to true for small tables like designations
		},
